	p += vec3(1.35, 1.54, 1.23);
	p *= .3;
	for(int i = 0; i < 18; i++)
    {
		p.xyz = abs(p.xyz);
		p = p / dot(p,p);
		p = p * 1. - vec3(.9);
	}
	return pow(length(p),1.5)*.04;
}

float hash(float p)
{
    return fract(sin(p * 11.111111) * 91962.592632);
}

float hash(vec2 p)
{
    return fract(sin(p.x + p.y * 3.3333333) * 91962.592632);
}

float nse(float p)
{
    float fl = floor(p);
    p = fract(p);
    p = p * p * (3.0 - 2.0 * p);
    return mix(hash(fl), hash(fl + 1.0), p);
}

float nse(vec2 p)
{
    vec2 fl = floor(p);
    p = fract(p);
    p = p * p * (3.0 - 2.0 * p);

    return mix(
        mix(hash(fl), hash(fl + vec2(1.0, 0.0)), p.x),
       	mix(hash(fl + vec2(0.0, 1.0)), hash(fl + vec2(1.0, 1.0)), p.x),
           p.y);
}

float fbm(vec2 p)
{
    return nse(p * 0.5) * 0.4 + nse(p) * 0.5 + nse(p * 2.0 - 3.3333) * 0.25 + nse(p * 4.01 + 3.3333) * 0.125;
}

float fbm2(vec2 p)
{
    return nse(p) * 0.5 + nse(p * 2.0 - 3.3333) * 0.25 + nse(p * 4.01 + 3.3333) * 0.125 + nse(p * 10.01) * 0.09;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2.0 * uv - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    //uv *= 1.5 - exp(iTime * -0.01) * 0.5;

    float sd = iTime;//fragCoord.x * 7.0 + fragCoord.y * 1.11111;
    uv += (fract(vec2(cos(sd), sin(sd)) * 192925.1972) - 0.5) * 0.01 * exp(iTime * -0.04);

    float v = 0.0;

    float sf = strs(vec3(uv * 0.1, 0.0));

    vec2 p = vec2(uv.yx + sf * 0.13);
    vec2 c = vec2(0.1, 0.7);
    float fo = 20.0, it = 1.0 * exp(iTime * -0.01);
    for(int i = 0; i < 8; i++)
    {
    	p = vec2(p.x * p.x - p.y * p.y, 2.0 * p.x * p.y) + c;
        //p += nse(p * 20.0) * 0.02;
        v += exp(abs(dot(p, p) - iTime * 0.03) * -fo) * it;
        it *= 0.7;
        fo *= 0.8;
    }

    float v2 = /*v * 0.8 +*/ sf + abs(fbm(uv * 2.1 + fbm2(uv * 10.0) * 0.6) - 0.5);

    //v *= 2.0;

    vec3 col;

    //col = pow(vec3(clamp(v2 - v * 0.5, 0.0, 1.0) + length(uv) * 0.15), vec3(1.0, 0.8, 0.5) * 4.5) * 3.0;
    //col += pow(vec3(v * 1.0), vec3(0.5, 0.5, 0.7) * 6.5) * 4.0;

    float lit = pow(nse(iTime * 10.0), 3.0) * nse(uv * 4.0 - iTime) * (0.5 + v2 * 1.0) * exp(iTime * -0.04) + 0.5;

    vec3 cp = mix(vec3(1.0, 0.8, 0.5), vec3(0.3, 0.6, 0.7), smoothstep(0.0, 1.0, v2 + v));
    col = pow(vec3(v2 + v * 0.4) + exp(length(uv) * -2.0) * lit, cp * 7.0) * 4.0;


    //1.0 - fbm(uv * 2.0) * 0.5


    col = pow(col, vec3(1.0 / 2.2));

	fragColor = vec4(col, 1.0);
}

// carcarspacecar by eiffie
// This is the morph from the shiney toy car to the space car in kali's shader.
// It is part of a larger animation that wouldn't compile :(

//from iq, this version removes the mix
float smin(float a,float b,float k){float h=clamp(0.5+0.5*(b-a)/k,0.0,1.0);return b+h*(a-b-k+k*h);}

mat2 trmx,mrmx;//the wheel spinners
float minL,cc3,cc4;//car config
vec4 trpc,cc;//color and stuff
bool bColoring=false;

float DE(in vec3 p0){//carcarspacecar by eiffie
	vec3 p=p0;
	p.y+=1.24;
	float d=length(max(vec3(abs(p.x)-0.35,length(p.yz)-1.92,-p.y+1.4),0.0))-0.05;
	d=max(d,p.z-1.0);
	p=p0+vec3(0.0,-0.22,0.39);
	p.xz=abs(p.xz);
	float w1=0.24,w2=cc.z,dL=length(p+vec3(-0.3,0.0,-1.18-p0.z*0.17))-0.05;
	p.xz-=vec2(cc.w,1.0);
	if(p0.z<0.0){
		w1=cc.x;w2=cc.y;
		p.xy=mrmx*p.xy;
	}else p.xz=mrmx*p.xz;
	p.x=abs(p.x);
	float r=length(p.yz);
	d=smin(d,length(vec2(max(max(p.x-w2,0.0),-p.y-0.08),r-w1))-0.02,0.25);
	float d1=length(vec2(max(p.x-w2-0.01,0.0),r-w1+0.05))-0.04;
	if(p0.z<0.0)p.yz=p.yz*trmx;
	else p.yz=trmx*p.yz;
	float d2=min(min(abs(p.z+p.y),abs(p.z-p.y)),min(abs(p.z),abs(p.y)));//8 blades
	d2=max(r-w1+cc3,max(d2-0.003,p.x-w2+0.04));
	d2=min(d2,dL);
	minL=min(minL,d2);//catch the minimum distance to the glowing things
	if(bColoring){
		if(d2<d && d2<d1){trpc+=vec4(1.0,0.6,0.5,256.0);}//spokes/turbines
		else if(d1<d){trpc+=vec4(vec3(clamp(1.0-(r-w1+0.09)*100.0,0.0,1.0)),256.0);}
		else {//the car's body
			if(p0.z<-1.04 || (abs(p0.y-0.58)>0.05-p0.z*0.09 || p0.z>0.25) && length(max(abs(p0.xz+vec2(0.0,-0.27))-vec2(0.18,0.39),0.0))>0.1)trpc+=vec4(1.0,0.9,0.4,16.0);
			else trpc+=vec4(0.1,0.2,0.3,2.0);//the windshield
		}
	}
	return min(d,min(d1,d2)); 
}
void setConfig(){
	float t=mod(iTime,10.0);
	t=t-5.0;
	if(t>4.0)t=5.0-t;
	t=clamp(t,0.0,1.0);
	cc3=mix(0.06,-0.03,t);
	cc4=mix(0.0,-0.5,t);
	cc=mix(vec4(0.24,0.07,0.1,0.55),vec4(0.33,0.04,0.22,0.72),t);
}
mat3 lookat(vec3 fw,vec3 up){
	fw=normalize(fw);vec3 rt=normalize(cross(fw,normalize(up)));return mat3(rt,cross(rt,fw),fw);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	setConfig();
	float tim=iTime*5.0,a=cc4*3.0;
	trmx=mat2(cos(tim),sin(tim),-sin(tim),cos(tim));//the turbine spinner
	mrmx=mat2(cos(a),sin(a),-sin(a),cos(a));
	tim=iTime*0.5;
	vec3 ro=vec3(cos(tim),0.1+sin(tim*0.7),sin(tim))*(2.5-abs(sin(tim*0.7)));
	vec3 rd=lookat(-ro,vec3(0.0,1.0,0.0))*normalize(vec3((2.0*fragCoord.xy-iResolution.xy)/iResolution.y,1.0));
	vec3 col=vec3(0.4,0.5,0.6)+rd*0.1,L=normalize(vec3(0.5,0.5,-0.5));
	float t=0.0,d=1.0;
	minL=100.0;
	for(int i=0;i<32;i++){
		t+=d=DE(ro+rd*t);
	}
	if(d<0.1){
		vec3 p=ro+rd*t;
		vec2 v=vec2(2.0/iResolution.y,0.0);
		trpc=vec4(0.0);bColoring=true;
		vec3 N=normalize(vec3(-DE(p-v.xyy)+DE(p+v.xyy),-DE(p-v.yxy)+DE(p+v.yxy),-DE(p-v.yyx)+DE(p+v.yyx)));
		col=trpc.rgb*0.1666*max(0.2,0.25+0.75*dot(N,L));
		col*=mix(vec3(0.5,0.5,1.0),vec3(1.0,1.0,0.5),abs(dot(rd,N)));
		col+=vec3(1.0,0.5,0.0)*pow(max(0.0,dot(reflect(rd,N),L)),trpc.a);
	}
	col+=vec3(1.0,0.5,0.2)/(1.0+minL*minL*10000.0);//the glow
	fragColor = vec4(clamp(col,0.0,1.0),1.0);
}

// http://mathworld.wolfram.com/HeartSurface.html

float f(vec3 p) {
    vec3 pp = p * p;
    vec3 ppp = pp * p;
    float a = pp.x + 2.25 * pp.y + pp.z - 1.0;
    return a * a * a - (pp.x + 0.1125 * pp.y) * ppp.z;
}

// Bisection solver for y
float h(float x, float z) {
    float a = 0.0, b = 0.75, y = 0.5;
    for (int i = 0; i < 10; i++) {
        if (f(vec3(x, y, z)) <= 0.0)
            a = y;
        else
            b = y;
        y = (a + b) * 0.5;
    }
    return y;
}

// Analytical gradient
// (-2 x z^3+6 x (-1.+x^2+2.25 y^2+z^2)^2) 
// (-0.225 y z^3+13.5 y (-1.+x^2+2.25 y^2+z^2)^2)
// (z (-3 x^2 z-0.3375 y^2 z+6 (-1.+x^2+2.25 y^2+z^2)^2))
vec3 normal(vec2 p) {
    vec3 v = vec3(p.x, h(p.x, p.y), p.y);
    vec3 vv = v * v;
    vec3 vvv = vv * v;
    float a = -1.0 + dot(vv, vec3(1, 2.25, 1));
   	a *= a;

  	return normalize(vec3(
        -2.0 * v.x * vvv.z +  6.0 * v.x * a,
      -0.225 * v.y * vvv.z + 13.5 * v.y * a,
      v.z * (-3.0 * vv.x * v.z - 0.3375 * vv.y * v.z + 6.0 * a)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec3 p = vec3((2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.y, iResolution.x), 0);

    float s = sin(iTime * 5.0);
    s *= s;
    s *= s;
    s *= 0.1;
    vec3 tp = p * vec3(1.0 + s, 1.0 - s, 0.0) * 2.0;

    vec3 c;
    if (f(tp.xzy) <= 0.0) {
        vec3 n = normal(tp.xy);
        float diffuse = dot(n, normalize(vec3(-1, 1, 1))) * 0.5 + 0.5;
        float specular = pow(max(dot(n, normalize(vec3(-1, 2, 1))), 0.0), 64.0);
        float rim = 1.0 - dot(n, vec3(0.0, 1.0, 0.0));
        c = diffuse * vec3(1.0, 0, 0) + specular * vec3(0.8) + rim * vec3(0.5);
    }
	else
        c = vec3(1.0, 0.8, 0.7 - 0.07 * p.y) * (1.0 - 0.15 * length(p));

	fragColor = vec4(c, 1);
}
