void mainImage( out vec4 f, in vec2 p )
{
    float 
        e = iTime*.3+5.,
        m = 0.,
        y;

    vec2 
        s = iResolution.xy,
		v = 2.*(2.*p.xy-s)/s.y;

    for(float i=0.;i<3e2;i++)
    {
       	y = 6.28*i/3e2;

        s = v - fract((e+492.87)*sin(i*695.58))*e * vec2(cos(y), sin(y));

        m += 2e-3/dot(s,s);

    }

    f = 
            mix(
                vec4(m), 
                texture(iChannel0, v/5.), 
                clamp(m, 0., .5)
            );
}

bool inTri(vec2 verts[3], vec2 point) {
  bool hit = false;

  for (int i = 0; i < 3; i++) {
      vec2 vI = verts[i];
      vec2 vJ = verts[int(mod(float(i)+2.0,3.0))];
  	  if ( ((vI.y > point.y) != (vJ.y > point.y)) && (point.x < (vJ.x-vI.x) * (point.y-vI.y) / (vJ.y-vI.y) + vI.x) ) {
         hit = !hit;
      }
  }
  return hit;
}



