float rand(float x) {
	return sin(x * 123. + 17.) * sin(x * 69. + 49.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 P;
	vec3 C;
	vec4 hist;
	vec2 p = (fragCoord.xy / iResolution.xy * 2. - 1.) * vec2(iResolution.x / iResolution.y, 1.) * 3.;
	hist = vec4(0., 0., 0., 0.);
	for(int temp_5 = 0; temp_5 < 10; ++temp_5) {
		P = vec2(rand((iTime + p.x) * float(temp_5)), rand((iTime * 17. + p.y + p.x) * float(temp_5)));
		C = vec3(0., 0., 0.);
		for(int temp_6 = 0; temp_6 < 50; ++temp_6) {
			float ci = floor(abs(rand(P.x * length(P))) * 3.);
			if(ci == 0.) {
				P = vec2(P.x * vec3(.2 * iTime, 1.2, .7).x + P.y * vec3(.2 * iTime, 1.2, .7).y + vec3(.2 * iTime, 1.2, .7).z, P.x * .3 + P.y * .12 + .1);
				C = (C + vec3(1., 0., 0.)) / 2.;
			} else if(ci == 1.) {
				P = sin(vec2(P.x * .3 + P.y * 1.7 + .4, P.x * vec3(.6, .5 * iTime, .34).x + P.y * vec3(.6, .5 * iTime, .34).y + vec3(.6, .5 * iTime, .34).z));
				C = (C + vec3(0., 1., 0.)) / 2.;
			} else if(ci == 2.) {
				P = vec2(P.x * .7 + P.y * .4 + .8, P.x * vec3(.17 * iTime, .2, .2).x + P.y * vec3(.17 * iTime, .2, .2).y + vec3(.17 * iTime, .2, .2).z) / length(vec2(P.x * .7 + P.y * .4 + .8, P.x * vec3(.17 * iTime, .2, .2).x + P.y * vec3(.17 * iTime, .2, .2).y + vec3(.17 * iTime, .2, .2).z));
				C = (C + vec3(0., 0., 1.)) / 2.;
			}
			if(length(P - p) < (1. / (hist.x * 10.) + .001)) {
				hist = vec4(hist.x + 1., (hist.yzw + C) / 2.);
			}
		}
	}
	fragColor = vec4(hist.yzw, 1.);
}

