void mainImage( out vec4 f, in vec2 w )
{
    vec2 p = 2.*w/iResolution.xy - 1.;
    f = vec4( fract( atan(p.y,p.x) + .5*iTime - 1440.*log(length(p)) ) > .5 );
}

