float perlin(vec2 coords, int octaves, float gain, float freq, float amplitude)
{
    float p = 0.0;
    const int max_octaves = 20;
    for (int i = 0; i < max_octaves; ++i)
    {
        if (i >= octaves)
            break;
        p += texture(iChannel0, vec2(coords.x * freq, coords.y * freq)).r * amplitude;
        amplitude *= gain;
        freq *= 1.0 / gain;
    }
    return p;   
}

