#define Iterations 256
#define Speed 10.0
#define FPS 60.0
#define Planets 6
//#define StarMovement

struct Particle {
	vec2  pos;
    vec2  vel;
    float mass;
};

const float delta = 1.0/(FPS/Speed);

Particle particles[Planets];

float grav(Particle p1, Particle p2){
	vec2 d = p1.pos-p2.pos;
    return p1.mass * p2.mass / (d.x*d.x+d.y*d.y);
}

Particle updateParticle(Particle p){
    vec2 acc = vec2(0.);
    for(int i = 0; i < Planets; i++){
    	Particle po = particles[i];
    	acc += (po.pos - p.pos) * grav(p,po);
    }
    p.vel += acc / p.mass * delta;
    p.pos += p.vel * delta;
    return p;
}

vec3 hash3(const float n) {
	return fract(sin(n)*vec3(36.5453123,43.1459123,11234.3490423));
}

