#define t iTime
#define r iResolution.xy
#define L(i) clamp(i,0.,1.)
#define F float
#define D vec2
#define T vec3
#define R return
#define v D(0,1)

F B(D a,D b)
{
    D d=abs(a)-b;
    R min(max(d.x,d.y),0.)+length(max(d,0.));
}

F C(T a,D b)
{
    R min(B(a.xy,b),min(B(a.yz,b),B(a.zx,b)));
}

F f(T a)
{
    F b=0.;
    for(F c=0.;c<7.;c+=1.)
    {
        F H=c*(t*.002);
        a.xy=mat2(cos(H),-sin(H),sin(H),cos(H))*a.xy;
        F W=pow(1.6,c*.7);
        b=max(b,-C(mod(a+.15/W,.6/W)-.15/W,D(.1/W)));
    }
    R b;
}

T g(T a,F b)
{
	D d=v*b;
	R normalize(T(
		f(a+d.yxx)-f(a-d.yxx),
		f(a+d.xyx)-f(a-d.xyx),
		f(a+d.xxy)-f(a-d.xxy)
	));
}

