float softshadow(vec3 ro, vec3 rd, float mint, float maxt, float k) {
   float sh = 1.0;
   float t = mint;
   float h = 0.0;
   for (int i = 0; i < 10; i++) {
      if(t > maxt)
         continue;
      h = renderFunction(ro+rd*t);
      sh = min(sh, k*h/t);
      t += h;
   }
   return sh;
}
