vec2 rotate(in vec2 p, in float t)
{
	return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

float shapeRad1(in vec3 p)
{
    float a = atan(p.z, p.x);
    return 0.5 * ((sin(5.0 * a)) + 3.0);
}

float shapeRad2(in vec3 p)
{
    float a = atan(p.z, p.x);
    return abs(sin(2.0 * a)) + 1.0;
}

float deQuad(in vec2 p, in float h)
{
     return max(abs(p.x), abs(p.y)) - h;
}

float deTri(in vec2 p, in float h)
{
     return abs(p.x) + abs(p.y) - h;
}

float deCircle(in vec2 p, in float r)
{
     return length(p) - r;
}

float deShape(in vec2 p)
{

    return max(max(deTri(p, 1.0), -p.y*p.y+p.x-0.7), -p.x*p.x+ abs(p.y)-0.6);
}

float shape1(in vec3 p, vec2 q)  {return          deQuad(q, 1.0);}
float shape2(in vec3 p, vec2 q)  {return     deTri(q, 1.0);}
float shape3(in vec3 p, vec2 q)  {return        deCircle(q, 1.0);}
float shape4(in vec3 p, vec2 q)  {return   deShape(q);}
float shape5(in vec3 p, vec2 q)  {return    deQuad(q * vec2(shapeRad1(p), 1.0), 1.0);}
float shape6(in vec3 p, vec2 q)  {return     deTri(q * vec2(shapeRad1(p), 1.0), 1.0);}
float shape7(in vec3 p, vec2 q)  {return  deCircle(q * vec2(shapeRad1(p), 1.0), 1.0);}
float shape8(in vec3 p, vec2 q)  {return   deShape(q * vec2(shapeRad1(p), 1.0));}
float shape9(in vec3 p, vec2 q)  {return    deQuad(q * vec2(shapeRad2(p), 1.0), 1.0);}
float shape10(in vec3 p, vec2 q) {return     deTri(q * vec2(shapeRad2(p), 1.0), 1.0);}
float shape11(in vec3 p, vec2 q) {return  deCircle(q * vec2(shapeRad2(p), 1.0), 1.0);}
float shape12(in vec3 p, vec2 q) {return   deShape(q * vec2(shapeRad2(p), 1.0));}
float shape13(in vec3 p, vec2 q) {return  max(abs(p.y) - 1.0, deShape(p.xz));}
float shape14(in vec3 p, vec2 q) {return  max(abs(p.y) - 1.0, deShape(p.xz * (abs(p.y) + 1.0)));}

float map(in vec3 p)
{    
    p *= 0.35;
	vec2 q = vec2(length(p.xz), p.y);    
    float t = mod(iTime * 0.8, 42.0);
    if (t < 3.0)  return mix( shape1(p, q),  shape2(p, q), smoothstep( 1.0,  2.0, t));
    if (t < 6.0)  return mix( shape2(p, q),  shape3(p, q), smoothstep( 4.0,  5.0, t));
    if (t < 9.0)  return mix( shape3(p, q),  shape4(p, q), smoothstep( 7.0,  8.0, t));
    if (t < 12.0) return mix( shape4(p, q),  shape5(p, q), smoothstep(10.0, 11.0, t));
    if (t < 15.0) return mix( shape5(p, q),  shape6(p, q), smoothstep(13.0, 14.0, t));
    if (t < 18.0) return mix( shape6(p, q),  shape7(p, q), smoothstep(16.0, 17.0, t));
    if (t < 21.0) return mix( shape7(p, q),  shape8(p, q), smoothstep(19.0, 20.0, t));
    if (t < 24.0) return mix( shape8(p, q),  shape9(p, q), smoothstep(22.0, 23.0, t));
	if (t < 27.0) return mix( shape9(p, q), shape10(p, q), smoothstep(25.0, 26.0, t));    
	if (t < 30.0) return mix(shape10(p, q), shape11(p, q), smoothstep(28.0, 29.0, t));    
	if (t < 33.0) return mix(shape11(p, q), shape12(p, q), smoothstep(31.0, 32.0, t));    
	if (t < 36.0) return mix(shape12(p, q), shape13(p, q), smoothstep(34.0, 35.0, t));
	if (t < 39.0) return mix(shape13(p, q), shape14(p, q), smoothstep(37.0, 38.0, t));
	if (t < 42.0) return mix(shape14(p, q),  shape1(p, q), smoothstep(40.0, 41.0, t));
    return 1.0;
}

vec3 calcNormal(in vec3 p)
{
	const vec2 e = vec2(0.0001, 0.0);
	return normalize(vec3(
		map(p + e.xyy) - map(p - e.xyy),
		map(p + e.yxy) - map(p - e.yxy),
		map(p + e.yyx) - map(p - e.yyx)));
}

float march(in vec3 ro, in vec3 rd)
{
	const float maxd = 50.0;
	const float precis = 0.001;
    float h = precis * 2.0;
    float t = 0.0;
	float res = -1.0;
    for(int i = 0; i < 256; i++)
    {
        if(h < precis || t > maxd) break;
	    h = map(ro + rd * t);
        t += h;
    }
    if(t < maxd) res = t;
    return res;
}

vec3 transform(in vec3 p)
{
    p.yz = rotate(p.yz, iTime * 0.5);
    p.zx = rotate(p.zx, iTime * 0.7);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 col = vec3(0.1 + p.y * 0.15);
   	vec3 rd = normalize(vec3(p, -1.8));
	vec3 ro = vec3(0.0, 0.0, 10.0);
    vec3 li = normalize(vec3(0.5, 0.8, 3.0));
    ro = transform(ro);
	rd = transform(rd);
	li = transform(li);
    float t = march(ro, rd);
    if(t > -0.001)
    {
        vec3 pos = ro + t * rd;
        vec3 n = calcNormal(pos);
		float dif = clamp((dot(n, li) + 0.5) * 0.7, 0.3, 1.0);
        col = vec3(0.9, 0.95, 0.8) * dif;
    }
   	fragColor = vec4(col, 1.0);
}

// Gravitation lensing simulation using DF-tracing
// by Tomkh '20.01.2015
// http:/moonedit.com/tom

#define ptcnt 12
vec3 pt[ptcnt];
float ptrad = 0.1;
float tim;

void setup_scene()
{
    float t0 = tim*.5;
    for(int i=0; i<ptcnt; ++i) 
    {
        float t = t0 + float(i)*1.7;

        // Simple animation of spheres:
        pt[i].x = cos(t)*.5;
        pt[i].y = sin(t*1.1)*.5;

        // Put 6 sphere in front (lens) and 6 in the back:
        pt[i].z = ((i*2<ptcnt)?4.0:16.0) + cos(t*2.1);
    }
}

vec4 df(vec3 p)
{
    float dmin = 1e32;
    vec3 n = vec3(0,0,0);
    for(int i=0; i<ptcnt; ++i) 
    {
        vec3 dp = pt[i] - p;
        float d = dot(dp,dp);

        // Here is the key to lensing effect, approx. gravity field:
        n += dp/d;

        dmin = min(d, dmin);
    }
    return vec4(n,sqrt(dmin));
}

vec4 tex(vec3 p)
{
    float dmin = 1e32;
    vec3 dpmin;
    for(int i=0; i<ptcnt; ++i) 
    {
        vec3 dp = pt[i] - p;
        float d = dot(dp,dp);
        if (d < dmin) 
        {
            dmin = d;
            dpmin = dp;
        }
    }
    float d = sqrt(dmin);
    return vec4(dpmin/d,d);
}

vec4 trace(vec3 p, vec3 n)
{
    float falloff = 1.0 - (1.0 - n.z)*16.0;

    // Lensing animation:
    float sc = sin(tim*.1);
    sc *= sc;
    sc *= .008;

    // DF tracing with lens-effect here:
    vec4 dn;
    float lit = 0.0;
    for(int k=0; k<64; ++k)
    {
        dn = df(p);
        float d = dn.w;
        float surf_dist = d - ptrad;
        lit += 0.003/(d + 0.1);

        // Move half the distance only:
        float f = surf_dist*.5;
        p += n*f;

        // Modify ray direction by gravity field:
        n = normalize(n + dn.xyz*f*sc);
    }

    // Shading:
    vec4 norm = tex(p);
    float shade = max(0.0, 1.0 - (norm.w - ptrad)*256.0);
    vec4 col = texture(iChannel1, norm.xy*0.1);
    vec4 bkg = texture(iChannel1, n.xy*2.0)*falloff;
    return vec4(vec3(lit,lit,lit*0.6) - vec3(0.0,col.x*col.x*0.5,col.x)*shade,1.0)
         + vec4(bkg.xy,bkg.z*2.0,0.0)*0.2*(1.0-shade);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Hold mouse button to stop motion / review time:
    tim = (iMouse.z > .5) ? iMouse.x * 20.0 / iResolution.x : iTime;

    setup_scene();
	vec2 uv = (fragCoord.xy - iResolution.xy*0.5) / iResolution.x;
    fragColor = trace(vec3(0,0,0), normalize(vec3(uv,1)));
}

