float valueNoiseSimple(vec2 vl) {
   float minStep = 1.0 ;

   vec2 grid = floor(vl);
   vec2 gridPnt1 = grid;
   vec2 gridPnt2 = vec2(grid.x, grid.y + minStep);
   vec2 gridPnt3 = vec2(grid.x + minStep, grid.y);
   vec2 gridPnt4 = vec2(gridPnt3.x, gridPnt2.y);

    float s = rand2(grid);
    float t = rand2(gridPnt3);
    float u = rand2(gridPnt2);
    float v = rand2(gridPnt4);

    float x1 = smoothstep(0., 1., fract(vl.x));
    float interpX1 = mix(s, t, x1);
    float interpX2 = mix(u, v, x1);

    float y = smoothstep(0., 1., fract(vl.y));
    float interpY = mix(interpX1, interpX2, y);

    return interpY;
}

float fractalNoise(vec2 vl) {
    float persistance = 2.0;
    float amplitude = 1.;
    float rez = 0.0;
    vec2 p = vl;

    for (float i = 0.0; i < OCTAVES; i++) {
        rez += amplitude * valueNoiseSimple(p);
        amplitude /= persistance;
        p *= persistance;
    }
    return rez;
}

float getTLow(float y0, float v0, float g) {

    // First solution is 0, second one is 2 * v0 / g
    return 2. * v0 / g;
}

float physTest3D(vec3 coord, vec3 start, float v0, float g, float r,
                out int numBumps, out float inBump) {
	float Gclr = 0.;
    float lowerPnt = getTLow(start.y, v0, g);
    float time = iTime / 3.;

    int attenuation = int(time / lowerPnt);

    attenuation = int(mod(float(attenuation), 3.));

    vec3 center = start;
    float y_offset = 0.;

    if (v0 > 0.5) {
        v0 /= float(attenuation + 1);
        lowerPnt = getTLow(start.y, v0, g);
    	time = mod(time, lowerPnt);

       	y_offset = v0 * time - (g * time * time) / 2.;

   		center.y = start.y + y_offset;
    }

    center.y += r;

    numBumps = attenuation;
    inBump = time;

    return object11(coord, center, r);
}

float sceneSimple(vec3 a) {
    float res = 10.;
    float Floor = -0.98;
    float Step = 20. / NUM_BALLS;

    float idxX = floor(a.x / Step);
    float idxZ = floor(a.z / Step);
    a.x = mod(a.x, Step);
    a.z = mod(a.z, Step);

    float r = 0.1;

    float offsetX = Step / 2. + (rand(vec2(idxX, idxZ))* 2. - 1.) * (Step / 2. - 2. * r);
    float offsetZ = Step / 2. + (rand2(vec2(idxX, idxZ))* 2. - 1.) * (Step / 2. - 2. * r);

    int tmp1;
    float tmp2;

    res = physTest3D(a,
                     vec3(offsetX, Floor, offsetZ), 
                     4. + 2. * (rand(vec2(idxX / 10., idxZ/ 10.)) * 2. - 1.),
                     9.8,
                     r,
                     tmp1,
                     tmp2);


    return min(res, a.y - Floor);
}

float scene(vec3 a, out objectInfo inf) {
    float res = 10.;
    float Floor = -0.98;
    float Step = 20. / NUM_BALLS;

    float idxX = floor(a.x / Step);
    float idxZ = floor(a.z / Step);
    a.x = mod(a.x, Step);
    a.z = mod(a.z, Step);

    float r = 0.1;

    float offsetX = Step / 2. + (rand(vec2(idxX, idxZ))* 2. - 1.) * (Step / 2. - 2. * r);
    float offsetZ = Step / 2. + (rand2(vec2(idxX, idxZ))* 2. - 1.) * (Step / 2. - 2. * r);

    res = physTest3D(a,
                     vec3(offsetX, Floor, offsetZ), 
                     4. + 2. * (rand(vec2(idxX / 10., idxZ/ 10.)) * 2. - 1.),
                     9.8,
                     r,
                     inf.numBumps,
                     inf.inBump);

    if (res > (a.y - Floor)) {
        res = a.y - Floor;
        inf.id = vec2(0.);
    } else {
        inf.id.x = idxX + 1.;
        inf.id.y = idxZ + 1.;
    }    

    return res;
}

vec3 snormal(vec3 a) {
   vec2 e = vec2(.0001, 0.);
   float tmp1;
   float tmp2;
   float w = sceneSimple(a);

    return normalize(vec3(
       sceneSimple(a+e.xyy) - w,
       sceneSimple(a+e.yxy) - w,
       sceneSimple(a+e.yyx) - w));
}

float trace(vec3 O, vec3 D, out objectInfo inf) {

    float L = 0.;
    int steps = 0;
    float d = 0.;
    for (int i = 0; i < MAX_STEPS; ++i) {
        d = scene(O + D*L, inf);
        d = clamp(d, 0., .15);
        L += d;

        if (d < THRESHOLD * L) // Adaptive threshold 
            break;
    }

    inf.hill = d;
    return L;
}

float occluded(vec3 p, float len, vec3 dir) {
    return max(0., len - sceneSimple(p + len * dir));
}

float occlusion(vec3 p, vec3 normal) {
    vec3 rotZccw = vec3(-normal.y, normal.xz);
    vec3 rotZcw = vec3(normal.y, -normal.x, normal.z);

    vec3 rotXccw = vec3(normal.x, normal.z, -normal.y);
    vec3 rotXcw = vec3(normal.x, -normal.z, normal.y);

    vec3 rotYccw = vec3(normal.z, normal.y, -normal.x);
    vec3 rotYcw = vec3(-normal.z, normal.y, normal.x);

    float rez = 0.;
    float dst = .2;

   	rez+= occluded(p, dst, normal);

    rez+= occluded(p, dst, rotXccw);
    rez+= occluded(p, dst, rotXcw);

    rez+= occluded(p, dst, rotYccw);
    rez+= occluded(p, dst, rotYcw);

    rez+= occluded(p, dst, rotZccw);
    rez+= occluded(p, dst, rotZcw);

    return (1. - min(rez, 1.));
}

vec3 enlight(vec3 p, vec3 normal, vec3 eye, vec3 lightPos, objectInfo inf) {
    vec3 dir = lightPos - p;
    vec3 eyeDir = eye - p;
    vec3 I = normalize(dir);

    vec3 color;
    float specMat = 0.3;

    if (inf.id.x == 0. && inf.id.y == 0.) {
        vec3 txtP = p;
        txtP.z += .75 * inf.timeOffset;
        color = texture(iChannel0, txtP.xz *.5 + .5).rgb;
    } else {
        float threshold = 0.15;
        float bumps = float(inf.numBumps) / 3.;

        color.r = mod(rand(vec2(inf.id.x / 1000.)) + bumps, 1.);
        color.g = mod(rand(vec2(inf.id.y / 1000.)) + bumps, 1.);
        color.b = mod(rand(vec2((inf.id.x + inf.id.y) / 1000.)) + bumps, 1.);

        specMat = clamp(rand(vec2((inf.id.x + inf.id.y) / 1000.)), 0.01, 1.);

        float rnd = rand(inf.id / 1000.);

        vec3 offset = vec3(rand(p.xz) * 2. -1.,
                           rand(p.xy) * 2. -1.,
                           rand2(p.xz)* 2. -1.);

        if (rnd > 0.75) {
            float noise = fractalNoise(p.xy * 50. + rnd * 10.);
            color *= noise;
            normal *= noise;
        } else {
            normal *= clamp(rnd + 0.5, .5, 1.);
        }

        if (inf.inBump < threshold) {

            color = mix(vec3(.5, .0, .0),
                        color,
                        inf.inBump / threshold);
        }
    }

    vec3 ambient = color;
    vec3 diffuse = max(dot(normal, I), 0.) * color.rgb;

    diffuse = clamp(diffuse, 0., 1.) * 0.5;

    vec3 refl = normalize(-reflect(I, normal));
    float spec = max(dot(refl, normalize(eyeDir)), 0.);

    spec = pow(spec, specMat * 60.);
    spec = clamp(spec, 0., 1.);

    vec3 Ispec = spec * vec3(1.0, 1.0, 1.0);

    return Ispec + diffuse + ambient * occlusion(p, normal);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 centered_uv = uv * 2. - 1.;
    centered_uv.x *= iResolution.x / iResolution.y;

    float timeOffset = iTime * 2.;

    vec3 O = vec3(0., 0.1, 2. - timeOffset);
    vec3 D = normalize(vec3(centered_uv, -3.5)); //fov

    objectInfo inf;
    float path = trace(O, D, inf);
    vec3 coord = O + path * D;
    inf.timeOffset = timeOffset;

    vec3 skyBlueColor = vec3(0.529411765, 0.807843137, 0.980392157);
    vec3 color = mix(skyBlueColor, vec3(1.), clamp(centered_uv.y, 0., 1.));

    if (inf.hill >= 0.15) {
        fragColor = vec4(color , 1.);
    } else {
        vec3 lightPos = vec3(0., 10., -4. - timeOffset);
        vec3 normal = snormal(coord);

        vec3 resColor = enlight(coord, normal, O, lightPos, inf);
        // Calc some fog
    	float fogFactor = exp(-pow(abs(fogDensity * (coord.z - 1. + timeOffset)), 4.0));
    	fogFactor = clamp(fogFactor, 0.0, 1.0);
    	resColor = mix(color, resColor, fogFactor);

        fragColor = vec4(resColor,
            				1.0);
    }
}

#define OCTAVES  8

#define MAX_STEPS  64
#define THRESHOLD .0001

#define SHARP_MODE 0 // Just for fun.

float rand(vec2 co){
   return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float rand2(vec2 co){
   return fract(cos(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float valueNoiseSimple(vec2 vl) {

   const vec2 helper = vec2(0., 1.);
    vec2 interp = smoothstep(vec2(0.), vec2(1.), fract(vl));
    vec2 grid = floor(vl);

    float rez = mix(mix(rand2(grid + helper.xx),
                        rand2(grid + helper.yx),
                        interp.x),
                    mix(rand2(grid + helper.xy),
                        rand2(grid + helper.yy),
                        interp.x),
                    interp.y);
#if SHARP_MODE==1    
    return abs(rez*2. -1.);
#else
    return rez;
#endif
}

const mat2 unique_transform = mat2( 0.85, -0.65, 0.65, 0.85 );

float fractalNoise(vec2 vl, out float mainWave) {

#if SHARP_MODE==1
    const float persistance = 2.4;
    float frequency = 2.2;
    const float freq_mul = 2.2;
    float amplitude = .4;
#else
    const float persistance = 3.0;
    float frequency = 2.3;
    const float freq_mul = 2.3;
    float amplitude = .7;
#endif

    float rez = 0.0;
    vec2 p = vl;

    float mainOfset = (iTime + 40.)/ 2.;

    vec2 waveDir = vec2(p.x+ mainOfset, p.y + mainOfset);
    float firstFront = amplitude + 
			        (valueNoiseSimple(p) * 2. - 1.);
    mainWave = firstFront * valueNoiseSimple(p + mainOfset);

    rez += mainWave;
    amplitude /= persistance;
    p *= unique_transform;
    p *= frequency;


    float timeOffset = iTime / 4.;


    for (int i = 1; i < OCTAVES; i++) {
        waveDir = p;
        waveDir.x += timeOffset;
        rez += amplitude * sin(valueNoiseSimple(waveDir * frequency) * .5 );
        amplitude /= persistance;
        p *= unique_transform;
        frequency *= freq_mul;

        timeOffset *= 1.025;

        timeOffset *= -1.;
    }

    return rez;
}


float scene(vec3 a) {
   float mainWave;
   float zVal = fractalNoise(vec2(a.x - 5., a.z ), mainWave);
   return a.y + 0.2 + sin(zVal / 6.5);
}

float fractalNoiseLow(vec2 vl, out float mainWave) {
    #if SHARP_MODE==1
    const float persistance = 2.4;
    float frequency = 2.2;
    const float freq_mul = 2.2;
    float amplitude = .4;
#else
    const float persistance = 3.0;
    float frequency = 2.3;
    const float freq_mul = 2.3;
    float amplitude = .7;
#endif

    float rez = 0.0;
    vec2 p = vl;

    float mainOfset = (iTime + 40.)/ 2.;

    vec2 waveDir = vec2(p.x+ mainOfset, p.y + mainOfset);
    float firstFront = amplitude + 
			        (valueNoiseSimple(p) * 2. - 1.);
    mainWave = firstFront * valueNoiseSimple(p + mainOfset);

    rez += mainWave;
    amplitude /= persistance;
    p *= unique_transform;
    p *= frequency;


    float timeOffset = iTime / 4.;


    for (int i = 1; i < OCTAVES - 5; i++) {
        waveDir = p;
        waveDir.x += timeOffset;
        rez += amplitude * sin(valueNoiseSimple(waveDir * frequency) * .5 );
        amplitude /= persistance;
        p *= unique_transform;
        frequency *= freq_mul;

        timeOffset *= 1.025;

        timeOffset *= -1.;
    }

    return rez;
}

float sceneLow(vec3 a) {
   float mainWave;
   float zVal = fractalNoiseLow(vec2(a.x - 5., a.z ), mainWave);
   return a.y + 0.2 + sin(zVal / 6.5);
}

vec3 snormal(vec3 a) {
   vec2 e = vec2(.0001, 0.);
   float w = scene(a);
   return normalize(vec3(
       scene(a+e.xyy) - w,
       e.x,
       scene(a+e.yyx) - w));
}

float trace(vec3 O, vec3 D, out float hill) {
    float L = 0.;
    int steps = 0;
    float d = 0.;
    for (int i = 0; i < MAX_STEPS; ++i) {
        d = sceneLow(O + D*L);
        L += d;

        if (d < THRESHOLD*L)
            break;
    }

    hill = d;
    return L;
}

float occluded(vec3 p, float len, vec3 dir) {
    return max(0., scene(p + len * dir));
}


float occlusion(vec3 p, vec3 normal) {
    vec3 rotZccw = vec3(-normal.y, normal.xz);
    vec3 rotZcw = vec3(normal.y, -normal.x, normal.z);

    vec3 rotXccw = vec3(normal.x, normal.z, -normal.y);
    vec3 rotXcw = vec3(normal.x, -normal.z, normal.y);

    vec3 rotYccw = vec3(normal.z, normal.y, -normal.x);
    vec3 rotYcw = vec3(-normal.z, normal.y, normal.x);

    float rez = 0.;
    float dst = .28;

   	rez+= occluded(p, dst, normal);

    rez+= occluded(p, dst, rotXccw);
    rez+= occluded(p, dst, rotXcw);

    rez+= occluded(p, dst, rotYccw);
    rez+= occluded(p, dst, rotYcw);

    rez+= occluded(p, dst, rotZccw);
    rez+= occluded(p, dst, rotZcw);

    return (pow(min(rez, 1.), 4.5) - 0.13725) * 1.7;
}

vec3 enlight(vec3 p, vec3 normal, vec3 eye, vec3 lightPos) {
    vec3 dir = lightPos - p;
    vec3 eyeDir = eye - p;
    vec3 I = normalize(dir);
    const vec3 color0 = vec3(0.0470588, 0.1921569, 0.2980392);
    const vec3 color1 = vec3(0.0470588, 0.3450980, 0.4078431);
    const vec3 color2 = vec3(0.1294117, 0.5137254, 0.6901961);
    const vec3 color3 = vec3(0.1686274, 0.7176471, 0.8156863);

    vec3 diffuse = vec3(max(dot(normal, I), 0.));

    vec3 diffuse0 = clamp(diffuse * color0.rgb, 0., 1.);
    vec3 diffuse1 = clamp(diffuse * color1.rgb, 0., 1.);
    vec3 diffuse2 = clamp(diffuse * color2.rgb, 0., 1.);
    vec3 diffuse3 = clamp(diffuse * color3.rgb, 0., 1.);

    vec3 refl = normalize(-reflect(I, normal));
    float spec = max(dot(refl, normalize(eyeDir)), 0.);

    const vec3 spec_clr = vec3(.8, .9, 1.);
    float dst = clamp(length(eyeDir),1. , 500.);
    spec = pow(spec, 0.3 * 300.)* pow(.85, dst);

    spec = clamp(spec, 0., 1.);

    vec3 Ispec = spec * spec_clr;

    float dist = length(eyeDir);
    float atten = pow(0.93, dist * 7. );
    float deep = occlusion(p, normal) * atten;
    const float one_of_third = 1./3.;

    // Some logic workaround, hard to say what is better from performance reason
    float third1 = max(0., sign(one_of_third - deep));
    float third2 = (1. - third1) * max(0., sign(2. * one_of_third - deep));
    float third3 = (1. - third1) * (1. - third2);

    return Ispec + third1 * mix(diffuse0, diffuse1, deep * 3.) + 
        third2 * mix(diffuse1, diffuse2, (deep - one_of_third) * 3.) +
        third3 * mix(diffuse2, diffuse3, (deep - 2. * one_of_third) * 3.)
        ;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 centered_uv = uv * 2. - 1.;
    centered_uv.x *= iResolution.x / iResolution.y;

    vec2 sunPos = vec2(.845 * iResolution.x / iResolution.y, .75);

    float timeOffset = iTime / 5.;

    vec3 O = vec3(0., 0.1, 1. - timeOffset);
    float h = scene(O) * 0.65;
    O.y -= h;

    vec3 D = normalize(vec3(centered_uv, -2.0)); //fov

    float hill;
    float path = trace(O, D, hill);
    vec3 coord = O + path * D;

    vec3 resColor;
    const vec3 skyBlueColor = vec3(0.529411765, 0.807843137, 0.980392157); // nice blue color
    const vec3 sunColor = vec3(1.0, 1.0, 1.);
    const vec3 sunGalo = vec3(.9, .9, .8);

    // Background color
    vec3 bgColor = mix(vec3(1.), skyBlueColor, clamp(centered_uv.y, 0., 1.));
    float sunDst = length(centered_uv - sunPos) ;
    float sunFluctuation = valueNoiseSimple(centered_uv - sunPos + timeOffset);
    sunFluctuation = clamp(sunFluctuation * .25, 0.1, .2);

    float galoVal= exp(-pow(sunDst * 0.35, 1.15));
    float val  = clamp(1. / (sunDst *110.5), 0., 1.);

    bgColor = mix(bgColor, sunColor*val + (galoVal + sunFluctuation) * sunGalo, galoVal);

    vec3 lightPos = vec3(20., 90. -h, -95. - timeOffset);
    vec3 normal = snormal(coord);

    resColor = enlight(coord, normal, O, lightPos);
    resColor = mix(resColor, bgColor, min(hill, 1.));


    fragColor = vec4(resColor, 1.);
}

