   // }

    Particle star    = Particle(vec2(0.,0.) ,  vec2(0.,0.),  100.);
    Particle planet  = Particle(vec2(-50.,0.), vec2(0.,5.),    10.);
    Particle planet2 = Particle(muv2,  (muv-muv2)/4.,    20.);
    Particle planet3 = Particle(vec2(-60.,0.), vec2(0.,3.),    1.);

    float delta = 1./FPS;
    float n = 0.;

    float time = mod(iTime,delta*float(Iterations)/Speed);

    float a = 0.;
    float b = 0.;
    float c = 0.;
    float d = 0.;
    for(int i = 0; i < Iterations; i++){
        if(dot(star.pos - uv,   star.pos - uv)    < star.mass/20.)    a = 1.;
    	if(dot(planet.pos - uv, planet.pos - uv)  < planet.mass/20.)  b = 1.;
    	if(dot(planet2.pos - uv,planet2.pos - uv) < planet2.mass/20.) c = 1.;
    	if(dot(planet3.pos - uv,planet3.pos - uv) < planet3.mass/20.) d = 1.;
        #ifdef StarMovement
          star = updateP(star,planet, delta);
          star = updateP(star,planet2,delta);
          star = updateP(star,planet3,delta);
        #endif
        planet = updateP(planet,star,delta);
        planet = updateP(planet,planet2,delta);
        planet = updateP(planet,planet3,delta);
        planet2 = updateP(planet2,star,delta);
        planet2 = updateP(planet2,planet,delta);
        planet2 = updateP(planet2,planet3,delta);
        planet3 = updateP(planet3,star,delta);
        planet3 = updateP(planet3,planet,delta);
        planet3 = updateP(planet3,planet2,delta);
        n += delta;
        if(n > time*Speed) break;
    }

    if(dot(star.pos - uv,   star.pos - uv)    < star.mass)    a = 1.;
    if(dot(planet.pos - uv, planet.pos - uv)  < planet.mass)  b = 1.;
    if(dot(planet2.pos - uv,planet2.pos - uv) < planet2.mass) c = 1.;
    if(dot(planet3.pos - uv,planet3.pos - uv) < planet3.mass) d = 1.;
    if     (a > 0.5)
        fragColor = vec4(vec3(1.,1.,0.),1.);
    else if(b > 0.5)
        fragColor = vec4(vec3(0.,1.,0.),1.);
    else if(c > 0.5)
        fragColor = vec4(vec3(1.,1.,1.),1.);
    else if(d > 0.5)
        fragColor = vec4(vec3(1.,0.,0.),1.);
    else 
        fragColor = vec4(vec3(0.),1.);

}

