//Using Polar form, so any real number power is possible.

const float n1 = 2.; //Power 1
const float n2 = 4.; //Power 2
const float iterations = 50.;

vec2 Scale(vec2 p){
	return (2.*p-iResolution.xy)/iResolution.y*2.;
}


float mandelbrot(vec2 c){
    vec2 z = vec2(0);

    float t = sin(iTime/2.)*.5+.5;

	for (float i=0.;i<iterations;i++){
		//to (r,a)
		float rad = length(z);
		float phi = atan(z.y,z.x);

		//escaped?
		if (rad>5.) return i;

		//calculation1
		float rad1 = pow(rad,n1);
		float phi1 = phi*n1;

        //calculation2
		float rad2 = pow(rad,n2);
		float phi2 = phi*n2;

		//to (x,y)
		z = vec2(
			cos(phi1)*rad1,
			sin(phi1)*rad1
		)*t+
        vec2(
			cos(phi2)*rad2,
			sin(phi2)*rad2
		)*(1.-t)
        +c;
	}	
	return iterations;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 scaledp = Scale(fragCoord.xy);

	vec3 color = vec3(mandelbrot(scaledp)/iterations);

	fragColor = vec4(color, 1.0);
}

