// Procedural texture generation for the water
vec3 water(vec2 uv, vec3 cdir)
{
    uv *= vec2(0.25);

#if PARALLAX_WATER
    // Parallax height distortion with two directional waves at
    // slightly different angles.
    vec2 a = 0.025 * cdir.xz / cdir.y; // Parallax offset
    float h = sin(uv.x + iTime); // Height at UV
    uv += a * h;
    h = sin(0.841471 * uv.x - 0.540302 * uv.y + iTime);
    uv += a * h;
#endif

#if DISTORT_WATER
    // Texture distortion
    float d1 = mod(uv.x + uv.y, M_2PI);
    float d2 = mod((uv.x + uv.y + 0.25) * 1.3, M_6PI);
    d1 = iTime * 0.07 + d1;
    d2 = iTime * 0.5 + d2;
    vec2 dist = vec2(
    	sin(d1) * 0.15 + sin(d2) * 0.05,
    	cos(d1) * 0.15 + cos(d2) * 0.05
    );
#else
    const vec2 dist = vec2(0.0);
#endif

    vec3 ret = mix(WATER_COL, WATER2_COL, waterlayer(uv + dist.xy));
    ret = mix(ret, FOAM_COL, waterlayer(vec2(1.0) - uv - dist.yx));
    return ret;
}

// Camera perspective based on [0..1] viewport
vec3 pixtoray(vec2 uv)
{
    vec3 pixpos;
    pixpos.xy = uv - 0.5;
    pixpos.y *= iResolution.y / iResolution.x; // Aspect correction
    pixpos.z = -0.6; // Focal length (Controls field of view)
    return normalize(pixpos);
}

// Quaternion-vector multiplication
vec3 quatmul(vec4 q, vec3 v)
{
    vec3 qvec = q.xyz;
    vec3 uv = cross(qvec, v);
    vec3 uuv = cross(qvec, uv);
    uv *= (2.0 * q.w);
    uuv *= 2.0;
    return v + uv + uuv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
#if ANTIALIAS
    for(int y = 0; y < 2; y++) {
        for(int x = 0; x < 2; x++) {
        	vec2 offset = vec2(0.5) * vec2(x, y) - vec2(0.25);
#else
        	vec2 offset = vec2(0.0);
#endif
            // Camera stuff
            vec2 uv = (fragCoord.xy + offset) / iResolution.xy;
            vec3 cpos = vec3(0.0, 4.0, 10.0); // Camera position
            vec3 cdir = pixtoray(uv);
            cdir = quatmul( // Tilt down slightly
                vec4(-0.19867, 0.0, 0.0, 0.980067), cdir);
#if ANIMATE_CAM
            // Rotating camera
            float cost = cos(iTime * -0.05);
            float sint = sin(iTime * -0.05);
            cdir.xz = cost * cdir.xz + sint * vec2(-cdir.z, cdir.x);
            cpos.xz = cost * cpos.xz + sint * vec2(-cpos.z, cpos.x);
#endif

            // Ray-plane intersection
            const vec3 ocean = vec3(0.0, 1.0, 0.0);
            float dist = -dot(cpos, ocean) / dot(cdir, ocean);
            vec3 pos = cpos + dist * cdir;

            vec3 pix;
            if(dist > 0.0 && dist < 100.0) {
                // Ocean
                vec3 wat = water(pos.xz, cdir);
                pix = mix(wat, FOG_COL, min(dist * 0.01, 1.0));
            } else {
                // Sky
                pix = mix(FOG_COL, SKY_COL, min(cdir.y * 4.0, 1.0));
            }
#if ANTIALIAS
        	fragColor.rgb += pix * vec3(0.25);
    	}
    }
#else
    fragColor.rgb = pix;
#endif
}

// Shader Created by Phil Lira - felipedrl@gmail.com

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	// Inputs - Play with these values.
    const int steps = 100;
    const float scale = 2.5;
    const vec2 pan = vec2(0.6, 0.5);
    const float animationFactor = 8.0;
    const float minN = 1.0;
    const float maxN = 4.0;
    ////////////////////////////////

    vec2 c = fragCoord.xy / iResolution.xy;

    // map from [0,1] to [-0.5,0.5] then apply pan and scale.
    c = (c - pan) * scale;

	// apply aspect ratio to fix distortion     
    c.x *= iResolution.x / iResolution.y;

    int currStep = 0;

    // modelling complex number with vec2
    // z = x + yi
    vec2 z = vec2(0.0, 0.0);

    float alpha = abs(sin(iTime / animationFactor));
    float n = minN + alpha * (maxN - minN);

    for (int i = 0; i < steps; i++)
    {
        // General formula of Z -> pow(Z, n) + C
        float x2 = z.x * z.x;
        float y2 = z.y * z.y;

        float powX2Y2 = pow(x2 + y2, n * 0.5);
        float atanN = n * atan(z.y, z.x);

        float x = powX2Y2 * cos(atanN) + c.x;
		float y = powX2Y2 * sin(atanN) + c.y;

        if ((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;

        currStep++;
    }

    float color = float(currStep) / float(steps);
    float r = (currStep < steps) ? color * color : 0.0; 
	fragColor = vec4(log(r * 15.0) / log(n), color, color, 1.0);
}

