bool box(vec3 ro, vec3 rd, vec3 sz, out float tN, out float tF, out vec3 n) {
    vec3 m = 1./rd,
         k = abs(m)*sz,
         a = -m*ro-k*.5, b = a+k;
    n = -sign(rd) * step(a.yzx,a.xyz) * step(a.zxy,a.xyz);
    tN = max(max(a.x,a.y),a.z);
    tF = min(min(b.x,b.y),b.z);
    return tN<tF;
}

// - Bicubic --------------------------------------------------------
// [iq: https://www.shadertoy.com/view/XsSXDy]

vec4 BS_A = vec4(3.0,  -6.0,   0.0,  4.0 ) /  6.;
vec4 BS_B = vec4(-1.0,   6.0, -12.0,  8.0 ) /  6.;
vec4 powers( float x ) { return vec4(x*x*x, x*x, x, 1.0); }

float spline(float x, float c0, float c1, float c2, float c3 ) {
    return c0*dot( BS_B, powers(x + 1.)) + c1*dot( BS_A, powers(x      )) +
           c2*dot( BS_A, powers(1. - x)) + c3*dot( BS_B, powers(2. - x));
}

#define SAM(a,b) textureLod(iChannel0, (i+vec2(a,b)+0.5)/res,0.).g

float texture_Bicubic(sampler2D tex, vec2 t) {
    vec2 res = iChannelResolution[0].xy;
    vec2 p = res*t - 0.5, f = fract(p), i = floor(p);
    return spline( f.y, spline( f.x, SAM(-1,-1), SAM( 0,-1), SAM( 1,-1), SAM( 2,-1)),
                        spline( f.x, SAM(-1, 0), SAM( 0, 0), SAM( 1, 0), SAM( 2, 0)),
                        spline( f.x, SAM(-1, 1), SAM( 0, 1), SAM( 1, 1), SAM( 2, 1)),
                        spline( f.x, SAM(-1, 2), SAM( 0, 2), SAM( 1, 2), SAM( 2, 2)));
}

#ifdef BICUBIC
float valueAt(in vec3 p) {
    p*= 2.2;
    float cval0 = texture_Bicubic(iChannel0, .6*p.xy/MAX_STEPS);
    float cval1 = texture_Bicubic(iChannel0, .5*p.xz/MAX_STEPS);
    // Values
    float v0 = sqrt(cval0*cval1);  // true value = v0*255.
    return clamp(.1+.3*abs(cos(iTime*.1)) + .5*v0/*.5*mix(v0, v1, fract(p.z))*/,0.,1.);
}
#else
float valueAt(in vec3 p) {
    p*= 1.8;
    vec4 cval0 = textureLod(iChannel0, .6*p.xy/MAX_STEPS,0.);
    vec4 cval1 = textureLod(iChannel0, .5*p.xz/MAX_STEPS,0.);
    // Values
    float v0 = sqrt(cval0.g*cval1.b) + (cval0.r*cval1.g);  // true value = v0*255.
    return clamp(.1+.3*abs(cos(iTime*.1)) + .5*v0/*.5*mix(v0, v1, fract(p.z))*/,0.,1.);
}
#endif

