vec3 normalAt(in vec3 p) {
    vec2 e = vec2(0.1, -0.1); 
    return normalize(e.xyy * valueAt(p + e.xyy) + e.yyx * valueAt(p + e.yyx) + e.yxy * valueAt(p + e.yxy) + e.xxx * valueAt(p + e.xxx));;
}

const vec2 ep2 = vec2(.5,-.5); 
vec3 gradAt(in vec3 p) {
    return (ep2.xyy * valueAt(p + ep2.xyy) + ep2.yyx * valueAt(p + ep2.yyx) + ep2.yxy * valueAt(p + ep2.yxy) + ep2.xxx * valueAt(p + ep2.xxx));;
}



