float doShadow(vec3 ro, vec3 rd, float len) {
    vec3 p2,n;
    float dmin, dmax;
    bool intersect = box(ro, rd, Bounds, dmin, dmax, n);
    if (intersect) {   
        float val,valId,d=min(.25,dmax); //, d=.5+.5*dd*hash1(ro.x+ro.y+ro.z);
        // suivre cette direction et ajouter un truc a ao si on rtouve un obstacle avant dMax
        for(int k=0;k<40; k++) {
             if (d>=dmax) break;
            p2 = ro + min(d,dmax) *rd;
            val = valueAt(p2);
            if (val >= MinVal && val < MaxVal) {
                // on a rencontrÃ© un obstacle,
                return clamp(mix(0.,1.,(min(d,dmax)/len)),0.,1.); // plus il est pres plus il a de l'influence
            }
            d+=.25*dd;

        }
    }
    return 1.;
}


vec3 doShading(vec3 rd, vec3 pos, vec3 nor, vec3 lig, vec3 col, float t) {

    vec3 ref = reflect( rd, nor );	        
    float occ = 1.; 
    float amb = .9;
    float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
    float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
    float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

#ifdef SHADOWS
    dif *= doShadow(pos, lig, 10. );
#endif
    vec3 brdf = vec3(0.0);
    brdf += 1.20*dif*vec3(1.00,0.90,0.60);
    brdf += 1.20*spe*vec3(1.00,0.90,0.60)*dif;
    brdf += 0.30*amb*vec3(0.50,0.70,1.00)*occ;
    brdf += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
    brdf += 0.4;
    return col*brdf; //sqrt(brdf);
}

vec3 heatmapGradient(in float t) {
    return clamp((pow(t, 1.5) * .8 + .2) * vec3(smoothstep(0., .35, t) + t * .5, smoothstep(.5, 1., t), max(1. - t * 1.7, t * 7. - 6.)), 0., 1.);
}

vec3 getColor(float val) {
#ifndef COLOR_GRADIENT    
    val = floor(val/dval)*dval;                
#endif    
    return heatmapGradient(1.5*val);
}

