float preciseSurface(vec3 ro, vec3 rd, float val, float dmin, float dmax, float sig) {
    float dm = dmin;
    val *= sig;
    for (int j=0; j<6;j++) {
       dm = (dmin + dmax)*.5;  // on dÃ©termine l'indice de milieu
       if (valueAt(ro+rd*dm)*sig > val) dmax = dm;  //si la valeur qui est Ã  la case "im" est supÃ©rieure Ã  la valeur recherchÃ©e, l'indice de fin "ifin" << devient >> l'indice de milieu
       else dmin = dm;               //sinon l'indice de dÃ©but << devient >> l'indice de milieu et l'intervalle est de la mÃªme faÃ§on restreint
    }
    return dm;  
}

float findPosOfVal(vec3 ro, vec3 rd, float val, float dmin, float dmax, float dd2) {
    vec3 p = ro+rd*dmin;
    float v, d, sig = sign(val - valueAt(p));
    d = dmin+dd2+hash1(rd.y*(p.x+p.y+p.z));

    for (int i=0; i<100; i++) {
        v = valueAt(ro+rd*d);
    	if ((d>=dmax+dd2) || (sig*(v-val)>0.)) 
            break;
        d += dd2;
    }
    return (sig*(v-val)>0.) ? preciseSurface(ro, rd, val, d-dd, d, sig) : d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    globalFragCoord = fragCoord;
    Bounds = vec3(.8*SX, .7*SY, abs(.5)*SZ);

    vec2 q = fragCoord.xy/resolution.xy;

    vec3 lightDir = -normalize(vec3(1.,-5.,6.5));

    vec3 ro = 180.*vec3(cos(.5*time-3.*iMouse.x/resolution.x), cos(.5*time*.3), sin(.5*time-3.*iMouse.x/resolution.x)*sin(.5*time*.3));
    vec3 rd = RD(ro, vec3(0));

    vec4 cSum = vec4(0);
    vec3 n,p;

    float d, dmin, dmax,
          val, valIdMem, valId, v0;

    vec3 col = vec3(.22,.26,.28) * pow(16.0*q.x*q.y*(1.-q.x)*(1.-q.y),.3);

    if (box(ro, rd, Bounds, dmin, dmax, n) && dmin>0.) {
       p = ro+rd*(dmin);
       v0 = valueAt(p);

       if (v0 >= MinVal && v0 <= MaxVal) {
           col = doShading(rd,p,n,lightDir, getColor(v0), d);
           col = col*isoline(v0, p, n, dval, 1.);
       } else {
           val = (v0<=MinVal) ? MinVal : MaxVal;
           d = findPosOfVal(ro, rd, val, dmin, dmax, dd);
		   if (d<dmax) {
            	p = ro+rd*d;
            	n = sign(v0-MinVal)*normalAt(p);
           		col = doShading(rd,p,n, lightDir, getColor(val-.02), d);
           }
     	}
    }

   	fragColor = vec4(col,1);  
}

const mat2 axes = mat2(2., 2., 1., -1);
const vec3 bfreq = vec3(-0.618, 1.618, 2.618) * 0.3; 
const mat2 bamp = mat2(1., 1., 1., -1.) * 0.1;
const vec3 mfreq = vec3(2.618, 0.618, 1.618) * 0.3; 
const mat2 mamp = mat2(1., 0., 0., 1.) * 0.1;
const mat4 palette = mat4(4.,1.,5.,5.,
                          5.,1.,4.,5.,
                          1.,2.,5.,5., 
                          2.,1.,5.,5.
                          )/5.;

vec2 grid(vec2 uv, int repeat) {
 return floor(vec2(mod(axes * uv * float(repeat), float(repeat))));
}

vec2 wave(vec2 base, vec3 freq, mat2 amplitude) {
    float z = dot(vec3(base, iTime), freq);
    return vec2(sin(z), cos(z)) * amplitude + base;
}

vec4 plaid(vec2 uv) {
 	bvec2 col = bvec2(grid(uv, 2));
	bool margin = any(bvec2(step(grid(uv*2., 6), vec2(2))));
    if (margin)
        return vec4(0);
    else 
        if (col.x)
        if (col.y) return palette[0];
        else return palette[1];
        else if (col.y)
            return palette[2];
            else return palette[3];
}

float mask(vec2 uv) {
 	bvec2 x = bvec2(grid(uv, 3));
    return float(!any(x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / min(iResolution.x, iResolution.y) * 10.;
    vec4 back = plaid(wave(uv, bfreq, bamp));
	float mask = mask(wave(uv, mfreq, mamp));
    fragColor = back * mask;
}

