const float MaxDistance = 80.;
const vec3 FadeLightSource = vec3(4., 4., -2.);
const vec3 LightSource2 = vec3(-10., 10., 10.);

const vec3 TableColor = vec3(0.42, 0.57, 0.14);
const vec3 BarrierColor = vec3(0.55, 0.27, 0.08);
const vec3 RoomColor = vec3(0.37, 0.36, 0.21);
const vec3 DoorColor = vec3(0.);
const vec3 WinColor = vec3(2.);

const float BallRadius = 1.0;
const float BallReflectivity = 0.3;
const vec2 StartBallPos = vec2(0, 1);

const float TableHeight = 0.0;
const float BarrierThickness = 0.8;
const float RoomRadius = 20.0;

const float CamRotSpeed =  1.0 / 25.0;

#ifdef SQUARE_PATH
const float TableSize = 2.0 * BallRadius * (2.0 * M_PI / sqrt(2.0) + 1.0);
vec2 StartVel = normalize(vec2(1, 1));
