#else
float TableSize = 2.0 * BallRadius * (2.0 * M_PI * 10.0 / sqrt(101.0) + 1.0);
vec2 StartVel = normalize(vec2(0.1, 1));
const float EyePosRadius = 11.0;
#endif

const float HalfSizeTable = TableSize / 2.0;

float TableSurface(vec3 a) {
	vec3 sz = vec3(HalfSizeTable, 0, HalfSizeTable);
	return length(max(abs(a) - sz, 0.));
}

float Box(vec3 a, vec3 sz) {
	return length(max(abs(a) - sz, 0.));
}

float sBox(vec3 a, vec3 sz) {
  vec3 d = abs(a) - sz;
  return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float Barrier(vec3 a) {
	float ht = BarrierThickness / 2.0;
	float bx1 = sBox(a + vec3(HalfSizeTable + ht, -ht, 0), vec3(ht, ht, HalfSizeTable + BarrierThickness));
	float bx2 = sBox(a + vec3(-HalfSizeTable - ht, -ht, 0), vec3(ht, ht, HalfSizeTable + BarrierThickness));
	float bx3 = sBox(a + vec3(0, -ht, HalfSizeTable + ht), vec3(HalfSizeTable + BarrierThickness, ht, ht));
	float bx4 = sBox(a + vec3(0, -ht, -HalfSizeTable - ht), vec3(HalfSizeTable + BarrierThickness, ht, ht));
	return min(min(bx1, bx2), min(bx3, bx4));
}

float Room(vec3 a) {
	float wallSize = 60.0;
	float hs = wallSize / 2.0;
	float floorDist = wallSize / 8.0;
 	float wl1 = sBox(a + vec3(RoomRadius, -hs + floorDist, 0), vec3(0.1, hs, hs));
	float wl2 = sBox(a + vec3(-RoomRadius, -hs + floorDist, 0), vec3(0.1, hs, hs));
	float wl3 = sBox(a + vec3(0, -hs + floorDist, RoomRadius), vec3(hs, hs, 0.1));
	float wl4 = sBox(a + vec3(0, -hs + floorDist, -RoomRadius), vec3(hs, hs, 0.1));
	float cl = sBox(a + vec3(0, -wallSize + floorDist, 0), vec3(hs, 0.1, hs));
	float fl = sBox(a + vec3(0, floorDist, 0), vec3(hs, 0.1, hs));
	return min(min( min(wl1, wl2), min(wl3, wl4) ), min(cl, fl));
}

float Doors(vec3 a) {
	float size = 25.0;
	float floorDist = 10.0;
	float hs = size / 2.0;
    vec3 sz = vec3(hs / 4.0, hs, 0.2);
	float dr1 = Box(a + vec3(0.0, -hs + floorDist, RoomRadius), sz);
	float dr2 = Box(a + vec3(0.0, -hs + floorDist, -RoomRadius), sz);
	return min(dr1, dr2);
}

float Windows(vec3 a) {
	float size = 20.0;
    float floorDist = 1.0;
	float hs = size / 2.0;
    vec3 sz = vec3(0.15, hs, hs / 6.0);
	float w1 = Box(a + vec3(RoomRadius, -hs - floorDist, 0.0), sz);
	float w2 = Box(a + vec3(-RoomRadius, -hs - floorDist, 0.0), sz);
	return min(w1, w2);
}

float Amplitude = (TableSize / 2.0 - BallRadius);

float GetPos(float dist, float startPos) {
	return Amplitude * (mod(dist - startPos, 2.0) - 1.0) * sign(mod(dist - startPos, 4.0) - 2.0);
}

vec2 GetBallPos(float time) {
	return vec2(GetPos(StartVel.x * time, StartBallPos.x), GetPos(StartVel.y * time, StartBallPos.y));
}

vec3 ballCenter;

float Ball(vec3 a) {
	return length(a - ballCenter) - 1.;
}

float Scene(vec3 a) {
	return min( min( min(Ball(a), TableSurface(a)), min(Room(a), Barrier(a)) ), min(Doors(a), Windows(a)));
}

const vec4 CanvasColor = vec4(247.0 / 255.0, 242.0 / 255.0, 212.0 / 255.0, 1.0);
const vec4 DigitColor = vec4(0.0, 0.0, 0.0, 1);

vec4 DrawCircle(vec2 pos, vec2 center, vec4 color) {
	float radius = 0.133;

	float k = 2.0; // w/h = 2

	vec2 cPos = pos - center;
	cPos.x *= k;

	if (length(cPos) < radius)
		color = CanvasColor;

	// digits
	float h_2 = 0.052;
	float w = 0.022;
	float dc1 = -0.030;

	if ((dc1 - w < cPos.x) && (cPos.x < dc1) && (-h_2 < cPos.y) && (cPos.y < h_2))
		color = DigitColor;

	float r = 0.054;
	float dc0 = 0.05;
	vec2 zcPos = vec2(cPos.x * 1.5 - dc0, cPos.y);

	vec2 zcPos2 = vec2((cPos.x - dc0 / 1.5) * 2.0, cPos.y);

	if ( ( (r - w) < length(zcPos2) ) && (length(zcPos) < r) )
		color = DigitColor;

	return color;
}

vec4 DrawTex(vec2 pos) {
	const float stripWidth = 0.45;

	const vec4 stripColor = vec4(20.0 / 255.0, 50.0 / 255.0, 80.0 / 255.0, 1.0);

	vec4 res = CanvasColor;

	// draw strip
	if ( (pos.y > (0.5 - stripWidth / 2.0)) && (pos.y < (0.5 + stripWidth / 2.0)) )
		res = stripColor;

	// draw circles with digits
	res = DrawCircle(pos, vec2(0.245, 0.5), res);
	res = DrawCircle(pos, vec2(0.755, 0.5), res);

	return res;
}

// Rotate around axis
vec4 Quat(vec3 axis, float angle) {
	vec4 quat;
	quat.xyz = axis * sin(angle * 0.5);
	quat.w = cos(angle / 2.0);
	return quat;
}

// Multiply quaternion by vector
vec3 Transform(vec4 q, vec3 v) {
	vec3 uv = 2.0 * cross(q.xyz, v);
	vec3 uuv = cross(q.xyz, uv);
	return v + uv * q.w + uuv;
}

vec3 uVec, rVec; // transformed BallUpVec and BallRightVec

vec3 BallTextureColor(vec3 a) {
	vec3 eVec = normalize(cross(rVec, uVec)); // eye vec

	vec3 radVec = normalize(a - ballCenter);

	float angle1 = acos(-dot(radVec, uVec)) / M_PI;

	vec3 N = uVec;    // plane normal
	float K = -dot(N, radVec);
	vec3 prRadVec = radVec + K * N;  // equator plan projection
	vec3 equatorRadVec = normalize(prRadVec);

	float semiSphere = dot(equatorRadVec, eVec);
	float angle2 =  acos(-dot(equatorRadVec, rVec)) / M_PI / 2.0;
	if (semiSphere < 0.0)
		angle2 = acos(dot(equatorRadVec, rVec)) / M_PI / 2.0 + 1.0/2.0;

	return DrawTex(vec2(angle2, angle1)).rgb;
}

struct MaterialInfo {
	vec3 Kd;
	float Shininess;
};

bool ballReflection = false;

MaterialInfo Material(vec3 a) {
	MaterialInfo m = MaterialInfo(vec3(0.), 0.);
	float closest = MaxDistance;

	float roomDistance = Room(a);
	if (roomDistance < closest) {
		m.Kd = RoomColor;
		m.Shininess = 20.;
		closest = roomDistance;
	}

	float doorDistance = Doors(a);
	if (doorDistance < closest) {
		m.Kd = DoorColor;
		m.Shininess = 0.;
		closest = doorDistance;
	}

	float winDistance = Windows(a);
	if (winDistance < closest) {
		m.Kd = WinColor;
		m.Shininess = 0.;
		closest = winDistance;
	}

	float tableDistance = TableSurface(a);
	if (tableDistance < closest) {
		m.Kd = TableColor;
		m.Shininess = 20.;
		closest = tableDistance;
	}

	float barrierDistance = Barrier(a);
	if (barrierDistance < closest) {
		m.Kd = BarrierColor;
		m.Shininess = 80.;
		closest = barrierDistance;
	}

	float ballDistance = Ball(a);
	if ((ballDistance < closest) && (!ballReflection)) {
		m.Kd = BallTextureColor(a);
		m.Shininess = 150.;
		ballReflection = true;	
	}
	return m;
}

vec3 Normal(vec3 a) {
	vec2 e = vec2(.001, 0.);
	float s = Scene(a);
	return normalize(vec3(
		Scene(a+e.xyy) - s,
		Scene(a+e.yxy) - s,
		Scene(a+e.yyx) - s));
}

float Occlusion(vec3 at, vec3 normal) {	
	float b = 0.;
	for (int i = 1; i <= 4; ++i) {		
		float L = .06 * float(i);
		float d = Scene(at + normal * L);
		b += max(0., L - d);
	}
	return min(b, 1.);
}

vec3 LookAt(vec3 pos, vec3 at, vec3 rDir) {
	vec3 f = normalize(at - pos);
	vec3 r = cross(f, vec3(0., 1., 0.));
	vec3 u = cross(r, f);
	return mat3(r, u, -f) * rDir;
}

float Trace(vec3 rPos, vec3 rDir, float distMin) {
	float L = distMin;
	for (int i = 0; i < MAX_STEPS; ++i) {
		float d = Scene(rPos + rDir * L);
		L += d;
		if (d < EPSILON * L) break;
	}
	return L;
}

vec3 Lighting(vec3 at, vec3 normal, vec3 eye, MaterialInfo m, vec3 lColor, vec3 lPos) {
	vec3 lDir = lPos - at;

	vec3 lDirN = normalize(lDir);
	float t = Trace(at, lDirN, EPSILON*2.);
	if (t < length(lDir)) {
		return vec3(0.);
	}
	vec3 color = m.Kd * lColor * max(0., dot(normal, normalize(lDir)));

	if (m.Shininess > 0.) {
		vec3 h = normalize(normalize(lDir) + normalize(eye - at));
		color += lColor * pow(max(0., dot(normal, h)), m.Shininess) * (m.Shininess + 8.) / 25.;
	}
	return color;
}

vec3 FadeLighting(vec3 at, vec3 normal, vec3 eye, MaterialInfo m) {	
	vec3 color = Lighting(at, normal, eye, m, vec3(1.2), FadeLightSource);
	vec3 lDir = FadeLightSource - at;
	return 5.0 * color / dot(lDir, lDir);
}

vec3 Shade(vec3 rpos, vec3 rdir, float t, vec3 pos, vec3 nor)
{	
	MaterialInfo mat = Material(pos);

	// ambient light
	vec3 color = .11 * (1. - Occlusion(pos, nor)) * mat.Kd;

	// light from light source
	color += FadeLighting(pos, nor, rpos, mat);
	color += Lighting(pos, nor, rpos, mat, vec3(0.6), LightSource2);

	return color;
}


vec3 ShadeAndReflect(vec3 rpos, vec3 rdir, float t)
{
	vec3 pos = rpos + rdir * t;
	vec3 nor = Normal(pos);

	vec3 color = Shade(rpos, rdir, t, pos, nor);

	if (ballReflection)
	{
		rdir = normalize(reflect(rdir, nor));
		rpos = pos + rdir * EPSILON * 2.;

		float t = Trace(rpos, rdir, 0.);

		pos = rpos + rdir * t;
		nor = Normal(pos);
		color += Shade(rpos, rdir, t, pos, nor) * BallReflectivity;

		ballReflection = false;
	}

	return color;
}

vec3 Camera(vec2 px) {
    #ifdef CAM_MOVING
	vec3 camPos = vec3(EyePosRadius * sin(CamRotSpeed * iTime), 3., EyePosRadius * cos(CamRotSpeed * iTime));
	#else
	vec3 camPos = vec3(0., 3., EyePosRadius);
	#endif

	vec2 uv = px / iResolution.xy * 2. - 1.;	
	uv.x *= iResolution.x / iResolution.y;
	vec3 rayStart = camPos;
	vec3 rayDirection = LookAt(rayStart, vec3(0, -1, 0), normalize(vec3(uv, -2.)));
	float path = Trace(rayStart, rayDirection, 0.);	
	return ShadeAndReflect(rayStart, rayDirection, path);
}

void CreateBall() {
	vec2 ballPos = GetBallPos(iTime);
	ballCenter = vec3(ballPos.x, TableHeight + BallRadius, ballPos.y);

	const float deltaTime = 0.001;
	vec2 ballPosPrev = GetBallPos(iTime - deltaTime);
	vec2 moveVec = ballPos - ballPosPrev;

	// Rotate aroung path normal
	vec4 quat = Quat(normalize(vec3(moveVec.y, 0, -moveVec.x)), length(Amplitude * iTime * StartVel));

	const vec3 BallUpVec = vec3(0.0, 1.0, 0.0);
	const vec3 BallRightVec = vec3(1.0, 0.0, 0.0);

	uVec = normalize(Transform(quat, BallUpVec));
	rVec = normalize(Transform(quat, BallRightVec));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	CreateBall();
	vec3 col = vec3(0.0);

	#ifdef ANTIALIASING

    	#ifdef AA5
		col = Camera(fragCoord.xy);
		col += Camera(vec2(fragCoord.x + AADist, fragCoord.y + AADist));
		col += Camera(vec2(fragCoord.x - AADist, fragCoord.y + AADist));
		col += Camera(vec2(fragCoord.x + AADist, fragCoord.y - AADist));
		col += Camera(vec2(fragCoord.x - AADist, fragCoord.y - AADist));
		col /= 5.0;
    	#else
		col = Camera(vec2(fragCoord.x, fragCoord.y + AADist));
		col += Camera(vec2(fragCoord.x + AADist * sqrt(3.0) / 2.0, fragCoord.y - AADist / 2.0));    	
		col += Camera(vec2(fragCoord.x - AADist * sqrt(3.0) / 2.0, fragCoord.y - AADist / 2.0));
		col /= 3.0;
    	#endif

    #else
	col = Camera(fragCoord.xy);
	#endif

	fragColor = vec4(col, 0.);
}

