  if (d < dHit) { dHit = d;  qHit = p;  idObj = idLogs; }
  q = p;  q.x = abs (q.x) - 0.2;
  q.y -= fCylPos.y - fCylLen + 0.43;
  d = PrCapsDf (q, 0.12 - 0.04 * Noisefv3a (15. * p), 0.4);
  if (d < dHit) { dHit = d;  qHit = p;  idObj = idLogs; }
  q = p;  q.y -= fCylPos.y - fCylLen - 0.01;
  d = PrCylDf (q.xzy, fCylRad, 0.01);
  if (d < dHit) { dHit = d;  qHit = q;  idObj = idCoal; }
  return dHit;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dHit, d;
  dHit = dstFar;
  if (tryFlm) {
    q = p;  q -= fCylPos;
    d = PrCylDf (q.xzy, fCylRad, fCylLen);
    if (d < dHit) { dHit = d;  idObj = idFCyl;  qHit = q; }
    q = p;  q.y -= 0.4;
    d = PrBoxDf (q, vec3 (2., 0.01, 6.));
    if (d < dHit) { dHit = d;  idObj = idPool;  qHit = q; }
  } else {
    dHit = 0.9 * BldgDf (p, dHit);
    dHit = FireDf (p, dHit);
  }
  return dHit;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float d;
  float dHit = 0.;
  for (int j = 0; j < 200; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  const vec3 e = vec3 (0.001, -0.001, 0.);
  vec4 v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd, float dLight)
{
  float sh = 1.;
  float d = 0.15;
  for (int i = 0; i < 30; i++) {
    float h = ObjDf (ro + rd * d);
    sh = min (sh, 20. * h / d);
    d += max (0.15, 0.01 * d);
    if (h < 0.01 || d > dLight) break;
  }
  return clamp (sh, 0., 1.);
}

vec3 FireSrcCol (vec3 n)
{ // Inspired by Dave_H's "Campfire", but implementation differs.
  vec3 col, q;
  float f, di, gl, bri;
  if (idObj == idLogs) {
    n = VaryNf (0.4 * qHit, n, 5.);
    q = 45. * qHit;  q.y -= 0.8 * tCur;
    f = Noisefv3a (q);   
    f += Noisefv3a (10. * qHit) + 0.4 * Noisefv3a (123. * qHit);
    f = 0.01 * pow (abs (f), 13.) + 0.7 * max (1. - 0.11 * dot (qHit, qHit), 0.);
    bri = max (dot (normalize (vec3 (0., 0.65 + flmFlkr, 0.) - qHit), n), 0.4);
    col = bri * bri * f * vec3 (0.5, 0.1, 0.);
    di = 0.;
    gl = 0.;
    for (int i = 0; i < 3; i ++) {
      di += 0.02;
      gl += max (0., di - 1.2 * ObjDf (qHit + di * n));
    }
    col += flmFlkr * flmFlkr * clamp (2. * gl, 0., 1.) * vec3 (1., 0.3, 0.05);
  } else if (idObj == idCoal) {
    q.xz = 20. * qHit.xz;  q.y = tCur;
    f = Noisefv3a (11. * q);
    q.y = 0.04 * tCur;
    f += Noisefv3a (3. * q);
    bri = 3. - 0.5 * flmFlkr;
    col = 0.1 * bri * bri * pow (f, 4.) * vec3 (0.7, 0.05, 0.) *
       (1.1 - pow (length (qHit.xz) / fCylRad, 4.));
  }
  return col;
}

float FireLum (vec3 ro, vec3 rd, float dHit)
{
  vec3 p, q, dp;
  float g, s, fh, fr, f;
  p = ro;
  dp = (fCylRad / 40.) * rd;
  g = 0.;
  for (int i = 0; i < 40; i ++) {
    p += dp;
    s = distance (p.xz, fCylPos.xz);
    q = 4. * p;  q.y -= 6. * tCur;
    fh = 0.5 * max (1. - (p.y - fCylPos.y) / fCylLen, 0.);
    fr = max (1. - s / fCylRad, 0.);
    f = Fbm3 (q);
    q = 7. * p;  q.y -= 8.5 * tCur;
    f += Fbm3 (q);
    g += max (0.5 * fr * fr * fh * (f * f - 0.6), 0.);
    q = 23. * p;  q.y -= 11. * tCur;
    g += 1000. * pow (abs (Noisefv3a (q) - 0.11), 64.);
    if (s > fCylRad || p.y < fCylPos.y - 0.99 * fCylLen || g > 1.) break;
  }
  return g;
}

vec3 ObjCol (vec3 n)
{
  vec3 col;
  if (idObj == idBase) col = vec3 (0.8, 0.8, 0.7);
  else if (idObj == idCol || idObj == idColEnd) col = vec3 (0.7, 0.8, 0.6);
  else if (idObj == idRoof) col = vec3 (0.8, 0.1, 0.1) *
      (1. - 0.6 * SmoothBump (0.98, 1.02, 0.01, mod (qHit.z, 2.))) *
      (1. - 0.6 * SmoothBump (0.98, 1.02, 0.01, mod (qHit.x, 2.)));
  else if (idObj == idRoofV) col = vec3 (0.8, 0.1, 0.1);
  else if (idObj == idPost) col = vec3 (0.8, 0.1, 0.1);
  else if (idObj == idBall) col = vec3 (1., 1., 0.1);
  else if (idObj == idGal) col = vec3 (0.1, 0.3, 0.1);
  else if (idObj == idAltr) col = vec3 (0.6, 0.5, 0.2);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 roo, rdo, objCol, flmCol, foVec, col, vn;
  float dstHit, dstFlm, dstHitR, dstFlmR, fIntens, fLum, lDist, sh, bk,
     dif, ltExt, reflFac, a, f;
  int idObjT;
  bool flmRefl, objRefl;
  fCylPos = vec3 (0., 3.53, 0.);
  fCylRad = 0.8;
  fCylLen = 2.;
  roo = ro;
  rdo = rd;
  idObj = -1;
  tryFlm = true;
  dstFlm = ObjRay (roo, rdo);
  flmRefl = false;
  if (dstFlm < dstFar && idObj == idPool) {
    dstFlmR = dstFlm;
    roo += dstFlm * rdo;
    vn = ObjNf (roo);
    rdo = reflect (rdo, vn);
    roo += 0.01 * rdo;
    idObj = -1;
    dstFlm = ObjRay (roo, rdo);
    flmRefl = true;
  }
  fIntens = (dstFlm < dstFar) ? FireLum (roo + dstFlm * rdo, rdo, dstFlm) : 0.;
  flmFlkr = Noiseff (tCur * 64.);
  idObj = -1;
  tryFlm = false;
  dstHit = ObjRay (ro, rd);
  if (idObj < 0) dstHit = dstFar;
  objRefl = false;
  reflFac = 1.;
  if (dstHit < dstFar && idObj == idPool) {
    dstHitR = dstHit;
    ro += dstHit * rd;
    vn = ObjNf (ro);
    rd = reflect (rd, VaryNf (0.4 * qHit, vn, 0.1));
    ro += 0.01 * rd;
    idObj = -1;
    dstHit = ObjRay (ro, rd);
    if (idObj < 0) dstHit = dstFar;
    reflFac = 0.8;
    objRefl = true;
  }
  idObjT = idObj;
  if (dstHit >= dstFar) col = 0.1 * BgCol (ro, rd);
  else {
    ro += rd * dstHit;
    vn = ObjNf (ro);
    if (idObj == idBase) vn = VaryNf (10. * qHit, vn, 0.4);
    if (idObj == idCol) {
      a = 0.5 - mod (12. * (atan (qHit.x, qHit.z) / (2. * pi) + 0.5), 1.);
      vn.xz = Rot2D (vn.xz, -0.1 * pi * sin (pi * a));
    }
    if (idObj == idCol || idObj == idColEnd) vn = VaryNf (20. * qHit, vn, 0.3);
    else if (idObj == idAltr) vn = VaryNf (10. * qHit, vn, 1.);
    idObj = idObjT;
    if (idObj == idLogs || idObj == idCoal) col = FireSrcCol (vn);
    else {
      objCol = ObjCol (vn);
      foVec = fCylPos - ro;
      lDist = length (foVec);
      foVec /= lDist;
      sh = ObjSShadow (ro, foVec, lDist);
      fLum = 5. * sh * (0.6 + 0.4 * flmFlkr) / pow (lDist, 1.5) *
         max (dot (foVec, vn), 0.);
      dif = max (dot (vn, sunDir), 0.);
      bk = max (dot (vn, - normalize (vec3 (sunDir.x, 0., sunDir.z))), 0.);
      ltExt = 0.1 * (0.4 * (1. + bk) + max (0., dif));
      col = objCol * (ltExt * vec3 (1.) + fLum * vec3 (1., 0.3, 0.2));
    }
  }
  idObj = idObjT;
  if (dstHit < dstFar && idObj == idBall) col *= 3.;
  if (! (dstHit < dstFar && (idObj == idCoal || idObj == idAltr) ||
     flmRefl == objRefl && dstHit < dstFlm ||
     objRefl && ! flmRefl && dstHitR < dstFlm ||
     ! objRefl && flmRefl && dstHit < dstFlmR)) {
    f = clamp (0.7 * fIntens, 0., 1.);
    f *= f;
    f *= f;
    flmCol = 1.5 * mix (vec3 (1., 0.2, 0.2), vec3 (1., 1., 0.5), f);
    fIntens *= fIntens;
    if (idObj == idLogs) fIntens *= 0.03;
    col = mix (col, flmCol, min (fIntens, 1.));
  }
  col *= reflFac;
  return col;
}

vec3 SetVuPt (float t)
{
  vec4 va, vb;
  vec3 wPt[11], ro;
  float tp[11], vel, tVu;
  wPt[0] = vec3 (0., 8., -20.);     wPt[1] = vec3 (0., 2., -12.);
  wPt[2] = vec3 (-0.5, 2.5, -6.2);  wPt[3] = vec3 (-2.6, 3., -6.2);
  wPt[4] = vec3 (-2.6, 4., 8.1);    wPt[5] = vec3 (0., 5.6, 8.9);
  wPt[6] = vec3 (2.6, 4., 8.1);     wPt[7] = vec3 (2.6, 3., -6.2);
  wPt[8] = wPt[2];  wPt[9] = wPt[1];  wPt[10] = wPt[0];
  tp[0]  = 0.;
  tp[1]  = tp[0] + distance (wPt[1],  wPt[0]); 
  tp[2]  = tp[1] + distance (wPt[2],  wPt[1]); 
  tp[3]  = tp[2] + distance (wPt[3],  wPt[2]); 
  tp[4]  = tp[3] + distance (wPt[4],  wPt[3]); 
  tp[5]  = tp[4] + distance (wPt[5],  wPt[4]); 
  tp[6]  = tp[5] + distance (wPt[6],  wPt[5]); 
  tp[7]  = tp[6] + distance (wPt[7],  wPt[6]); 
  tp[8]  = tp[7] + distance (wPt[8],  wPt[7]); 
  tp[9]  = tp[8] + distance (wPt[9],  wPt[8]); 
  tp[10] = tp[9] + distance (wPt[10], wPt[9]);
  vel = 0.5;
  tVu = mod (t, tp[10] / vel) * vel;
  if (tVu < tp[5]) {
    if (tVu < tp[1])      { va = vec4 (wPt[0], tp[0]);  vb = vec4 (wPt[1], tp[1]); }
    else if (tVu < tp[2]) { va = vec4 (wPt[1], tp[1]);  vb = vec4 (wPt[2], tp[2]); }
    else if (tVu < tp[3]) { va = vec4 (wPt[2], tp[2]);  vb = vec4 (wPt[3], tp[3]); }
    else if (tVu < tp[4]) { va = vec4 (wPt[3], tp[3]);  vb = vec4 (wPt[4], tp[4]); }
    else                  { va = vec4 (wPt[4], tp[4]);  vb = vec4 (wPt[5], tp[5]); }
  } else {
    if (tVu < tp[6])      { va = vec4 (wPt[5], tp[5]);  vb = vec4 (wPt[6], tp[6]); }
    else if (tVu < tp[7]) { va = vec4 (wPt[6], tp[6]);  vb = vec4 (wPt[7], tp[7]); }
    else if (tVu < tp[8]) { va = vec4 (wPt[7], tp[7]);  vb = vec4 (wPt[8], tp[8]); }
    else if (tVu < tp[9]) { va = vec4 (wPt[8], tp[8]);  vb = vec4 (wPt[9], tp[9]); }
    else                  { va = vec4 (wPt[9], tp[9]);  vb = vec4 (wPt[10], tp[10]); }
  }
  ro = mix (va.xyz, vb.xyz, (tVu - va.w) / (vb.w - va.w));
  return ro;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
  uv.x *= iResolution.x / iResolution.y;
  tCur = iTime;
  vec4 mPtr = iMouse;
  mPtr.xy = mPtr.xy / iResolution.xy - 0.5;
  mat3 vuMat;
  vec3 ro, rd, vd, u;
  float zmFac, hLkAt, f;
  sunDir = normalize (vec3 (1., 1., 1.));
  hLkAt = 3.;
  zmFac = 1.4;
  if (mPtr.z <= 0.) {
    ro = 0.5 * (SetVuPt (tCur + 0.2) + SetVuPt (tCur - 0.2));
  } else {
    ro = SetVuPt (mod (150. * (mPtr.x + 0.45), 1000.));
    hLkAt += 5. * mPtr.y;
  }
  vd = normalize (vec3 (0., hLkAt, 0.) - ro);
  u = - vd.y * vd;
  f = 1. / sqrt (1. - vd.y * vd.y);
  vuMat = mat3 (f * vec3 (vd.z, 0., - vd.x), f * vec3 (u.x, 1. + u.y, u.z), vd);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  vec3 col = ShowScene (ro, rd);
  col = sqrt (clamp (col, 0., 1.));
  fragColor = vec4 (col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 l=vec3(.577,-.577,-.577);
	vec3 v = normalize(vec3((2.*fragCoord.xy-iResolution.xy)/iResolution.y, 1.));
    vec3 o = vec3(sin(iTime),
                  cos(iTime),
                  sin(.618*iTime)+2.);


    float d=dot(o,v)*dot(o,v) - dot(v,v)*(dot(o,o) - 1.);

    if(d>0.){
        vec3 p=o + v*(-dot(o,v)+sqrt(d));
        float m=dot(p,l)*.7;
        if(m<0.)m*=-.2;
        m=m*.9+.1;
        float a=dot(v,2.*p*dot(p,l)-l);
        //a*=a*(3.-2.*a);
        //a*=a;
        fragColor=vec4((sin(p*30.)*.1 + .7)*m + pow(abs(a),19.)*.7, 1.);
    }else fragColor=vec4(-vec3(.4*dot(l,v)),1.);
    //fragColor+=vec4(sin(fragCoord.y*234.+fragCoord.x*123.)/255.);
        //fragColor=vec4(.3,.3,.3,1.);
}

// Distance functions playground, by Ruslan Shestopalyuk, 2014/15

#define PI                      3.14159265

#define MTL_BACKGROUND          -1.0
#define MTL_GROUND              1.0
#define MTL_AX                  2.0
#define MTL_AY                  3.0
#define MTL_AZ                  4.0
#define MTL_OBJ1                5.0
#define MTL_OBJ2                6.0

#define NORMAL_EPS              0.001

#define NEAR_CLIP_PLANE         0.001
#define FAR_CLIP_PLANE          1000.0
#define MAX_RAYCAST_STEPS       100
#define STEP_DAMPING            0.9
#define DIST_EPSILON            0.001


#define GLOBAL_LIGHT_COLOR      vec3(0.8,1.0,0.9)
#define SPEC_COLOR              vec3(0.8, 0.90, 0.60)
#define BACKGROUND_COLOR        vec3(0.3, 0.342, 0.5)


#define CAM_DIST                5.0
#define CAM_H                   1.5
#define CAM_FOV_FACTOR          2.5
#define LOOK_AT_H               0.2

#define LOOK_AT                 vec3(0.0, LOOK_AT_H, 0.0)


// Primitives
float plane(vec3 p, vec3 n, float offs) { return dot(p, n) - offs; }
float sphere(vec3 p, float r) { return length(p) - r; }
float cone(in vec3 p, vec2 n) { return dot(vec2(length(p.xz), p.y), n);}
float cone(vec3 p, float r, float h) {
    float ang = atan(r, h);
    vec2 n = vec2(cos(ang), sin(ang));
    float d = cone(vec3(p.x, p.y - h, p.z), n);
    return max(d, -p.y);
}


float cylinder(in vec3 p, float r) { return length(p.xz) - r; }
float cylinder(in vec3 p, float r, float h) { return max(cylinder(p, r), abs(p.y) - h*0.5); }
float torus(vec3 p, float ri, float ro) { return length(vec2(length(p.xz) - ri, p.y)) - ro; }


// Boolean operations
float diff(float d1, float d2) { return max(-d2, d1); }
float add(float d1, float d2) { return min(d2, d1); }
float intersect(float d1, float d2) { return max(d2, d1); }

// Boolean operations (with material ID in second component)
void diff(inout vec2 d1, in vec2 d2) {
    if (-d2.x > d1.x) {
        d1.x = -d2.x;
        d1.y = d2.y;
    }
}
void add(inout vec2 d1, in vec2 d2) { if (d2.x < d1.x) d1 = d2; }
void intersect(inout vec2 d1, in vec2 d2) { if (d1.x < d2.x) d1 = d2; }



// Affine transformations
vec3 translate(vec3 p, vec3 d) {
    return p - d;
}
vec2 rotate(vec2 p, float ang) {
    float c = cos(ang), s = sin(ang);
    return vec2(p.x*c-p.y*s, p.x*s+p.y*c);
}


//  Repetition
float repeat(float coord, float spacing) {
    return mod(coord, spacing) - spacing*0.5;
}

vec2 repeatAng(vec2 p, float n) {
    float ang = 2.0*PI/n;
    float sector = floor(atan(p.x, p.y)/ang + 0.5);
    p = rotate(p, sector*ang);
    return p;
}

vec3 repeatAngS(vec2 p, float n) {
    float ang = 2.0*PI/n;
    float sector = floor(atan(p.x, p.y)/ang + 0.5);
    p = rotate(p, sector*ang);
    return vec3(p.x, p.y, mod(sector, n));
}


// Helper objects
float star(vec3 p) {
    p.xy = (repeatAng(p.xy, 5.0)).xy;
    p.xz = abs(p.xz);
    return plane(p, vec3(0.5, 0.25, 0.8), -0.09);
}

vec2 grid(in vec3 p) {
    p.xz += vec2(0.5, 0.5);
    p.x = repeat(p.x, 1.0);
    p.z = repeat(p.z, 1.0);
    float c = cylinder(p.xzy, 0.003, 1.0);
    c = add(c,  cylinder(p.yxz, .003, 1.0));
    return vec2(c, MTL_GROUND);
}


float arrow(vec3 p) {
    float c = cylinder(p - vec3(0.0, 0.5, 0.0), 0.01, 1.0);
    return add(c, cone(p - vec3(0.0, 1.0, 0.0), 0.03, 0.2));
}


vec2 coordSys(vec3 p) {
    vec2 ax = vec2(arrow(p.yxz), MTL_AX);
    vec2 ay = vec2(arrow(p.xyz), MTL_AY);
    vec2 az = vec2(arrow(p.xzy), MTL_AZ);
    vec2 res = ax;
    add(res, ay);
    add(res, az);
    return res;
}


// Your distance function here
float distf(vec3 p) {
    float scale = 0.5;
    p *= 1.0/scale;
    vec3 p0 = p;
    float d = cylinder(translate(p, vec3(0.0, 1.7, 0.0)), 1.2, 0.1);
    p.xz = repeatAng(p.xz, 5.0).xy;
    d = add(d, sphere(translate(p, vec3(0.0, 0.0, 1.0)), 0.5));
    d = add(d, torus(translate(p.xzy, vec3(0.0, 1.0, 1.0)), 0.5, 0.2));
    d = intersect(d, plane(p0, normalize(vec3(1.0,1.4, 1.0)), 0.8));

    d = add(d, sphere(translate(p, vec3(0.0, -8.0, 0.0)), 8.0));
    return d*scale;
}


vec2 map(vec3 p) {
    vec2 res = grid(p);
    vec2 cs = coordSys(p);
    add(res, cs);

    vec2 obj = vec2(distf(p), MTL_OBJ1);

    add(res, obj);
    return res;
}


vec3 calcNormal(in vec3 p)
{
    vec2 d = vec2(NORMAL_EPS, 0.0);
    return normalize(vec3(
        map(p + d.xyy).x - map(p - d.xyy).x,
        map(p + d.yxy).x - map(p - d.yxy).x,
        map(p + d.yyx).x - map(p - d.yyx).x));
}


vec2 rayMarch(in vec3 ro, in vec3 rd) {
    float t = NEAR_CLIP_PLANE;
    float m = MTL_BACKGROUND;
    for (int i=0; i < MAX_RAYCAST_STEPS; i++) {
        vec2 res = map(ro + rd*t);
        if (res.x < DIST_EPSILON || t>FAR_CLIP_PLANE) break;
        t += res.x*STEP_DAMPING;
        m = res.y;
    }

    if (t > FAR_CLIP_PLANE) m = MTL_BACKGROUND;
    return vec2(t, m);
}


vec3 getMaterialColor(float matID) {
    vec3 col = BACKGROUND_COLOR;
    if (matID <= MTL_GROUND) col = vec3(0.3, 0.3, 0.5);
    else if (matID <= MTL_AX) col = vec3(1.0, 0.0, 0.0);
    else if (matID <= MTL_AY) col = vec3(0.0, 1.0, 0.0);
    else if (matID <= MTL_AZ) col = vec3(0.0, 0.0, 1.0);
    else if (matID <= MTL_OBJ1) col = vec3(0.8, 0.8, 1.8);
    else if (matID <= MTL_OBJ2) col = vec3(1.4, 1.3, 0.3);
    else col = vec3(0.7, 0.7, 1.8);
    return col;
}


vec3 applyFog(vec3 col, float dist) {
    return mix(col, BACKGROUND_COLOR, 1.0 - exp(-0.0015*dist*dist));
}


vec3 render(in vec3 ro, in vec3 rd) {
    vec2 res = rayMarch(ro, rd);
    float t = res.x;
    float mtlID = res.y;
    vec3  lig = -rd;
    vec3 pos = ro + t*rd;
    vec3 nor = calcNormal(pos);
    vec3 mtlColor = getMaterialColor(mtlID);

    float ambient = 0.05;
    float diffuse = clamp(dot(nor, lig), 0.0, 1.0);

    vec3 col = mtlColor*(ambient + GLOBAL_LIGHT_COLOR*diffuse);
    col = applyFog(col, t);
    return vec3(clamp(col, 0.0, 1.0));
}


vec3 getRayDir(vec3 camPos, vec3 viewDir, vec2 pixelPos) {
    vec3 camRight = normalize(cross(viewDir, vec3(0.0, 1.0, 0.0)));
    vec3 camUp = normalize(cross(camRight, viewDir));
    return normalize(pixelPos.x*camRight + pixelPos.y*camUp + CAM_FOV_FACTOR*viewDir);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
    p.x *= iResolution.x/iResolution.y;
    float ang = 0.1*(40.0 + iTime);
    vec3 camPos = vec3(CAM_DIST*cos(ang), CAM_H, CAM_DIST*sin(ang));
    vec3 rayDir = getRayDir(camPos,normalize(LOOK_AT - camPos), p);
    vec3 color = render(camPos, rayDir);
    fragColor = vec4(color, 1.0);
}

vec2 rotate(in vec2 p, in float t)
{
	return p*cos(-t)+vec2(p.y, -p.x)*sin(-t);
}

float sdTorus(in vec3 p, in vec2 t)
{
	vec2 q = vec2(length(p.xz)-t.x, p.y);
	return length(q)-t.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p2 = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.05);
    const float steps = 30.0;
    float pe = 3.0;
	for(float i = 0.0; i < steps; i++)
	{
        float r = i/steps;
        float z = -1.0+2.0*r*r;
        z *= 3.0;
        float s = pe/(pe+z);
        vec3 p3 = vec3(p2*s, z);
    	p3.zx = rotate(p3.zx, iTime*0.521);
    	p3.yz = rotate(p3.yz, iTime*0.632);
        p3.x += 0.45;
        float de = sdTorus(p3,
            vec2(
                0.7*cos(iTime*1.678)+1.2,
           		0.35*(sin(iTime*1.234)+1.0)));
        de = smoothstep(0.08,0.0,de);
        if (de > 0.0 && de < 0.5)
        {
    		col = vec3(r*0.5+0.5, 1.0-r, r*(sin(iTime*1.987)*2.0-1.0))*(1.0-de*2.0);
        }
    }
	fragColor = vec4(col, 1.0);
}

