// Thanks yakoudbz for this idea to minimize the discontinuity!
// Unfortunately there is no good way to remove the discontinuity as it needs to be an _integer_ multiple.
// See this version which does a straight linear blend between N an N+1
// https://www.shadertoy.com/view/XtfGDN
#define MIN_DISCONTUINITY
//    const float PI = 3.14159265358979323846264;
//    float N = 2.+cos(iTime);
//    float d = atan(p.y,p.x) * N/PI;
// To "single step" the rainbow
//     float N = floor(iTime);
//
// Legend:
//   d = angle (was originally in degrees)
//   c = color
//   m = mix
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  p = 2.*fragCoord.xy/iResolution.xy - 1.; // uv coordinates -1 .. +1
    float d = atan(p.y,p.x) + iTime; // angle
#ifdef MIN_DISCONTUINITY
    const float PI = 3.14159265358979323846264;
    float N = 2.+cos(iTime);
    d -= iTime;
    d *= N/PI; // Technically should be N/(2.0*PI), but N/PI looks better with twice the rings
#endif
    vec3  c = hsv2rgb( vec3(d,1.0,1.0) ); // hsv
    vec3  m = vec3(c.r,c.g,c.b);
    fragColor = vec4(m, 1.0 );
}

