// comment to supress isolines | shade | color
#define ISOLINES
#define SHADE
#define COLORS

// change it as you want
#define powermax 2.0
#define powermin 1.0

// isoline interval and width
#define interval 0.3
#define linewidth 0.1

#define pi 3.14159265359

vec2 cartToPol(vec2 z){
	return vec2(length(z),atan(z.y,z.x));
}

vec2 polToCart(vec2 p){
	return vec2(p.r*cos(p.g),p.r*sin(p.g));
}

vec3 colorize(vec2 p){
   	// give a color for each number p
    #ifdef COLORS
    float A = mod(p.g/pi,2.0)-1.0; // angle from -1 to 1
    float AA = abs(A + 1./3.) - 1.0;
    float AAA = 1.0 - abs(A - 1./3.);
    vec3 color = vec3(1.0 - abs(A), 1.0 - abs(AA),1.0 - abs(AAA));
    color/=length(color);
    #else
    vec3 color=vec3(0);
    #endif

    #ifdef SHADE
    color *= exp(-p.r)*1.2;
    #endif

    // ==== iso-line ===
    #ifdef ISOLINES
    vec2 z = polToCart(p);
    //float isog = smoothstep(interval-linewidth,interval+linewidth,abs(mod(A,2.0*interval)-interval));
    float isor = smoothstep(linewidth,-0.03,abs(mod(p.r,interval)*2.0/interval - 1.0));
    float isox = smoothstep(linewidth,-0.03,abs(mod(z.x,interval)*2.0/interval - 1.0));
    float isoy = smoothstep(linewidth,-0.03,abs(mod(z.y,interval)*2.0/interval - 1.0));

    return color + isox + isoy + isor;
    #else
    return color;
    #endif
}

vec2 powz(vec2 p , float power){
    return vec2(pow(p.r,power),power*mod(p.g,2.*pi));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 Z = ( 2.0*fragCoord.xy / iResolution.xy ) - 1.0;
	vec2 P = cartToPol(Z);

    vec2 p = powz(P,cos(iTime)*0.5*(powermax-powermin)+0.5*(powermin+powermax));

    fragColor = vec4(colorize(p), 1.0 );

}

/*	kalizyl 

	(c) 2015, stefan berke (aGPL3)

	Another attempt on the kali set
	http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/

	Looks cool and frametime is not too bad -
	but still uses precission 0.1 for raymarching. (but see EIFFIE_MOD)
	Maybe saves some speed by not coloring the surface.

*/

// plot a 2d slice of the distance function
#define PLOT_2D			0
// enable eiffie's modification (https://www.shadertoy.com/view/XtlGRj)
#define EIFFIE_MOD		0


#define KALI_PARAM 		vec3(0.71)
#define LIGHT_COL 		vec3(1.0, 0.8, 0.4)

#if EIFFIE_MOD == 0
    #define FOG_DIST 		5.
    #define FOG_COL 		vec3(0.5, 0.7, 1.0)
    #define CYL_RADIUS 		0.07
    #define NUM_TRACE 		80
    #define MARCH_PRECISION	0.1
#else
    #define FOG_DIST 		2.
    #define FOG_COL 		(vec3(0.5, 0.7, 1.0)/3.)
    #define CYL_RADIUS 		0.009
    #define NUM_TRACE 		40
    #define MARCH_PRECISION	0.9
#endif

#if EIFFIE_MOD == 0
// standard kali set 
// modified to return distance to cylinders and spheres in 'kali-space'
vec2 scene_dist(in vec3 p)
{
	vec2 d = vec2(100.);
	for (int i=0; i<4; ++i)
	{
		p = abs(p) / dot(p, p) - KALI_PARAM;
        // distance to cylinder
		d.x = min(d.x, length(p.xz));	
        if (i < 3)
        {
            vec3 lightpos = vec3(0., 1.+sin(iTime+p.y+float(i)*1.3), 0.);
            // distance to sphere
        	d.y = min(d.y, length(p - lightpos));	
        }
	}
	return d - CYL_RADIUS;
}

#else

// eiffie's mod
vec2 scene_dist(in vec3 pos)
{
    // p.w will track how much we have stretched space
    vec4 p = vec4(pos, 1.);
	vec2 d = vec2(100.);
	for (int i=0; i<4; ++i)
	{
		p = abs(p) / dot(p.xyz, p.xyz) - vec4(KALI_PARAM, 0.);
		d.x = min(d.x, length(p.xz)/p.w); //now we are calcing unstretched distance
        if (i < 3)
        {
            vec3 lightpos = vec3(0., 1.+sin(iTime+p.y+float(i)*1.3), 0.);
            d.y = min(d.y, length(p.xyz - lightpos)/p.w);
        }
	}
	return d - CYL_RADIUS;
}

#endif

vec3 traceRay(in vec3 pos, in vec3 dir)
{
	vec3 p = pos;

	float t = 0., mlightd = 100.;

    vec2 d = scene_dist(pos);

	for (int i=0; i<NUM_TRACE; ++i)
	{	
		if (d.x < 0.001 || t >= FOG_DIST) 
			continue;

		p = pos + t * dir;
		vec2 d = scene_dist(p);

        // collect minimum distance to light
		mlightd = min(mlightd, d.y);

		t += d.x * MARCH_PRECISION;		
	}

    // only fog contribution
	vec3 col = FOG_COL * min(1., t/FOG_DIST);

    // plus light glow
    col += LIGHT_COL / (1. + 50.*max(0., mlightd));

	return col;
}

vec3 plot2d(in vec3 pos)
{
	vec2 d = scene_dist(pos);
    // inside?
    float ins = smoothstep(0.01,-0.01, d.x);
    vec3 col = vec3(d.x, ins, 0.);

    return col;
}

// camera path
vec3 path(float ti)
{
	float a = ti * 3.14159265 * 2.;

	return vec3(
				1.1 * sin(a),
				0.52 * sin(a*2.),
				1.1 * cos(a) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
	uv.x *= iResolution.x / iResolution.y;

    vec3 pos, dir;
    mat3 dirm = mat3(vec3(1.,0.,0.), vec3(0.,1.,0.), vec3(0.,0.,1.));

    if (iMouse.z < 0.5)
    {
        // camera time
        float ti = iTime / 19.;

        pos = path(ti);

        // camera orientation matrix
        vec3 look;

        // how much to look towards the center [0,1]
        float lookcenter = 0.45 + 0.45 * sin(ti*7.);
    	look = normalize(path(ti + 0.1) - pos);
		look = look + lookcenter * (normalize(-pos) - look);
        vec3 up = normalize(cross(vec3(0., 1., 0.), look));
        vec3 right = normalize(cross(look, up));
        //look = normalize(cross(up, right));
        dirm = mat3(right, up, look);

        dir = dirm * normalize(vec3(uv, 1.5));
    }
    else
    {
        vec2 m = iMouse.xy / iResolution.xy;
        pos = vec3(m.x*2.-1., 0., 3. * m.y);
        dir = normalize(vec3(uv, -1.));
    }
#if PLOT_2D == 0
	fragColor = vec4( traceRay(pos, dir), 1.);	
#else
    fragColor = vec4( plot2d(pos + dirm * vec3(uv,0.)), 1.);	
#endif
}

void mainImage( out vec4 f, vec2 p ){

    vec3 q=iResolution,d=vec3(p-.5*q.xy,q.y)/q.y,c=vec3(0,.5,.7);

    q=d/(.1-d.y);
    float a=iTime, k=sin(.2*a), w = q.x *= q.x-=.05*k*k*k*q.z*q.z;

    f.xyz=d.y>.04?c:
    	sin(4.*q.z+40.*a)>0.?
        w>2.?c.xyx:w>1.2?d.zzz:c.yyy:
	    w>2.?c.xzx:w>1.2?c.yxx*2.:(w>.004?c:d).zzz;

}

//edit made it less liney and more noisey by doing a has twice, the sin is only there to animate zoom

float hash(vec2 uv)
{
    float r;
    uv = abs(mod(10.*fract((uv+1.1312)*31.),uv+2.));
    uv = abs(mod(uv.x*fract((uv+1.721711)*17.),uv));
    return r = fract(10.* (7.*uv.y + 31.*uv.x));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = .5*(abs(sin(iTime/2.))+.5)*(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    uv.x = hash(vec2(hash(uv),1.0));

    fragColor = vec4(uv.x,uv.x,uv.x,1.);
}

void mainImage( out vec4 c, in vec2 w )
{
	vec2 p = w/iResolution.xy, a = p*5.; a.y -= iTime;
	vec2 f = fract(a); a -= f; f = f*f*(3.-2.*f);
    vec4 r = fract(sin(vec4(a.x + a.y*1e3) + vec4(0, 1, 1e3, 1001)) * 1e5)*30./p.y;
	c.rgb = p.y + vec3(1,.5,.2) * clamp(mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y)-30., -.2, 1.);
}

