//Hsv conversion from http://ploobs.com.br/?p=1499
vec3 Hue(float a)
{
	vec3 K = vec3(3,2,1)/3.;
	return clamp(abs(fract(vec3(a)+K)*6. - vec3(3.)) - K.xxx, 0., 1.);
}

vec3 HSVtoRGB(vec3 HSV)
{
    return vec3(((Hue(HSV.x) - 1.0) * HSV.y + 1.0) * HSV.z);
}

// Shader Begin

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized coordinates (-1 to 1 vertically)
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;

    // --- FROM IQ ----
    // modified distance metric. Usually distance = (xÂ² + yÂ²)^(1/2). By replacing all the "2" numbers
    // by 32 in that formula we can create distance metrics other than the euclidean. The higher the
    // exponent, then more square the metric becomes. More information here:    
    // http://en.wikipedia.org/wiki/Minkowski_distance

    //Makes everything a little trippy
    float crayStationsCoef = .01;     

    p.x +=  sin(pow(iTime,.5))*crayStationsCoef*10.0;    
    //p.y +=  cos(iTime)*crayStationsCoef*10.0;
    // angle of each pixel to the center of the screen
    float a = atan(p.y,p.x) + iTime * sin(iTime)*.002 + iMouse.x * .0001;


    float exp = 1.; // makes this a box if you see it fit
    //Mink.. Calculation 
    float r = pow( pow(p.x*p.x,exp) + pow(p.y*p.y,exp), 1.0/(exp * 2.0) ) + sin(iTime)*crayStationsCoef;
    a += pow(r,.5); 

    // index texture by angle and radious, and animate along radius    
    vec2 uv = vec2(0.5/r + 0.5*iTime + iMouse.y,                    a/3.1416 );

    // Lighting
    r -= sin(iTime)*.05;

    // fetch color and darken in the center
    vec3 col =  texture( iChannel0, uv ).xyz * r;

    //Lerp in Cray Mode
    col = (crayStationsCoef) * HSVtoRGB(vec3(col.r, 1.0, 1.0)) + col *(1.-crayStationsCoef); 	
    col *= (sin(uv.x*5.0)*1.0 + 1.0) * .7;

    fragColor = vec4(col, 1.0);
}

vec2 rotate(in vec2 p, in float t)
{
	return p*cos(-t)+vec2(p.y, -p.x)*sin(-t);
}

float sdTorus(in vec3 p, in vec2 t)
{
	vec2 q = vec2(length(p.xz)-t.x, p.y);
	return length(q)-t.y;
}

float map(in vec3 p)
{
    return sdTorus(p, vec2(0.8,0.3));
}

vec3 calcNormal(in vec3 p)
{
	const vec2 e = vec2( 0.0001, 0.0 );
	vec3 n = vec3(
		map( p + e.xyy ) - map( p - e.xyy ),
		map( p + e.yxy ) - map( p - e.yxy ),
		map( p + e.yyx ) - map( p - e.yyx ) );
	return normalize( n );    
}

vec3 transform(in vec3 p)
{
    p.zx = rotate(p.zx, iTime*0.5);
    p.yz = rotate(p.yz, iTime*0.6);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p2 = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.3+0.1*p2.y);
    vec3 light = normalize(vec3(0.5, 0.8, 3.0));
    const float steps = 64.0;
    float pe = 3.0;
	for(float i=0.0; i<steps; i++)
	{
        float z = 1.0-2.0*pow(i/steps, 2.0);
        float s = pe/(pe+z);
        vec3 p3 = vec3(p2*s, z);
        p3 = transform(p3);
        if (map(p3) < 0.001)
        {
			float dif = dot(calcNormal(p3), transform(light));
        	dif = clamp((dif+0.5)*0.7, 0.3, 1.0);
			col =  texture(iChannel0, p3.xy).rgb*dif;
            break;
        }
    }
	fragColor = vec4(col, 1.0);
}

precision highp float;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1. + 2. * fragCoord.xy / iResolution.xy;
    vec2 offset = vec2(.3, .3);

    float radius = sin(iTime + sqrt(dot(p, p)));
    float radius2 = sin(iTime + sqrt(dot(p - offset, p - offset)));
    float radius3 = sin(iTime + sqrt(dot(p + offset, p + offset)));

    bool toggle1 = mod(radius, .1) > .01;
    bool toggle2 = mod(radius2, .1) > .01;
    bool toggle3 = mod(radius3, .1) > .01;

    //float col = toggle || toggle2 || toggle3 ? 1. : 0.0;
    float col = 0.0;
    if (toggle1) col = 1.0; 
    if (toggle2) col = 1.0;
    if (toggle3) col = 1.0;
    if ((toggle1) && (toggle2) && (toggle3)) col = 0.0;

//    fragColor = vec4(toggle, toggle2, toggle3, 1.); 
    fragColor = vec4(col,col,col,1.0);
}

