// Inspired from Adrian's XOR Demoeffect in WebGL
// http://adrianboeing.blogspot.com/2011/01/xor-demoeffect-in-webgl.html

void mainImage( out vec4 f, in vec2 w )
{
    vec2  p = 2.*w/iResolution.xy-1.,
          v = vec2(sin(iTime*.5),cos(iTime*.5));
    bool  a = mod(sqrt(dot(p + v.x,p + v.y)),.1)>.05,
          b = mod(sqrt(dot(p - v  ,p - v.y)),.1)>.05; // intentional v.x -> v in first argument for anti-symmetry!
    float t = 
        a ? b ? .1
              : .6
          : b ? .8
              : dot(p,p);
    f = vec4(1.-t,1.-t*t,1.-t*t*t,1.);
}

mat2 rotate(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));	
}

// https://www.shadertoy.com/view/MtsGWH
vec4 boxmap( sampler2D sam, in vec3 p, in vec3 n, in float k )
{
    vec3 m = pow( abs(n), vec3(k) );
	vec4 x = texture( sam, p.yz );
	vec4 y = texture( sam, p.zx );
	vec4 z = texture( sam, p.xy );
	return (x*m.x + y*m.y + z*m.z)/(m.x+m.y+m.z);
}

float sdSphere(in vec3 p, in float r)
{   
    return (length(p)- r);
}

float udBox(vec3 p, vec3 b)
{
  return length(max(abs(p) - b,0.0));
}

float map1(in vec3 p)
{   
    p.y +=   0.3 * smoothstep( 0.2, 0.0, length(abs(p.xz) - vec2(0.4, 0.4))) * (1.0 - step(0.0, p.y));
    p.y +=  -0.2 * smoothstep(0.15, 0.0, length(abs(p.xz - vec2(0.4,0.0)) - vec2(0.0, 0.4))) * step(0.0, p.y);
    p.x += -0.15 * smoothstep( 0.4, 0.3, length(p.yz)) * step(0.0, p.x);
    p.x +=  0.15 * smoothstep( 0.1, 0.0, length(abs(p.yz) - vec2(0.0, 0.1))) * step(0.0, p.x);
    p.x +=   0.1 * smoothstep( 0.1, 0.0, length(abs(p.yz - vec2(0.35,0.0)) - vec2(0.0, 0.3))) * step(0.0, p.x);
    p.x +=  0.15 * smoothstep( 0.1, 0.0, length(p.yz - vec2(0.35,0.0))) * (1.0 - step(0.0, p.x));
    return 0.6 * sdSphere(p, 1.0);
}

float map2(in vec3 p)
{   
    float a;
    a = atan(p.y, p.x);
    p.xy += -0.1 * smoothstep(0.2, 0.0, abs(p.z)) * vec2(cos(a), sin(a));
    a = atan(p.z, p.y);
	p.yz += -0.1 * smoothstep(0.4, 0.2, abs(p.x)) * vec2(cos(a), sin(a));
    a = atan(p.x, p.z);
    p.zx += 0.1 * smoothstep(0.3, 0.1, abs(p.y)) * vec2(cos(a), sin(a));
    return 0.5 * udBox(p, vec3(0.7));
}

float map3(in vec3 p)
{   
    float a = atan(p.y, p.x);
    p.xy += -0.1 * smoothstep(0.1, 0.0, abs(mod(p.z, 0.3) - 0.15)) * vec2(cos(a), sin(a));
    return 0.6 * sdSphere(p, 1.0);
}

float de1(in vec2 p)
{
	return length(normalize(p) * 0.5 -p);	
}

float de2(in vec2 p)
{
	return abs(abs(p.x) + abs(p.y) - 0.5);
}

float sdSegment(in vec2 p, in vec2 a, in vec2 b)
{
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
	return length(pa - ba * h);
}

float de3(in vec2 p)
{
	return min(sdSegment(p, vec2(0.5, 0.5), vec2(-0.5, -0.5)),
               sdSegment(p, vec2(-0.5, 0.5), vec2(0.5, -0.5)));
}

float map4(in vec3 p)
{   
    if (abs(p.x) > 0.6999) p.x += -0.15 * sign(p.x) * smoothstep(0.2, 0.0, de1(p.yz));
    if (abs(p.y) > 0.6999) p.y += -0.15 * sign(p.y) * smoothstep(0.2, 0.0, de2(p.xz));
    if (abs(p.z) > 0.6999) p.z += -0.15 * sign(p.z) * smoothstep(0.2, 0.0, de3(p.xy));
    return 0.4 * udBox(p, vec3(0.7));
}

float map(in vec3 p)
{   
    float t = mod(iTime * 0.5, 12.0);
    if (t < 3.0)  return mix(map1(p), map2(p), smoothstep( 1.0,  2.0, t));
    if (t < 6.0)  return mix(map2(p), map3(p), smoothstep( 4.0,  5.0, t));
    if (t < 9.0)  return mix(map3(p), map4(p), smoothstep( 7.0,  8.0, t));
    if (t < 12.0) return mix(map4(p), map1(p), smoothstep(10.0, 11.0, t));
    return 1.0;
}

vec3 calcNormal(in vec3 p)
{
	const vec2 e = vec2(0.0001, 0.0);
	return normalize(vec3(
		map(p + e.xyy) - map(p - e.xyy),
		map(p + e.yxy) - map(p - e.yxy),
		map(p + e.yyx) - map(p - e.yyx)));
}

float march(in vec3 ro, in vec3 rd)
{
	const float maxd = 50.0;
	const float precis = 0.001;
    float h = precis * 2.0;
    float t = 0.0;
	float res = -1.0;
    for(int i = 0; i < 64; i++)
    {
        if(h < precis || t > maxd) break;
	    h = map(ro + rd * t);
        t += h;
    }
    if(t < maxd) res = t;
    return res;
}

vec3 transform(in vec3 p)
{
    p.yz *= rotate(iTime * 0.5);
    p.zx *= rotate(iTime * 0.7);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 col = vec3(0.1 + p.y * 0.15);
   	vec3 rd = normalize(vec3(p, -1.8));
	vec3 ro = vec3(0.0, 0.0, 3.0);
    vec3 li = normalize(vec3(0.5, 0.8, 3.0));
    ro = transform(ro);
	rd = transform(rd);
	li = transform(li);
    float t = march(ro, rd);
    if(t > -0.001)
    {
        vec3 pos = ro + t * rd;
        vec3 n = calcNormal(pos);
		float dif = clamp((dot(n, li) + 0.5) * 0.7, 0.3, 1.0);
        //col = texture(iChannel0, pos.xz).rgb * dif * dep;
        col = boxmap( iChannel0, pos * 2.0, n, 32.0 ).xyz * dif;
        col = pow(col, vec3(0.8));
	}
   	fragColor = vec4(col, 1.0);
}

//254 chars with Fabrice's help

void mainImage( out vec4 f, vec2 p ) {
    p /= iResolution.y;
    float
        t=iTime,
        s=sin(t),
        o = p.x/(.15+s/8.) - 5.*t,
        u = (p.y-.5)/(.25+sin(3.*p.x+s)/16.),
        a = asin( u ),
        c = cos( a + o ),
        b = - cos( a - o );

    s = c>.17 ? 1.+log(c)*.5 : 0.;

    f = vec4( s*.95, (1.-s)*(b>.1 ? .5 + log(b)/4. : 0.), 0, 0 ) * cos( 1.35*u );
}


