vec3 JuliaFractal(vec2 c, vec2 c2, float animparam, float anim2 ) {	
	vec2 z = c;

	float ci = 0.0;
	float mean = 0.0;

	for(int i = 0;i < 64; i++)
    {
		vec2 a = vec2(z.x,abs(z.y));

        float b = atan(a.y*(0.99+animparam*9.0), a.x+.110765432+animparam);

        if(b > 0.0) b -= 6.303431307+(animparam*3.1513);

        z = vec2(log(length(a*(0.98899-(animparam*2.70*anim2)))),b) + c2;

        if (i>0) mean+=length(z/a*b);

        mean+=a.x-(b*77.0/length(a*b));

        mean = clamp(mean, 111.0, 99999.0);
	}

	mean/=131.21;
	ci =  1.0 - fract(log2(.5*log2(mean/(0.57891895-abs(animparam*141.0)))));

	return vec3( .5+.5*cos(6.*ci+0.0),.5+.75*cos(6.*ci + 0.14),.5+.5*cos(6.*ci +0.7) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float animWings = 0.004 * cos(iTime*0.5);
    float animFlap = 0.011 * sin(iTime*1.0);    
    float timeVal = 56.48-20.1601;
	vec2 uv = fragCoord.xy - iResolution.xy*.5;
	uv /= iResolution.x*1.5113*abs(sin(timeVal));
    uv.y -= animWings*5.0; 
	vec2 tuv = uv*125.0;
	float rot=3.141592654*0.5;

	uv.x = tuv.x*cos(rot)-tuv.y*sin(rot);
	uv.y =1.05* tuv.x*sin(rot)+tuv.y*cos(rot);
	float juliax = tan(timeVal) * 0.011 + 0.02/(fragCoord.y*0.19531*(1.0-animFlap));
	float juliay = cos(timeVal * 0.213) * (0.022+animFlap) + 5.66752-(juliax*1.5101);//+(fragCoord.y*0.0001);// or 5.7


    float tapU = (1.0/ float(iResolution.x))*25.5;//*cos(animFlap);
    float tapV = (1.0/ float(iResolution.y))*25.5;//*cos(animFlap);


	fragColor = vec4( JuliaFractal(uv+vec2(0.0,0.0), vec2(juliax, juliay), animWings, animFlap ) ,1.0);

    fragColor += vec4( JuliaFractal(uv+vec2(tapU,tapV), vec2(juliax, juliay), animWings, animFlap ) ,1.0);
//    fragColor += vec4( JuliaFractal(uv+vec2(tapU,-tapV), vec2(juliax, juliay), animWings, animFlap ) ,1.0);
//    fragColor += vec4( JuliaFractal(uv+vec2(-tapU,tapV), vec2(juliax, juliay), animWings, animFlap ) ,1.0);
    fragColor += vec4( JuliaFractal(uv+vec2(-tapU,-tapV), vec2(juliax, juliay), animWings, animFlap ) ,1.0);  
    fragColor *= 0.3333;

    fragColor.xyz = fragColor.zyx;
	fragColor.xyz = vec3(1)-fragColor.xyz;

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float zoom = iTime * 0.05 + 1.0;
    zoom = pow(2.0, pow(zoom, zoom) - 1.0) - 1.0;
    vec2 uv = (fragCoord.xy / iResolution.xy) / vec2(zoom, zoom);
    fragColor = vec4(
        mod(1.0 / uv.y, uv.x) / uv.x,
        mod(4.0 / uv.y, uv.x) / uv.x,
        mod(16.0 / uv.y, uv.x) / uv.x,
        1.0);
}

void mainImage(out vec4 f, vec2 u )
{
	u /= iResolution.xy;
    f.xy = .5 - u;

    float t = iTime,
          z = atan(f.y,f.x) * 3.,
          v = cos(z + sin(t * .1)) + .5 + sin(u.x * 10. + t * 1.3) * .4;

    f.x = 1.2 + cos(z - t*.2) + sin(u.y*10.+t*1.5)*.5;
	f.yz = vec2( sin(v*4.)*.25, sin(v*2.)*.3 ) + f.x*.5;
}

#define FOV_SCALE 1.
#define EPSILON .008
const vec3 ne = vec3(.0004435,0.,0.);
const vec3 repeat = vec3(20.,0.,20.);
const vec3 repeat2 = repeat/2.;

float df(in vec3 p) {
    vec3 ap = abs(p);
    vec3 r = mod(ap,repeat)-repeat2;
    float d = min(1.+p.y,
                  length(max(abs(r)-vec3(2.,40.,2.),0.)));

    float dp = cos(ap.x+ap.z)*.5+.5;
    ap.y += dp;
    d = max(-(length(mod(ap,vec3(5.))-vec3(2.5))-2.), d);

    return d;
}
vec3 nrm(in vec3 p) {
    return normalize(vec3(df(p+ne)-df(p-ne),
                     df(p+ne.yxz)-df(p-ne.yxz),
                     df(p+ne.yzx)-df(p-ne.yzx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);
    uv.y *= iResolution.y/iResolution.x;

    vec3 ld = normalize(vec3(500.,100.,200.));
    float ct = iTime*10.;
    vec3 rp = vec3(0.,20.,ct);
    vec3 rd = normalize(vec3(0.,-2.,10.));
    vec3 srd = rd;
    vec3 rr = cross(vec3(0.,1.,0.),rd);
    rd = normalize(rd*FOV_SCALE + uv.x*rr + uv.y*cross(rd,rr));
    //rr = vec3(0.,0.,0.)*.001;

    float d,s = 0.;
    for (int i = 0; i < 256; i++) {
        d = df(rp);
        if (d < EPSILON) break;

        rp += d*rd;
        s += d*.02;
        //rd = normalize(rd+d*rr);
        rr = cross(normalize(vec3(sin(s),cos(s),sin(s))),rd);
    	rd = normalize(srd*FOV_SCALE + uv.x*rr + uv.y*cross(rd,rr));
    }

    vec3 c;
    if (d < EPSILON) {
        vec3 n = nrm(rp);
        c = texture(iChannel0,(rp.xy+rp.zx*.5)*.1).xyz;

        rd = n;
        rp -= rd*d;
        for (int i = 0; i < 4; i++) {
            d = df(rp);
            if (d < EPSILON) break;
        }

        c = .14*c +
            max(0.,dot(ld,n))*c;
    } else {
        c = vec3(.9);
    }

    fragColor = vec4(pow(c*2.0,vec3(1.2,1.2,1.3)),1.);
}

#define EPSILON 0.001
#define MAX_DIST 30.

vec3 lightPos = vec3(0, 0., 0.);

struct HitInfo {
    float d;
    vec3 c;
    vec3 p;
};

float sphere(vec3 pos, float r) {
    return length(pos) - r;
}

float box(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
  	return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

HitInfo map(vec3 pos, vec3 rd) {
    HitInfo hit = HitInfo(9999., vec3(0., 0., 0.), pos);
    float nd;

    nd = sphere(vec3(pos.xz, pos.y/(sin(iTime)+1.5)).xzy, 1.);
    if(nd < hit.d) hit = HitInfo(nd, vec3(1., 0., 0.), pos);
    nd = box(pos - vec3(0., -3, 0.), vec3(1000., 0.5, 1000.));
    if(nd < hit.d) hit = HitInfo(nd, texture(iChannel0, pos.xz/10.).rgb, pos);

    return hit;
}
#define AAA 0.1
mat2 getRotMat(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

HitInfo march(in vec3 ro, in vec3 rd, bool right) {
    if(right) {
        ro.xz *= getRotMat(AAA);
    } else {
        ro.xz *= getRotMat(-AAA);
    }
    HitInfo ret = HitInfo(1000., vec3(0.), ro);
    bool didHit = false;
    float l = 0.;
    for(int i = 0; i < 100; i++) {
        vec3 r = ro + rd * l;
        HitInfo hit = map(r, rd);
        if(hit.d <= EPSILON) {
            ret = HitInfo(l, hit.c, r);
            didHit = true;
            break;
        }
        if(hit.d > MAX_DIST) {
            break;
        }
        l += hit.d;
    }

    if(!didHit) return ret;

    ro = ro + rd * l;
    rd = normalize(lightPos - ro);
    l = 0.1;
    for(int i = 0; i < 50; i++) {
        vec3 r = ro + rd * l;
        HitInfo hit = map(r, rd);
        if(hit.d <= EPSILON) {
            ret.c*=0.5;
            return ret;
        }
        if(distance(r, lightPos) < hit.d) {
            return ret;
        }
        l += min(hit.d, distance(r, lightPos));
    }

    return ret;
}

vec3 pixel(vec2 uv) {
    vec3 ro = vec3(0., 0., -6.);
    vec3 rd = normalize(vec3(uv, 1.));
    HitInfo hit = march(ro, rd, uv.x > 0.);
    float i = smoothstep(20., 0., distance(hit.p, lightPos));
    return hit.c * i;
}

