    objects[0] = getObject(vec3(0.0), TYPE_CUBE);
    objects[0].size = vec3(0.1, 0.1, 0.1);

    objects[0].rot *= rotx(iTime * 1.5);
    objects[0].rot *= roty(sin(iTime * 1.5));

    vec3 camPos = vec3(0.0, 0.0, 1.0);    
    vec3 lk = vec3(-toClipSpace(uv.x), -toClipSpace(uv.y), -2.0);
    vec3 mclip = vec3(0.0);
    vec3 dir = lookAt(camPos, mclip, normalize(lk));
    vec3 hit = vec3(0.0);
    vec4 color = vec4(0.0);

    float m;
    bool h = trace(camPos, dir, hit, m);
    vec4 aColor = vec4(1.0);

    float p =  pow(length(uv - vec2(0.5, 0.5)), 3.0);
    aColor.r = 0.5 + (sin((iTime + 100.0) * 0.25) * 0.5);
    aColor.g = 0.5 + (cos((iTime + 300.0) * 0.15) * 0.5);

    if(h)
    {
        vec2 offset = vec2(0.001, 0.0);
        vec3 grad = normalize(vec3(dist(hit + offset.xyy) - dist(hit - offset.xyy), 
                                   dist(hit + offset.yxy) - dist(hit - offset.yxy),
                                   dist(hit + offset.yyx) - dist(hit - offset.yyx)));

        float d = clamp(dot(grad, vec3(0.0, 0.0, 1.0)), 0.0, 1.0);
        float rim = (1.0 - d) * 1.4;        
        color += aColor * pow(rim, 2.0);

    }
    else
    {
        color = aColor * pow(length(uv - vec2(0.5, 0.5)) * (1.0 + p * audio.x * 5.0), 1.4) * 1.5;
    }

    float d = (1.0 - m * 75.0) * 15.0 * p;
    color += aColor * pow(clamp(d, 0.0, 0.0), 1.2);

    fragColor = color;
}

float noise3D(vec3 p)
{
    p.z = fract(p.z)*256.0;
    float iz = floor(p.z);
    float fz = fract(p.z);
    vec2 a_off = vec2(23.0, 29.0)*(iz)/256.0;
    vec2 b_off = vec2(23.0, 29.0)*(iz+1.0)/256.0;
    float a = texture(iChannel0, p.xy + a_off, -999.0).r;
    float b = texture(iChannel0, p.xy + b_off, -999.0).r;
    return mix(a, b, fz) - 0.5;
}

float perlin(vec3 p)
{
    float v = 0.0;
    for (float i = 0.0; i < 6.0; i += 1.0)
        v += noise3D(p * pow(2.0, i)) * pow(0.35, i);
    return v;
}

float density(vec3 p)
{
    float n = perlin(p * 0.04);
    //n *= abs(n);
    n = sqrt(n+1.0) * 0.2 * (1.0+sin(iTime));
    //n = n * 0.1;
    float l = length(p);
    return n + l*l*0.7;
}

float thresh = 1.0;

bool trace(vec3 start, vec3 ray, out vec3 hit, out vec3 norm)
{
    bool hashit = false;
    const float steps = 64.0;
    vec3 p = start;
    vec3 s = ray/steps;
    for (float i = 0.0; i < steps; i += 1.0)
    {
        p += s;
        if (density(p) > thresh)
        {
            hashit = true;
            break;
        }
    }
    if (!hashit)
        return false;
    for (int i = 0; i < 10; ++i)
    {
        s *= 0.5;
        if (density(p) > thresh)
            p -= s;
       	else
            p += s;
    }
    hit = p;
    const float e = 0.005;
    float hit_density = density(hit);
    norm = -normalize(vec3(density(p+vec3(e,0,0)), density(p+vec3(0,e,0)), density(p+vec3(0,0,e))) - hit_density);
    return true;
}

vec3 getCol(vec3 start, vec3 dir, out vec3 hit, out vec3 norm, out vec3 ref)
{
    trace(start, dir, hit, norm);
    vec3 eye = -normalize(dir);
    vec3 light = -normalize(hit);
    ref = reflect(-eye, norm);
    float dist = length(hit-start);
    float fog = exp(-dist);
    float diffuse = max(0.0, dot(norm, light));
    float specular = pow(max(0.0, dot(ref, light)), 100.0);
    vec3 p = hit * 0.1;
    vec3 col = normalize(vec3(noise3D(p), noise3D(p+1.234), noise3D(p+3.543))*0.2+0.5);
    //vec3 col = vec3(1.0);
    return vec3(col * (diffuse*0.6+0.4)+specular*0.3);
}

//http://code.google.com/p/qshaderedit/source/browse/qshaderedit/shaders/sancho/minnaertward.glsl?spec=svn165&r=165
float fresnel(vec3 i, vec3 n)
{
    float idotn = dot(-i, n);
    if (idotn < 0.0)
        return 0.0;
    const float ior = 1.3;
	float kr = (ior-1.0)/(ior+1.0);
	kr *= kr;
	return kr + (1.0-kr)*pow( 1.0 - idotn, 5.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float fog_density = 0.3;

	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.z*iResolution.x/iResolution.y;
    vec3 start = vec3(0,-0.8,-0.5);
    vec3 dir = 2.5 * normalize(vec3((uv*2.0-1.0) * vec2(aspect,1.0) * 6.0, 10.0));
 	//dir = dir.xzy;

    vec3 hit, norm, ref;
    vec3 first = getCol(start, dir, hit, norm, ref);
    vec3 qwe1, qwe2;
    bool shadow = trace(hit, -hit, qwe1, qwe2);
    float fog = exp(-length(hit-start)*fog_density);
    float f = clamp(fresnel(normalize(dir), norm)*2.0, 0.0, 1.0);
    vec3 second = getCol(hit, ref*3.0, hit, norm, ref);
    float fog2 = exp(-length(hit-start)*fog_density);
    vec3 col = fog * mix(first * (shadow?0.8:1.0), fog2 * second, f);

    fragColor = vec4(col, 1.0);
    //fragColor = vec4(vec3(), 1.0);
}

#define ITERATIONS 64
#define EPSILON 0.01
#define FAR 1.0e6
#define PI 3.1415926535897932384626

float hTorus(in vec4 pos, in float r1, in float r2)
{
    return length(vec3(length(pos.xz)-r1,pos.y, pos.w))-r2;
}

float tesseract(in vec4 p, in float s)
{
    vec4 d = abs(p) - 0.5*s;
  return min(max(max(d.x, d.y),max(d.z,d.w)),0.0) +
         length(max(d,0.0));
}

// Should loook like a sphere
float hCylinder(in vec4 pos, float r)
{
	return length(pos.xyz)-r;   
}

float hSphere(in vec4 pos, float r)
{
    return length(pos)-r;
}

vec4 repetition(in vec4 p, in vec4 c )
{
   return mod(p-0.5*c,c)-0.5*c;
}



vec2 map(in vec4 pos)
{
    vec4 localPos = repetition(pos, vec4(4, 2, 5, 4));

    float distance = FAR;
    float distortedDistance = FAR;

    distance = min(distance, tesseract(localPos, 1.0));
    distance = max(distance, -hTorus(localPos, 0.5, 0.5));

    distance = min(distance, hCylinder(localPos, 0.05));
    distance = min(distance, hSphere(localPos-vec4(vec3(0.0), 1.0), 0.2));



    vec4 spos = 16.0*pos;
    distance += 0.01*(sin(spos.x)+sin(spos.y)+sin(spos.z)+sin(spos.w));
    distortedDistance = distance-0.01;

    distance = min(distance, pos.y+1.0);

    return vec2(distance, min(distortedDistance, distance));
}

vec4 calculateNormal(vec4 pos, float epsilon)
{
	vec4 epsilonVec = vec4(epsilon, 0.0, 0.0, 0.0);
    vec4 xyyy = vec4(epsilon, 0.0, 0.0, 0.0);
    vec4 yxyy = vec4(0.0, epsilon, 0.0, 0.0);
    vec4 yyxy = vec4(0.0, 0.0, epsilon, 0.0);
    vec4 yyyx = vec4(0.0, 0.0, 0.0, epsilon);

	vec4 normal = vec4(
	    map(pos+xyyy).x - map(pos-xyyy).x,
	    map(pos+yxyy).x - map(pos-yxyy).x,
	    map(pos+yyxy).x - map(pos-yyxy).x,
    	map(pos+yyyx).x - map(pos-yyyx).x);
	return normalize(normal);
}

vec4 castRay(in vec4 pos, in vec4 direction)
{
    vec4 rayPos = pos+0.25*direction;
    vec4 rayDirection = direction;
    float distance = 0.0;
    float rayDistance = distance;

    float iterations = 0.0;

    for (int i = 0; i < ITERATIONS; ++i)
    {
        iterations += 1.0;


        vec2 mapPoint = map(rayPos);
        distance = max(mapPoint.y, 0.25*mapPoint.x);

        rayPos += distance*rayDirection;
        rayDistance += distance;

        if (mapPoint.x < EPSILON) break;
        if (rayDistance  > FAR) break;

    }


    vec4 lightdir = normalize(vec4(0.0, 0.0, 0.0, 1.0));
    vec4 normal = calculateNormal(rayPos, EPSILON);


    vec3 aura = vec3(1.0)*iterations/float(ITERATIONS)/pow(rayDistance*0.0625, 0.5);
    vec3 color = vec3(0.0, 0.2, 0.0)*(dot(lightdir, normal))/pow(rayDistance*0.25, 0.5);

    if (distance < 0.1)
       return vec4(vec3(color+aura-0.25), 1.0);
    else
   		return vec4(vec3(aura-0.25), 1.0);
}


mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


