vec2 warp( float x, float y, float radius ){

    // width of radius at y
    float rad_w = sqrt( radius*radius - y*y );
    float warp_x = x / rad_w;

    // height of radius at x
    float rad_h = sqrt( radius*radius - x*x );
    float warp_y = y / rad_h;

    //
    warp_x = warp_x + (cos( x * PI ));
    warp_y = warp_y + (cos( y * PI ));

    return vec2( warp_x, warp_y );
}

vec4 hext( float x, float y ){
    float sides = 4.0;
    float spotCount = 15.0;
    float spotRatio = 0.75;

    float ox = iTime * 0.11;
    float oy = iTime * 0.13 * y * 0.1;

    float spdx = mod( x * spotCount + ox, 2.0 ) - 1.0;
    float spdy = mod( y * spotCount + oy, 2.0 ) - 1.0;

    float sptheta = atan( spdy, spdx );
    float spdis = sqrt( spdx * spdx + spdy * spdy );

    if( spdis > spotRatio ){
        return vec4( sin(x)*0.7, 0.0, sin(x), 1.0 );
    }else{

        float hexangle = (2.0 * PI / sides );
        float hextheta = mod( sptheta / hexangle, 1.0 );

        float Ax = 1.0;
        float Ay = 0.0;
        float Bx = cos( hexangle );
        float By = sin( hexangle );

        float Mx = Bx * hextheta + Ax * (1.0 - hextheta);
        float My = By * hextheta + Ay * (1.0 - hextheta);
        float Mdis = sqrt( Mx * Mx + My * My );
        Mdis *= Mdis;

        if( spdis > Mdis ){
            return vec4( 0.0, sin(x), 0.0, 1.0 );
        }else{
            return vec4( sin(x)*1.4, 0.0, 0.0, 1.0 );
        }
    }
}



