   float qx = p.x * 0.15 + d.x * 0.85;
   float qy = p.y * 0.15 + d.y * 0.85;



    //----- shading -----
    dismf = 1.0 - dismf;


    vec4 texs = texture( iChannel0, vec2( qx * focal + heartX, qy * focal + heartY ) );
    vec4 tex = hext( qx * focal + heartX, qy * focal + heartY );

