		{
			vec3 d = abs(p) - b;
			return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
		}

		vec4 mapCube( vec3 pos, vec3 size, vec3 cl)
		{
			float dist = sdBox(pos , size);
			return vec4(cl, dist );
		}

		vec4 combine(vec4 val1, vec4 val2 )
		{
			if ( val1.w < val2.w ) return val1;
			return val2;
		}

		mat4 matRotateY(float rad)
		{
			return mat4( cos(rad),0,-sin(rad),0,
						 0,       1,        0,0,
						 sin(rad),0, cos(rad),0,
						 0,       0,        0,1);
		}

		vec4 renderDot(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( 0.0, -size*2.0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));
			vec4 val2 = mapCube(pos.xyz + vec3( 0.0, size*2.0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));
			return combine( val1, val2);
		}

		vec4 renderJ(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( -size*2.0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			vec4 val2 = mapCube(pos.xyz + vec3( 0, size*4.0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));
			vec4 val3 = mapCube(pos.xyz + vec3( size*2.0, 3.0, 0 )+translate, vec3(size,size*2.0,size), vec3( 3, 3, 3));
			vec4 val4 = combine ( val1, val2 );
			return combine( val3, val4);
		}

		vec4 renderU(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( size*2.0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			vec4 val2 = mapCube(pos.xyz + vec3( -size*2.0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			vec4 val3 = mapCube(pos.xyz + vec3( 0, size*4.0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));

			vec4 val4 = combine ( val1, val2 );

			return combine( val3, val4);
		}

		vec4 renderS(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( -size*2.0, 2.0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));
			vec4 val2 = mapCube(pos.xyz + vec3( size*2.0, -2.0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));
			vec4 val3 = mapCube(pos.xyz + vec3( 1.0, size*4.0, 0 )+translate, vec3(size*2.0,size,size), vec3( 3, 3, 3));
			vec4 val4 = mapCube(pos.xyz + vec3( -1.0, -size*4.0, 0 )+translate, vec3(size*2.0,size,size), vec3( 3, 3, 3));
			vec4 val5 = mapCube(pos.xyz + vec3( 0, 0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));
			vec4 val6 = combine ( val1, val2 );
			vec4 val7 = combine ( val3, val4 );
			vec4 val8 = combine ( val5, val6 );
			return combine( val7, val8);
		}

		vec4 renderT(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( 0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			vec4 val2 = mapCube(pos.xyz + vec3( 0, -size*4.0, 0 )+translate, vec3(size*3.0,size,size), vec3( 3, 3, 3));
			return combine( val1, val2);
		}

		vec4 renderK(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( size*2.0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			vec4 val2 = mapCube(pos.xyz + vec3( 0.0, 0.0, 0.0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));
			vec4 val3 = mapCube(pos.xyz + vec3( -size*2.0, -3.0, 0 )+translate, vec3(size,size*2.0,size), vec3( 3, 3, 3));
			vec4 val4 = mapCube(pos.xyz + vec3( -size*2.0, 3.0, 0 )+translate, vec3(size,size*2.0,size), vec3( 3, 3, 3));
			vec4 val5 = combine ( val1, val2 );
			vec4 val6 = combine ( val3, val4 );
			return combine( val5, val6);
		}

		vec4 renderI(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( 0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			return val1;
		}

		vec4 renderH(vec4 pos, int num, vec3 translate, float size)
		{
			vec4 val1 = mapCube(pos.xyz + vec3( size*2.0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			vec4 val2 = mapCube(pos.xyz + vec3( -size*2.0, 0, 0 )+translate, vec3(size,size*5.0,size), vec3( 3, 3, 3));
			vec4 val3 = mapCube(pos.xyz + vec3( 0, 0, 0 )+translate, vec3(size,size,size), vec3( 3, 3, 3));

			vec4 val4 = combine ( val1, val2 );
			return combine( val3, val4);
		}

		vec4 renderChar(vec4 pos, int num, vec3 translate)
		{
			float size = 1.0;
			vec4 res = vec4(0);
			if(num == CHAR_J) 	    { res = renderJ(pos,num,translate,size); } 
			else if(num == CHAR_U)  { res = renderU(pos,num,translate,size); }
			else if(num == CHAR_S)  { res = renderS(pos,num,translate,size); }
			else if(num == CHAR_T)  { res = renderT(pos,num,translate,size); }
			else if(num == CHAR_K)  { res = renderK(pos,num,translate,size); }
			else if(num == CHAR_I)  { res = renderI(pos,num,translate,size); }
			else if(num == CHAR_H)  { res = renderH(pos,num,translate,size); }	

			return res;
		}

		vec4 map( vec3 pos, mat4 m)
		{
			vec4 q = vec4(pos+vec3(0,0,-50.0),1.0)*m;

			int num = int(mod(iTime,10.));
			if(num != 8 && num != 9 && num != 0){
				q.x = mod(q.x, 10.0)-5.0;
				q.y = mod(q.y, 16.0)-8.0;
				//q.z = mod(q.z, 16.0)-8.0;
			}

			vec4 digit;
			int char0;
			int char1;
			int char2;
			int char3;
			if(num == 8 ) {
				char0 = CHAR_T;
				char1 = CHAR_S;
				char2 = CHAR_U;
				char3 = CHAR_J;
			} else if(num == 9) {
				char0 = CHAR_S;
				char1 = CHAR_U;
			} else if(num == 0) {
				char0 = CHAR_H;
				char1 = CHAR_T;
				char2 = CHAR_I;
				char3 = CHAR_K;
			}

			if(num == 8 || num == 0) {
				// JU:ST
				vec4 num1 = renderChar(q,char0,vec3(-15,0,0));
				vec4 num2 = renderChar(q,char1,vec3(-5,0,0));
				vec4 num3 = renderChar(q,char2,vec3(5,0,0));
				vec4 num4 = renderChar(q,char3,vec3(15,0,0));

				vec4 num5 = combine(num1, num2);
				vec4 num6 = combine(num3, num4);
				return combine(num5, num6);
			} if(num == 9) {
				vec4 num1 = renderChar(q,char0,vec3(-5,0,0));
				vec4 num2 = renderChar(q,char1,vec3(5,0,0));
				return combine(num1, num2);
			} else {
				digit = renderChar(q,num,vec3(0,0,0));
			}

			return digit;
		}

		vec2 rot(vec2 p, float a) {
			return vec2(
				cos(a) * p.x - sin(a) * p.y,
				sin(a) * p.x + cos(a) * p.y);
		}

		void mainImage( out vec4 fragColor, in vec2 fragCoord ){

			vec2 position = ( fragCoord.xy / iResolution.xy );
			position -= .5;
			//if(abs(position.y) > 0.40) discard;
			vec3 dir = vec3(position, -1.0 );
			dir = normalize(dir);

         	float aspect = iResolution.x / iResolution.y;
         	dir = normalize(vec3(position * vec2(aspect, 1.0), 1.0));
		 	dir.yz = rot(dir.yz, 0.1);

			vec3 pos = vec3(0.0, 5.0, 15.0);

			mat4 m;
			int sec = int(mod(iTime,10.));
			if(sec != 8 && sec != 9 && sec != 0){
				float dir = 1.0;
				if(sec > 4) {
					dir = -1.0;
				}
				m = matRotateY(iTime*0.2*dir);
			} else {
				pos = vec3(0.0, 5.0, 10.0+mod(iTime,15.));
				m = matRotateY(0.);
			}

			vec4 result;
			for (int i =0; i < ITER; i++)
			{
				result = map( pos, m);
				if (result.w < NEAR || result.w > FAR) break;
				pos += result.w * dir;
			}

			vec3 col = map(pos, m).xyz;
			if ( pos.z> 100. )
			{
				// background effect
				vec2 uv = fragCoord.xy / iResolution.xy;
				col = vec3(uv.x+sin(iTime*0.7),0.1,0.3);
			}
			else
			{
				vec3 lightPos = vec3(20.0, 20.0, 20.0 );
				vec3 light2Pos = normalize( lightPos - pos);
				vec3 eps = vec3( .1, .01, .0 );
				vec3 n = vec3( result.w - map( pos - eps.xyy , m).w,
					       result.w - map( pos - eps.yxy , m).w,
					       result.w - map( pos - eps.yyx , m).w );
				n = normalize(n);

				float lambert = max(.0, dot( n, light2Pos));
				col *= vec3(lambert);

				vec3 h = normalize( -dir + light2Pos);
				float spec = max( 0., dot( n, h ) );
				col += vec3(pow( spec, 16.)) ;
			}
			fragColor = vec4( col, 1.0);
		}

vec2 rotation(in float angle,in vec2 position,in vec2 center)
{
    //Function seen from https://www.shadertoy.com/view/XlsGWf
    float rot = radians(angle);
    mat2 rotation = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
    return vec2((position-center)*rotation);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Time set-up
    float time = iTime;
    float sintime = (sin(time)/2.)+0.5;

    //Stretched uv and angle
	vec2 uv = (fragCoord.xy/iResolution.xy);
    vec2 stuv = ((uv-0.5)*vec2(1.0,0.6))+0.5;
	vec2 angle = rotation(time*40.,stuv,(iMouse.xy/iResolution.xy));

    //Rotating uv
    vec2 rotuv = vec2(fract((angle*5.)+0.5));

    //Texture
    vec3 texture = texture(iChannel0, rotuv).rgb;

	fragColor = vec4(mix(texture,vec3(rotuv,0.),sintime),1.0);
}

