// Make a ray using normalized pixel position, eye position and focus point
ray_t lookAtDir(in vec3 uv_dir, in vec3 pos, in vec3 at) {
	vec3 f = normalize(at - pos);
	vec3 r = cross(f, vec3(0.,1.,0.));
	vec3 u = cross(r, f);
	return ray_t(pos, normalize(uv_dir.x * r + uv_dir.y * u + uv_dir.z * f));
}

float hash(in float x) { return fract(sin(x*.0007)*29835.24389); }
float hash(in vec2 x) { return hash(dot(x,vec2(23.17,17.23))); }
float hash(in vec3 x) { return hash(dot(x,vec3(23.17,17.23,42.5))); }
float noise_value(in vec2 p) {
	vec2 e=vec2(1.,0.), F = floor(p), f = fract(p), k = (3. - 2.*f) * f * f;
	return mix(mix(hash(F),      hash(F+e.xy), k.x),
			   mix(hash(F+e.yx), hash(F+e.xx), k.x), k.y);
}
float noise_value(in vec3 p) {
	vec2 e = vec2(1.,0.);
	vec3 F = floor(p), f = fract(p), k = (3. - 2.*f) * f * f;
	return mix(
		   mix(mix(hash(F),       hash(F+e.xyy), k.x),
			   mix(hash(F+e.yxy), hash(F+e.xxy), k.x), k.y),
		   mix(mix(hash(F+e.yyx), hash(F+e.xyx), k.x),
			   mix(hash(F+e.yxx), hash(F+e.xxx), k.x), k.y), k.z);
}

float terrain_height(in vec3 at) {
	float k = .5;
	vec2 p = at.xz * .0005;
	mat2 m = mat2(1.3,  1.5, 1.5, -1.3);
	float h = 0.;//noise_value(p) * k;
	for (int i = 0; i < 8; ++i) { h += noise_value(p) * k; k *= .5; p = m * p; }
	return 1200. * h * h * h * h;
}

float terrain_distance(in vec3 at) {
	return min(1.+at.y,at.y - terrain_height(at));
}

float cloud_noise(in vec3 p) {
	float k = .5, v = 0.;
    v += noise_value(p) * k; k *= .5; p =  p * 2.01;
    v += noise_value(p) * k; k *= .5; p =  p * 2.01;
    v += noise_value(p) * k; k *= .5; p =  p * 2.01;
    v += noise_value(p) * k; k *= .5; p =  p * 2.01;
	return v;
}

#define MAX_DISTANCE 23000.
#define CLOUDS_HEIGHT_MAX 3000.
#define CLOUDS_HEIGHT_MIN 1000.

vec4 accum = vec4(0.);
float prevV = 0.;
float dist = 0.;
float solid = 0.;

float world(in vec3 at, in vec3 dir) {
	if (at.y > CLOUDS_HEIGHT_MAX)
		return (dir.y < 0.) ? (at.y - CLOUDS_HEIGHT_MAX + 10.) / -dir.y : 1000.;
	if (at.y > CLOUDS_HEIGHT_MIN) {
		vec3 cs = at*.001 - t *.3;
		float k = sin((at.y - CLOUDS_HEIGHT_MIN) / (CLOUDS_HEIGHT_MAX - CLOUDS_HEIGHT_MIN) * c_pi),
			v = cloud_noise(cs) * k,
			V = max(0., v * v - .13);
		if (V > .0) {
			float A = ((V + prevV) * .5 * dist) * .005;
			//vec3 cloudcolor = vec3(.125);// * V;// + sun.color * max(0.,(-cloud_noise(cs+sun.dir*.01)+v))*7.725;
			vec3 cloudcolor = vec3(1.) + sun.color * max(0.,(-cloud_noise(cs+sun.dir*100.)+v)) * 2.;
			accum.rgb += cloudcolor * A * (1. - accum.a);
			accum.a += A;
		}
		prevV = V;
		return 1. + 200. * (1. - V);
	}
	float H = terrain_distance(at);
	return (dir.y <= 0.) ? H : min(H, (CLOUDS_HEIGHT_MIN + 10. - at.y) / dir.y);
}

vec3 normal(in vec3 at) {
	vec2 e = vec2(.1, .0);
	return normalize(vec3(world(at+e.xyy, vec3(0.))-world(at, vec3(0.)),
		  world(at+e.yxy, vec3(0.))-world(at, vec3(0.)),
		  world(at+e.yyx, vec3(0.))-world(at, vec3(0.))));
}

float trace(in ray_t ray, in float maxPath) {
	float path = 0.;
	for (int i = 0; i < 128; ++i) {
		dist = world(RT(ray, path), ray.dir);
		if (dist < .001*path) { solid = 1.; break; }
		if (accum.a >= 1.) break;
		path += dist;
		if (path > maxPath) break;
	}
	return path;
}

vec3 background(in vec3 dir) {
    vec3 ac = vec3(101.,133.,162.) / 255.;
	return ac + sun.color * pow(max(0., dot(dir, sun.dir)), 40.);
}

vec3 light(in vec3 at, in vec3 from, in light_dir_t l) {
	vec3 n = normal(at), h = normalize(from + l.dir);
	//if (trace(ray_t(at+n*1., l.dir), 3000.) < 3000.) return vec3(0.);
	return l.color * (
		cmat.diffuse * max(0., dot(n, l.dir))
		+ pow(max(0., dot(n, h)), cmat.specular) * (cmat.specular + 8.) / (8. * c_pi));
}

void update_mat(in vec3 at) {
	float h = at.y;// + 30. * (noise_value(at.zx*.00002) - .5);
	if (h < 125.) {
		cmat.diffuse = vec3(.2, .4, .9);
		cmat.specular = 30.;
	} else if(h < 150.) {
		cmat.diffuse = vec3(.8, .8, .2);
	} else if(h < 220.) {
		cmat.diffuse = vec3(.8, .9, .2);
	} else if(h > 400.) {
		cmat.diffuse = vec3(1.);
	} else {
		cmat.specular = 100000.;
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    t = iTime * .5;
	// Calculate normalized and aspect-corrected pixel position 
	float aspect = iResolution.x / iResolution.y;
	vec2 uv = (fragCoord.xy / iResolution.xy * 2. - 1.) * vec2(aspect, 1.);

	ray_t ray;
	if (iMouse.z <= 0.) {
		float s = sin(-t*.2), c = cos(t*.2);
		mat3 r = mat3(c, 0., -s, 0., 1., 0., s, 0., c);
		vec3 at = vec3(1500.*t, 0., 0.);
		vec3 pos = at + r * (vec3(8000.,4000.,8000.)*(1.1 + sin(t*.5)));
		ray = lookAtDir(normalize(vec3(uv, 2.)), pos, at+vec3(0., 2000.*sin(t), 0.));
	} else {
		vec2 mp = iMouse.xy / iResolution.xy;
		float a = 2. * c_pi * mp.x;
		float s = sin(a), c = cos(a);
		mat3 r = mat3(c, 0., -s, 0., 1., 0., s, 0., c);
		ray = lookAtDir(normalize(vec3(uv, 2.)),
		 			    vec3(1500.*t, 0. ,0.)+r*vec3(6000.,100.+6000.*(1.-mp.y),6000.),
						vec3(1500.*t, 0. ,0.));
	}

	ray.origin.y = max(terrain_height(ray.origin)+10., ray.origin.y);
	float path = trace(ray, MAX_DISTANCE);

	vec3 color = vec3(0.);
	accum.a = min(accum.a, 1.);
	if (solid > 0.) {
		vec3 at = RT(ray,path);
		update_mat(at);
		color = light(at, -ray.dir, sun);
	} else {
		color = accum.rgb;
	}

    float klen = clamp(pow(path / MAX_DISTANCE, 2.), 0., 1.);
	color = mix(color, background(ray.dir), klen) * (1.-accum.a) + accum.rgb;
    color += .64 * sun.color * pow(max(0., dot(ray.dir, sun.dir)), 2.);

	fragColor = vec4(color, 0.);
}

/* Modified by Avi Levy (c) 2015
 *
 * This is a Manhattan surface,
 * rendered using an IFS.
 *
 * IFS equations due to Brent Werness
 *
 * Instructions:
 *      Click and drag the fractal
 *          - Upwards zooms in
 *          - Left/right rotates
 *
 * 
 * 
 * Original by inigo quilez - iq/2013
 */

const int iterations = 8;
float scale = 3.;
float colorScale = 9.;

const vec3 v1 = vec3(  2./3.,  0.,  0. );
const vec3 v2 = vec3(  -2./3.,  0.,  0. );
const vec3 v3 = vec3(  0.,  2./3.,  0. );
const vec3 v4 = vec3(  0.,  -2./3.,  0. );
const vec3 v5 = vec3(  0.,  0.,  2./3. );
const vec3 v6 = vec3(  0.,  0.,  -2./3. );

const vec3 a1 = vec3(  1./3.,  1./3., 0. );
const vec3 a2 = vec3(  -1./3.,  1./3., 0. );
const vec3 a3 = vec3(  1./3.,  -1./3., 0. );
const vec3 a4 = vec3(  -1./3.,  -1./3., 0. );

const vec3 b1 = vec3(  0.,  1./3., 1./3. );
const vec3 b2 = vec3(  0.,  -1./3., 1./3. );
const vec3 b3 = vec3(  0.,  1./3., -1./3. );
const vec3 b4 = vec3(  0.,  -1./3., -1./3. );

const vec3 c1 = vec3(  1./3.,  0., 1./3. );
const vec3 c2 = vec3(  -1./3.,  0., 1./3. );
const vec3 c3 = vec3(  1./3.,  0., -1./3. );
const vec3 c4 = vec3(  -1./3.,  0., -1./3. );

const vec3 x1 = vec3(  1./3.,  1./3., 1./3. );
const vec3 x2 = vec3(  -1./3.,  1./3., 1./3. );
const vec3 x3 = vec3(  1./3.,  -1./3., 1./3. );
const vec3 x4 = vec3(  -1./3.,  -1./3., 1./3. );
const vec3 x5 = vec3(  1./3.,  1./3., -1./3. );
const vec3 x6 = vec3(  -1./3.,  1./3., -1./3. );
const vec3 x7 = vec3(  1./3.,  -1./3., -1./3. );
const vec3 x8 = vec3(  -1./3.,  -1./3., -1./3. );

float rect(vec3 p, vec3 radius) {
    return length(max(abs(p) - radius, 0.));
}

// return distance and address
vec2 map(vec3 p) {
    float cube = rect(p, vec3(1./3.));

    float a = 0.0;
    float s = 1.0;
    float r = 1.0;
    float dm;
    vec3 v;
    for(int i=0; i < iterations; i++) {
        float d, t;
        d = dot(p-v1,p-v1);
        //if( d<dm ) {
            v=v1; dm=d; t = 0.;
        //}        
        d = dot(p-v2,p-v2);
        if( d<dm ) {
            v=v2; dm=d; t = 1.;
        }
        d = dot(p-v3,p-v3);
        if( d<dm ) {
            v=v3; dm=d; t = 2.;
        }
        d = dot(p-v4,p-v4);
        if( d<dm ) {
            v=v4; dm=d; t = 3.;
        }
        d = dot(p-v5,p-v5);
        if( d<dm ) {
            v=v5; dm=d; t = 4.;
        }
        d = dot(p-v6,p-v6);
        if( d<dm ) {
            v=v6; dm=d; t = 5.;
        }

        d = dot(p-a1,p-a1);
        if( d<dm ) {
            v=a1; dm=d; t = 6.;
        }
        d = dot(p-a2,p-a2);
        if( d<dm ) {
            v=a2; dm=d; t = 7.;
        }
        d = dot(p-a3,p-a3);
        if( d<dm ) {
            v=a3; dm=d; t = 8.;
        }
        d = dot(p-a4,p-a4);
        if( d<dm ) {
            v=a4; dm=d; t = 9.;
        }                        

        d = dot(p-b1,p-b1);
        if( d<dm ) {
            v=b1; dm=d; t = 10.;
        }
        d = dot(p-b2,p-b2);
        if( d<dm ) {
            v=b2; dm=d; t = 11.;
        }
        d = dot(p-b3,p-b3);
        if( d<dm ) {
            v=b3; dm=d; t = 12.;
        }
        d = dot(p-b4,p-b4);
        if( d<dm ) {
            v=b4; dm=d; t = 13.;
        }

        d = dot(p-c1,p-c1);
        if( d<dm ) {
            v=c1; dm=d; t = 14.;
        }
        d = dot(p-c2,p-c2);
        if( d<dm ) {
            v=c2; dm=d; t = 15.;
        }
        d = dot(p-c3,p-c3);
        if( d<dm ) {
            v=c3; dm=d; t = 16.;
        }
        d = dot(p-c4,p-c4);
        if( d<dm ) {
            v=c4; dm=d; t = 17.;
        }

        d = dot(p-x1,p-x1);
        if( d<dm ) {
            v=x1; dm=d; t = 18.;
        }
        d = dot(p-x2,p-x2);
        if( d<dm ) {
            v=x2; dm=d; t = 19.;
        }
        d = dot(p-x3,p-x3);
        if( d<dm ) {
            v=x3; dm=d; t = 20.;
        }
        d = dot(p-x4,p-x4);
        if( d<dm ) {
            v=x4; dm=d; t = 21.;
        }
        d = dot(p-x5,p-x5);
        if( d<dm ) {
            v=x5 ; dm=d; t = 22.;
        }
        d = dot(p-x6,p-x6);
        if( d<dm ) {
            v=x6; dm=d; t = 23.;
        }
        d = dot(p-x7,p-x7);
        if( d<dm ) {
            v=x7; dm=d; t = 24.;
        }
        d = dot(p-x8,p-x8);
        if( d<dm ) {
            v=x8; dm=d; t = 25.;
        }        
        p = v + scale * (p - v);
        r *= scale;
        s *= colorScale;
        a = t + colorScale * a;
    }

    float manhattan = (sqrt(dm)-0.5)/r;
    if(cube < manhattan) {
        return vec2(cube, 0.);
    } else {
        return vec2(manhattan, a/s);
    }
}

const float precis = 0.000001;

vec3 intersect(in vec3 ro, in vec3 rd) {
    vec3 res = vec3(1e20, 0.0, 0.0);

    float maxd = 5.0;

    // sierpinski
    float h = 1.0;
    float t = 0.5;
    float m = 0.0;
    vec2 r;
    for(int i=0; i<100; i++) {
        r = map( ro+rd*t );
        if( r.x<precis || t>maxd ) break;
        m = r.y;
        t += r.x;
    }

    if(t < maxd && r.x < precis) {
        res = vec3(t, 2., m);
    }

    return res;
}

vec3 light = normalize(vec3(1., .7, .9));

vec4 render(in vec3 ro, in vec3 rd) {
    // raymarch
    vec3 tm = intersect(ro,rd);
    if(tm.y < 0.5) {
        return vec4(0., 0., 0., 1.);
    }
    // Position vector
    vec3 position = ro + tm.x * rd;

    // Normal vector calculation
    vec3 epsilon = vec3(precis, 0., 0.);
    vec3 normal = normalize(
        vec3(
            map(position + epsilon.xyy).x - map(position - epsilon.xyy).x,
            map(position + epsilon.yxy).x - map(position - epsilon.yxy).x,
            map(position + epsilon.yyx).x - map(position - epsilon.yyx).x
        )
    );

    // Occlusion calculation
    float ao = 0.;
    float sca = 1.;
    for(int i=0; i<8; i++) {
        float h = 0.001 + 0.5*pow(float(i)/7.0,1.5);
        float d = map(position + h * normal).x;
        ao += -(d-h)*sca;
        sca *= 0.95;
    }

    // surface-light interacion
    vec3 color = (
        .5 + .5 * cos(
            6.2831 * tm.z + vec3(0., 1., 2.)
        )
    )
    * (0.5 + 0.5 * normal.y) // ambient lighting
    * clamp(1.0 - 0.8*ao, 0.0, 1.0) // occlusion
    * 1.5 * vec3(1);

    return vec4(
        pow(clamp(color, 0., 1.), vec3(.45)) // gamma
    , 1.);
}

void mainImage(out vec4 color, in vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;
    vec2 m = vec2(0.5);
    if(iMouse.z>0.0) {
        m = iMouse.xy/iResolution.xy/10.;
        m.y = pow(2., 150. * m.y - 3.);
    }

    // camera
    float an = 3.2 + 0.5*iTime - 6.2831*(m.x-0.5);

    vec3 ro = vec3(2.5*sin(an),0.,2.5*cos(an));
    vec3 ta = vec3(0.0,0.,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 rd = normalize( p.x*uu + p.y*vv + 5.0*ww*m.y );

    color = render(ro, rd);
}

// Companion Cube Remix - @christinacoffin
//	- heart shape mapping doesnt completely play nice with the parallax mapping, something to improve upon.
// 	
// modified version of Parallax mapping demo by nimitz (twitter: @stormoid)  : https://www.shadertoy.com/view/4lSGRh
// modified procedural heartshape by Iq : https://www.shadertoy.com/view/XsfGRn

//Show only the raymarched geometry (for comparison)
//#define RAYMARCHED_ONLY

//The amount of parallax
#define PARALLAX_SCALE .2

//Scale the texture offset as a function of incidence (much better results)
#define USE_OFFSET_SCALING
#define OFFSET_SCALE 4.

//Bump mapping intensity
#define BUMP_STRENGTH .21
#define BUMP_WIDTH 0.004

//Main texture scale
const float texscl = 2.5;

#define ITR 70
#define FAR 15.
#define time iTime

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}
float hash(vec2 x){	return fract(cos(dot(x.xy,vec2(2.31,53.21))*124.123)*412.0); }

float sdfsw = 0.; //Global mouse control

float length4(in vec3 p ){
	p = p*p; p = p*p;
	return pow( p.x + p.y + p.z, 1.0/4.0 );
}

float map(vec3 p)
{
    float d = mix(length(p)-1.1,length4(p)-1.,sdfsw-0.3);
    d = min(d, -(length4(p)-4.));
    return d*.95;
}

float march(in vec3 ro, in vec3 rd)
{
	float precis = 0.001;
    float h=precis*2.0;
    float d = 0.;
    for( int i=0; i<ITR; i++ )
    {
        if( abs(h)<precis || d>FAR ) break;
        d += h;
	    float res = map(ro+rd*d);
        h = res;
    }
	return d;
}

vec3 normal(in vec3 p)
{  
    vec2 e = vec2(-1., 1.)*0.005;   
	return normalize(e.yxx*map(p + e.yxx) + e.xxy*map(p + e.xxy) + 
					 e.xyx*map(p + e.xyx) + e.yyy*map(p + e.yyy) );   
}

//From TekF (https://www.shadertoy.com/view/ltXGWS)
float cells(in vec3 p)
{
    p = fract(p/2.0)*2.0;
    p = min(p, 2.0-p);
    return 1.-min(length(p),length(p-1.0));
}

vec4 heartMapping( in vec2 p )
{	
	p.y -= 0.25;

    // background color
    // make this black for now since we use the heart shape to displace things and dont want the other parts affected (keep it black colored)
    vec3 bcol = vec3(0,0,0);//1.0,0.8,0.7-0.07*p.y)*(1.0-0.25*length(p));

    float tt = mod(iTime,1.5)/1.5;
    float ss = pow(tt,.2)*0.5 + 0.5;
    ss = 1.0 + ss*0.5*sin(tt*6.2831*3.0 + p.y*0.5)*exp(-tt*4.0);
    p *= vec2(0.5,1.5) + ss*vec2(0.5,-0.5);

    // shape
    float a = atan(p.x,p.y)/3.141593;
    float r = length(p);
    float h = abs(a);
    float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);

	// color
	float s = d;//1.0-0.5*clamp(r/d,0.0,1.0);

	s = 0.5;// + 0.5*p.x;
	s *= 1.0;//-0.25*r;
	s = 0.5 + 0.6*s;

	s *= 0.5+0.5*pow( 1.0-clamp(r/d, 0.0, 1.0 ), 0.1 );

	vec3 hcol = vec3(1.0,0.0,0.3)*s;

    vec3 col = mix( bcol, hcol, smoothstep( -0.01, 0.01, d-r) );

    col.x *= ss;//fluctate color based on the beating animation
    col.y = ss;//

    //todo: we could encode other shapes in other color channels and mix them in tex() differently

    return vec4(col,1.0);
}

float tex( vec3 p )
{
    p *= texscl;
    float rz= 0.0;//-0.5;
    float z= 1.;

    // do the Heart shape mapping before the parallax loop warps the position data
    // triplanar map it so we get it on all 6 sides
    float heartScaleFactor = 1.1;
    vec4 heart0 = heartMapping( p.xy * heartScaleFactor );
    vec4 heart1 = heartMapping( p.zy * heartScaleFactor );
    vec4 heart2 = heartMapping( p.xz * heartScaleFactor );

    heart0 = max( heart0, heart1 );
    heart0 = max( heart0, heart2 );
    float heartMask = (heart0.x + heart1.x + heart2.x *0.333);
    heartMask = clamp(heartMask, 0.0, 1.0);

    for ( int i=0; i<2; i++ )
    { 
        #ifndef RAYMARCHED_ONLY
        rz += cells(p)/z;
        #endif

        p *= 2.5*0.15;
        z *= -1.1*0.15;
    }

    return clamp(heartMask+(rz*rz)*4.95,0.,1.)*2.73 - 1.0-heartMask;
}

/*
	The idea is to displace the shaded position along the surface normal towards
	the viewer,	the tgt vector is the displacement vector, then	I apply a scaling
	factor to the displacement and also have an incidence based	offset scaling set up.
*/
vec3 prlpos(in vec3 p, in vec3 n, in vec3 rd)
{
    //vec3 tgt = cross(cross(rd,n), n); //Naive method (easier to grasp?)
    vec3 tgt = n*dot(rd, n) - rd; //Optimized

#ifdef USE_OFFSET_SCALING
    tgt /= (abs(dot(tgt,rd)))+OFFSET_SCALE;

#endif

    p += tgt*tex(p)*PARALLAX_SCALE;
    return p;
}

float btex(in vec3 p)
{
    float rz=  tex(p);
    rz += tex(p*20.)*0.01; //Extra (non-parallaxed) bump mapping can be added

 //   rz += tex(p);

    return rz;
}

vec3 bump(in vec3 p, in vec3 n, in float ds)
{
    vec2 e = vec2(BUMP_WIDTH*sqrt(ds)*0.5, 0);
    float n0 = btex(p);
    vec3 d = vec3(btex(p+e.xyy)-n0, btex(p+e.yxy)-n0, btex(p+e.yyx)-n0)/e.x;
    vec3 tgd = d - n*dot(n ,d);
    n = normalize(n-tgd*BUMP_STRENGTH*2./(ds));
    return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 bp = fragCoord.xy/iResolution.xy*2.-1.; 
    vec2 p = bp;
	p.x*=iResolution.x/iResolution.y;
	vec2 mo = iMouse.xy / iResolution.xy-.5;
    mo = (mo==vec2(-.5))?mo=vec2(0.4,-0.25):mo;
	mo.x *= iResolution.x/iResolution.y;


    mo.y = 0.425;// lock the shape to be 'companion cube-ish in appearance
    mo.y += (0.5 * abs(sin(iTime)))-0.15; 

    p.x += mo.x*1.;
    sdfsw = mo.y*4.;

	vec3 ro = vec3(0.,0.,4.);
    vec3 rd = normalize(vec3(p,-3.+sin(time*0.9+sin(time))));
    mat2 mx = mm2(time*.1+sin(time*0.4)-0.2);
    mat2 my = mm2(time*0.07+cos(time*0.33)-0.1);
    ro.xz *= mx;rd.xz *= mx;
    ro.xy *= my;rd.xy *= my;

	float rz = march(ro,rd);

    vec3 col = vec3(0);

    if ( rz < FAR )
    {
        vec3 pos = ro+rz*rd;
        vec3 nor= normal( pos );
        pos = prlpos(pos,nor,rd);
        float d = distance(ro,pos);
        nor = bump(pos, nor, d);

        vec3 ligt = normalize( vec3(-.5, 0.5, -0.3) );
        float dif = clamp( dot( nor, ligt ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-ligt))), 0.0, 1.0 );
        float spe = pow(clamp( dot( reflect(rd,nor), ligt ), 0.0, 1.0 ),10.);
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
        vec3 brdf = vec3(0.3);
        brdf += bac*vec3(0.3);
        brdf += dif*0.5;

        float tx=  tex(pos);
        col = sin(vec3(1.5+mo.x*0.4,2.2+mo.x*0.25,2.7)+tx*1.2+4.2)*0.6+0.55;
        col = col*brdf + spe*.5/sqrt(rz) +.25*fre;

        col = mix(col,vec3(.0),clamp(exp(rz*0.43-4.),0.,1.));
    }

    col.x *= col.z;//colorize
    col.xyz = col.zxy;//color channel flip!   


    col = clamp(col*1.05,0.,1.);
    col *= pow(smoothstep(0.,.2,(bp.x + 1.)*(bp.y + 1.)*(bp.x - 1.)*(bp.y - 1.)),.3);
    col *= smoothstep(3.9,.5,sin(p.y*.5*iResolution.y+time*10.))+0.1;
    col -= hash(col.xy+p.xy)*.025;



	fragColor = vec4( col, 1.0 );
}

