procedure length(px, py, pz);
	sqrt(px*px + py*py + pz*pz);

procedure sdBox(px, py, pz, bx, by, bz);
	min(max(abs(px)-bx, max(abs(py)-by, abs(pz)-bz)), 0)
    + length(max(abs(px)-bx, 0), max(abs(py)-by, 0),
             max(abs(pz)-bz, 0));
sdbox(px + rx*s, py + ry*s, pz + rz*s, w, w, w);

*/


float box(vec3 pos, float w) {
   return -sdBox(pos, vec3(w,w,w));
}

float crossb(vec3 pos, float w) {
    float b1 = box(vec3(pos[0], pos[1], 0.0), w);
    float b2 = box(vec3(pos[0], 0.0, pos[2]), w);
    float b3 = box(vec3(0.0, pos[1], pos[2]), w);

    return max(max(b1, b2), b3);
}

float sponge(vec3 pos, float w) {
    float c1 = crossb(pos, w);
    float c2 = box(pos, w*3.0);

    return min(-c1, c2);
}

float density(vec3 pos, float sz) {

    float w = 0.15;

    //pos += pnoise(pos, 0.3)*0.1-0.1;
    vec3 opos = pos;
    float x = pos[0], y = pos[1], z = pos[2];
    float t1 = abs(fract(0.8+iTime*0.1)-0.5)*2.0*1.15 + 0.15;

    float c = sponge(pos, w);
    float sz1 = (t1)*1.5; //(t1*0.5+0.5)*2.0;
    float p = (1.0-pnoise(pos*sz1, 0.3))-0.4*t1*2.0;

    c = min(c, p);
    for (int i=0; i<3; i++) {
	    pos *= 3.0;
    	float c2 = sponge(mod(pos + w*0.5, w*3.0), w);
        c = min(c, c2);
    }
    return c;

}

vec3 sample1(vec3 p, vec3 d) {
    float t=0.0, ret=0.0, dt, dt1=1.0/float(STEPS), dt2=dt1*1.0;
    float sum = 0.0, r=0.0;
    float first = 1.0, df=0.0;
    float fac, lastdf, lastr=density(p - d*dt1, 1.0);

    dt = dt1;

    for (int i=0; i<STEPS; i++) {
        r = density(p + d*t, 1.0);

        if (abs(r) < 0.00001) {
           	sum = 1.0;
            break;
        }

        //df = (r-lastr)/dt;

        float r2 = density(p + d*(t+0.001), 1.0);
        lastdf = df;
        df = (abs(r2)-abs(r))/0.001 + 0.000;

        ///*
        fac = (-abs(r)/df);

        dt = -r*23.0*dt1; //(r+lastr)*0.5;


        lastr = r;
        t += dt;
        if (t < 0.0) t = 0.0;
    }

    float t2 = t - 0.1;
    vec3 pos = p + d*t;

#define DF1 0.0001

    float r2 = density(vec3(pos[0]+DF1, pos[1], pos[2]), 1.0);
    float r3 = density(vec3(pos[0], pos[1]+DF1, pos[2]), 1.0);
    float r4 = density(vec3(pos[0], pos[1], pos[2]+DF1), 1.0);
   // float r6 = density(p + d*(t+0.005), 1.0);

    vec3 norm = vec3(r2, r3, r4)/DF1;

    //df = (abs(r2)-abs(r))/0.001 + 0.000;
    norm = normalize(norm);
    norm *= -0.1;
    float r5 = density(pos+norm, 1.0);

    //abs(abs(df-df)
    //float ao = (abs(r6)*0.5 + abs(r)*0.5)/0.005;
    //ao = (0.5*abs(df)+(1.0-ao*0.9))*0.9;
    float ao2 = 1.0-t;
    ao2 = 1.0-r5*1.0;
    ao2 = pow(min(ao2*0.96, 1.0), 50.0);
    ao2 = ao2*(1.0-t*t*t*0.4)*1.4;
    return vec3(ao2, t, dt);
}

