    vec3 target = vec3(0.0, 0.0, 0.0);

    //uv *= 2.0;
	vec3 up = normalize(cross(origin-target, vec3(0.0, 0.0, 1.0)));
    vec3 cr = normalize(cross(up, origin-target));
    vec3 zc = normalize(origin-target);

    vec3 ray = origin + up*uv[0] + cr*uv[1];
    ray = normalize(ray - (origin+zc*0.5)) ;// - origin);
    float c1=0.0;
    //vec3 ray = -normalize((origin + uv) - target);
    float c = 0.0, totw = 0.0, ts=0.0, mint=100.0, maxt=-100.0, thet;

    #define DF 0.000001

    vec3 isect = sample1(origin, ray);
    float ft = isect[1];
    vec3 pos = origin + ray*isect[1];
    float r0 = density(pos, 1.0);
    float r1 = (density(pos+vec3(DF, 0.0, 0.0), 1.0)-r0)/DF;
    float r2 = (density(pos+vec3(0.0, DF, 0.0), 1.0)-r0)/DF;
    float r3 = (density(pos+vec3(0.0, 0.0, DF), 1.0)-r0)/DF;

    vec3 norm = normalize(vec3(r1, r2, r3));

    float spec = dot(normalize((norm+ray)), normalize(vec3(0, -1, 0)));
    spec = max(spec, 0.0);
    spec = pow(spec*1.0, 60.0);
    float diffa = max(dot(norm, normalize(vec3(10.0, 0.5, 2.0))), 0.0);
    float diffb = max(dot(norm, normalize(vec3(-20.0, 0.5, 2.0))), 0.0)*0.2;

    //abs((0.2+0.6*spec+diffa*0.5+diffb))
    float light = abs((spec*0.0+diffa*0.5+diffb))*1.0;
    light = min(max(light, 0.0), 1.0);

    c = isect[0] != 0.0 ? isect[0]*0.2 + 3.0*isect[0]*0.5*min(max(light, 0.0), 1.0) : 0.0;

	fragColor = vec4(c*1.5, c*1.32, c*0.9, 1.0);
    fragColor = fragColor*0.6+0.2;
}

const float pi = 3.14159;

mat3 xrot(float t)
{
    return mat3(1.0, 0.0, 0.0,
                0.0, cos(t), -sin(t),
                0.0, sin(t), cos(t));
}

mat3 yrot(float t)
{
    return mat3(cos(t), 0.0, -sin(t),
                0.0, 1.0, 0.0,
                sin(t), 0.0, cos(t));
}

mat3 zrot(float t)
{
    return mat3(cos(t), -sin(t), 0.0,
                sin(t), cos(t), 0.0,
                0.0, 0.0, 1.0);
}

float btime = 1.0;
float bspeed = 10.0;

float map(vec3 pos)
{
    float dir = 1.0;
    vec3 id = floor(pos);
    pos.y += sin(iTime+id.x*10.0+id.z*10.0);
    pos = fract(pos) * 2.0 - 1.0;
    pos *= yrot(iTime*btime*4.0*dir);
    float s = length(pos.xz);
    float k = atan(pos.z, pos.x);
    float mr = 0.9;
    float r = mix(0.8, 1.0, 0.5 + 0.5 * sin(s/mr*dir*bspeed+k*10.0));
    float p = mr * r;
    float e = 1.0 - s/p;
    vec3 c = vec3(pos.x, clamp(pos.y,-0.02*e, 0.02*e), pos.z);
	return min(max(length(c-pos), s-p), s-0.1);
}

vec3 normal(vec3 p)
{
    vec3 o = vec3(0.01, 0.0, 0.0);
    return normalize(vec3(map(p+o.xyy) - map(p-o.xyy),
                          map(p+o.yxy) - map(p-o.yxy),
                          map(p+o.yyx) - map(p-o.yyx)));
}

float trace(vec3 o, vec3 r)
{
 	float t = 0.0;
    for (int i = 0; i < 16; ++i) {
     	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

float pshade(vec3 p)
{
    vec3 id = floor(p);
    p.y += sin(iTime+id.x*10.0+id.z*10.0);
    p = fract(p) * 2.0 - 1.0;
    p *= yrot(iTime*btime*4.0+length(p.xz)*4.0);
    p += id;
	float ac = texture(iChannel0, vec2(p.y,p.z)).x;
    float bc = texture(iChannel0, vec2(p.x,p.z)).x;
    float cc = texture(iChannel0, vec2(p.x,p.y)).x;
    float s = ((ac + bc + cc) / 3.0) * 2.0 - 1.0;
    return s;
}

vec4 surf(vec3 r, vec3 w, vec3 sn, float t)
{
    float prod = max(dot(sn,-r), 0.0);
    float off = 0.5 + 0.5 * sin(pshade(w)*pi*5.0);
    float ao = map(w) * 20.0;
    float fog = prod / (1.0 + t * t + off + ao);
    return vec4(vec3(fog),off);
}

vec3 shade(vec3 r, float time)
{
    r *= xrot(iTime) * zrot(time);

    vec3 o = vec3(0.0, 0.0, 0.0);
    o.z += time;

    float t = trace(o, r);
    vec3 w = o + r * t;
    vec3 sn = normal(w);

    vec4 ac = surf(r, w, sn, t);

    vec3 from = vec3(0.8, 0.2, 0.1);
    vec3 to = vec3(1.0, 1.0, 1.0);

    float fx = 1.0 - ac.w;

    vec3 mixed = ac.xyz * mix(from, to, fx);

    return mixed;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0 - dot(uv,uv) * 0.33));

    btime = 2.0;
    bspeed = 10.0;
    vec3 sa = shade(r, iTime);

    float of = 0.01;

    btime = 4.0;
    bspeed = 20.0;
    vec3 sb = shade(r, iTime+of);

    btime = 6.0;
    bspeed = 20.0;
    vec3 sc = shade(r, iTime+of*2.0);

    vec3 f = sa * 0.2 + sb * 0.5 + sc * 0.3;

	fragColor = vec4(f,1.0);
}

float rand(in vec2 uv){
    return fract(sin(dot(uv.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise1d(in float p) {
  	float fl = floor(p);
    float fc = fract(p);

  	return mix(rand(vec2(fl,fl)),rand(vec2(fl,fl)+1.0),fc);   
}

float noise(in vec2 uv) {
	return sin(1.5*uv.x)*sin(1.5*uv.y);
}

const mat2 m = mat2( 0.80,  0.60, -0.60,  0.80 );

float fbm1d(in float p) {
        float f = 0.0;
    f += 0.5000*noise1d(p); p = p*2.02;
    f += 0.2500*noise1d(p); p = p*2.03;
    f += 0.1250*noise1d(p); p = p*2.01;
    f += 0.0625*noise1d(p);
    return f/0.9375;
}

float fbm(vec2 uv) {
    float f = 0.0;
    f += 0.5000*noise(uv+iTime*.1); uv = m*uv*2.02;
    f += 0.2500*noise(uv+iTime*.1); uv = m*uv*2.03;
    f += 0.1250*noise(uv+iTime*.1); uv = m*uv*2.01;
    f += 0.0625*noise(uv+iTime*.1);
    return f/0.9375;
}

float fbm2(in vec2 uv) {
   vec2 p = vec2(fbm(uv + vec2(0.0,0.0)),
                 fbm(uv + vec2(5.2,1.3)));

   return fbm(uv + 4.0*p);
}

float r = 0.0;

vec3 mountain(in vec2 p, in vec2 offset) {

    float c = 0.0;
    if(-p.y > fbm1d(p.x)) {

       float mountain = (1.0 - (ceil(-p.y) * -p.y)) * length(p-offset) * .125;

       c = max(c,mountain);
    }

    return c * (.95 + .05 * texture(iChannel0,p*.5).rgb);
}

vec3 scene( in vec2 p) {

    vec2 offset = vec2(1.3,-.6);

    vec3 c = vec3(.9) * length(p+offset) + fbm2(p*20.0+20.0)*.0025; 

    	 c = clamp(c,.0,.2) / length(p+offset) - fbm2(p+vec2(iTime*.01,0))*.025;

    float stars = (1.0 - clamp(0.0, 0.025, r*10.0) * 40.0) * .4 * (p.y * 3.0 + 1.0) * (.25 + fbm2(p*200.0)) * ceil(p.y);

    c = max(c, stars);

    c = max(c,mountain(p, offset));

    c = max(c,mountain(p+vec2(-0.4,0.1), offset+vec2(-0.4,0.1)));

    c = max(c,mountain(p+vec2(-0.55,0.2), offset+vec2(-0.55,0.2)));

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = -1.0 + 2.0 * uv;
    	 p.x *= iResolution.x/iResolution.y;

    	 r = rand(uv);

    vec3 c = scene(p);

	fragColor = vec4(c,1.0);
}

// Reference shaders below
// RAYMARCHED BALL (iq):  https://www.shadertoy.com/view/ldfSWs
// NOISE (iq): https://www.shadertoy.com/view/4sfGzS#
// CUBEMAP (iq): https://www.shadertoy.com/view/ltl3D8
// SWIRLY STUFF (Antonalog): https://www.shadertoy.com/view/4s23WK
// STAR/FLARE (mu6k): https://www.shadertoy.com/view/4sX3Rs
// RAY V SPHERE (reinder): https://www.shadertoy.com/view/4tjGRh

const int star_iterations = 10;
const float distort_iterations = 5.0;
const float tex_scale = 0.025;
const float time_scale = 0.2;
const vec3 col_star = vec3( 1.0, 0.7, 0.5 );
const vec3 pos_star = vec3( 0.0, 9.0, 30.0 );
const vec3 world_up = vec3( 0.0, 1.0, 0.0 );

struct CameraInfo
{
    vec3 pos;
    vec3 dir;
    mat3 m;
    mat3 mInv;
};

float hash( float n ) { return fract(sin(n)*123.456789); }

vec2 rotate( in vec2 uv, float a)
{
    float c = cos( a );
    float s = sin( a );
    return vec2( c * uv.x - s * uv.y, s * uv.x + c * uv.y );
}

float noise( in vec3 p )
{
    vec3 fl = floor( p );
    vec3 fr = fract( p );
    fr = fr * fr * ( 3.0 - 2.0 * fr );

    float n = fl.x + fl.y * 157.0 + 113.0 * fl.z;
    return mix( mix( mix( hash( n +   0.0), hash( n +   1.0 ), fr.x ),
                     mix( hash( n + 157.0), hash( n + 158.0 ), fr.x ), fr.y ),
                mix( mix( hash( n + 113.0), hash( n + 114.0 ), fr.x ),
                     mix( hash( n + 270.0), hash( n + 271.0 ), fr.x ), fr.y ), fr.z );
}

float fbm( in vec2 p, float t )
{
    float f;
    f  = 0.5000 * noise( vec3( p, t ) ); p *= 2.1;
    f += 0.2500 * noise( vec3( p, t ) ); p *= 2.2;
    f += 0.1250 * noise( vec3( p, t ) ); p *= 2.3;
    f += 0.0625 * noise( vec3( p, t ) );
    return f;
}

vec2 field(vec2 p)
{
    float t = time_scale * iTime;

    p.x += t;

    float n = fbm( p, t );

    float e = 0.25;
    float nx = fbm( p + vec2( e, 0.0 ), t );
    float ny = fbm( p + vec2( 0.0, e ), t );

    return vec2( n - ny, nx - n ) / e;
}

vec3 distort( in vec2 p )
{
    for( float i = 0.0; i < distort_iterations; ++i )
    {
        p += field( p ) / distort_iterations;
    }
    vec3 s = 2.5 * texture( iChannel0, vec2( 0.0, p.y * tex_scale ) ).xyz;

    return fbm( p, 0.0 ) * s;
}

vec2 map( in vec2 uv )
{
    uv.x *= 5.0;
    uv.x += 0.01 * iTime;
    uv.y *= 15.0;
    return uv;
}

vec3 doBackgroundStars( in vec3 dir )
{
    vec3 n  = abs( dir );
    vec2 uv = ( n.x > n.y && n.x > n.z ) ? dir.yz / dir.x: 
              ( n.y > n.x && n.y > n.z ) ? dir.zx / dir.y:
                                           dir.xy / dir.z;

    float f = 0.0;

    for( int i = 0 ; i < star_iterations; ++i )
    {
        uv = rotate( 1.07 * uv + vec2( 0.7 ), 0.5 );

        float t = 10. * uv.x * uv.y + iTime;
        vec2 u = cos( 100. * uv ) * fbm( 10. * uv, 0.0 );
        f += smoothstep( 0.5, 0.55, u.x * u.y ) * ( 0.25 * sin( t ) + 0.75 );
    }

    return f * col_star;
}

vec3 doMainStar( in vec2 uv, in vec2 sp)
{
    float t = atan( uv.x - sp.x, uv.y - sp.y );
    float n = 2.0 + noise( vec3( 10.0 * t, iTime, 0.0 ) );
    float d = length( uv - sp ) * 25.0;
    return ( ( 1.0 + n ) / ( d * d * d ) ) * col_star;
}

float doCastSphere( in vec3 p, in vec3 rd )
{
    float b = dot( p, rd );
    float c = dot( p, p ) - 1.0;

    float f = b * b - c;
    if( f >= 0.0 )
    {
        return -b - sqrt( f );
    }
    return -1.0;
}

vec3 doMaterial( in vec3 pos )
{
    vec2 uv;
    uv.x = atan( pos.x, pos.z );
    uv.y = asin( pos.y );
    return distort( map( uv ) );
}

vec3 doLighting( in vec3 n, in vec3 c, in vec3 rd, in vec3 rdc )
{
    vec3  l   = normalize( pos_star + 2.0 * ( pos_star - dot( pos_star, rdc ) * rdc ) );
    float ndl = dot( n, l );
    float ndr = dot( n, -rd );
    float ldr = dot( l, rd );
    float f   = max( ndl, 0.0 ) + 0.002;
    float g   = ldr * smoothstep( 0.0, 0.1, ndr ) * pow( 1.0 - ndr, 10.0 );
    return clamp( f * c + g * col_star, 0.0, 1.0 );
}

float doFlare( in vec2 uv, in vec2 dir, float s )
{
    float d = length( uv - dot( uv, dir ) * dir );
    float f = 0.0;
    f += max( pow( 1.0 - d, 128.0 ) * ( 1.0   * s - length( uv ) ), 0.0 );
    f += max( pow( 1.0 - d,  64.0 ) * ( 0.5   * s - length( uv ) ), 0.0 );
    f += max( pow( 1.0 - d,  32.0 ) * ( 0.25  * s - length( uv ) ), 0.0 );
    f += max( pow( 1.0 - d,  16.0 ) * ( 0.125 * s - length( uv ) ), 0.0 );
    return f;
}

float doLensGlint( in vec2 uv, in vec2 c, float r, float w )
{
    float l = length( uv - c );
    return length( c ) * smoothstep( 0.0, w * r, l ) * ( 1.0 - smoothstep( w * r, r, l ) );
}

vec3 render( in vec2 uv, in CameraInfo ci )
{
    // create view ray
    vec3 rd  = ci.m * normalize( vec3( uv, 1.0 ) );
    vec3 rdc = ci.m * vec3( 0.0, 0.0, 1.0 );

    // background stars
    vec3 c = doBackgroundStars( rd );

    // main star
    vec3 cp = ci.mInv * (pos_star - ci.pos);
    vec2 sp = cp.xy / cp.z;
    if( cp.z > 0. )
    {
        c += doMainStar( uv, sp );
    }

    // planet
    float t = doCastSphere( ci.pos, rd );
    if( t > 0.0 )
    {
        vec3 pos = ci.pos + t * rd;
        vec3 nor = normalize( pos );
        c = doMaterial( pos );
        c = doLighting( nor, c, rd, rdc );
    }

    // lens flare
    if( cp.z > 0.0 && sp.x > -1.0 && sp.x < 1.0 )
    {
        float oc = smoothstep( 0.35, 0.4, length( sp ) );
        float f = 0.0;
        f += doFlare( uv - sp, vec2( 1.,0. ), oc );
        f += oc * 0.05 * doLensGlint( uv, -0.4 * sp, 0.2, 0.92 );
        f += oc * 0.09 * doLensGlint( uv, -0.8 * sp, 0.3, 0.95 );
        f += oc * 0.04 * doLensGlint( uv, -1.1 * sp, 0.06, 0.8 );
        c += f * col_star;
    }

    return c;
}

CameraInfo doCamera( in vec3 pos, in vec3 dir )
{
    CameraInfo ci;

    vec3 ww = dir;
    vec3 uu = normalize( cross( ww, world_up ) );
    vec3 vv = normalize( cross( uu, ww ) );
    mat3 m = mat3( uu, vv, ww );
    mat3 mInv = mat3( uu.x, vv.x, ww.x,
                      uu.y, vv.y, ww.y,
                      uu.z, vv.z, ww.z );

    ci.pos = pos;
    ci.dir = dir;
    ci.m = m;
    ci.mInv = mInv;

    return ci;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = gl_FragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 m = iMouse.xy / iResolution.xy;

    // camera default movement
    float cx = cos( 0.1 * iTime + 3.55 );
    float sx = sin( 0.1 * iTime + 3.55 );
    float cy = 0.;

    // camera mouse movement
    if( iMouse.z > 0. )
    {
        cx = cos( 10. * m.x );
        sx = sin( 10. * m.x );
        cy = cos( 3.2 * m.y );
    }

    // camera position/direction
    vec3 camPos = 2. * vec3( cx - sx, cy, sx + cx );
    vec3 camDir = normalize( -camPos );

    // render scene
    vec3 c = render( uv, doCamera( camPos, camDir ) );
    //vec3 c = vec3( distort( map( uv ) ) );
    //vec3 c = vec3( fbm( map( uv ), iTime ) );

    // gamma correction
    c = pow( c, vec3( 0.4545 ) );

    fragColor = vec4( c, 1.0 );
}

#define ss 4
#define pi 3.1415926535897
#define rotation 1.

float roundf(float v, float d){
    return ceil(v/d-0.5)*d;
}

vec2 roundf(vec2 uv, float d){
    return ceil(uv/d-0.5)*d;
}


float checkerboard(vec2 uv){
    vec2 p=mod(uv-vec2(0.25,0.25),1.0);
    return mod(step(p.x,0.5)+step(p.y,0.5),2.0);
}

vec2 rot(vec2 uv, float r){
    float cr=cos(r),sr=sin(r);
    return vec2(cr*uv.x-sr*uv.y,sr*uv.x+cr*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tv=0.0;
    float t=iTime*0.5;

    for(int xp=0;xp<ss;xp++){
        for(int yp=0;yp<ss;yp++){
	vec2 uv = 2.0*(fragCoord.xy-iResolution.xy*0.5+vec2(xp,yp)/float(ss))/iResolution.x;
            uv*=1.0;//+0.01*cos(pi*t)*pow(length(uv),2.0); Yes, it's just an optical illusion - no barrel distortion.
            float rad=pow(2.0,t*0.1-2.9);
    uv*=4.0*rad;//clamp(pow(2.0,iTime*0.1),1.0,2.0);
    //smoothstep(4.0,10.0,t)
    //uv=rot(uv,0.01*rotation*sin(pi*t));


   // uv.x=uv.x-round(uv.y-0.25,0.5)*t;
	float v=checkerboard(uv);

            //get activeness of inversion
            vec2 squarepos=roundf(uv,0.5);
            float isactive=smoothstep(2.3*(rad+0.1),2.3*(rad-0.1),length(squarepos));

            //Possible inversion
            uv=mod(abs(uv),0.5)/0.5;
            float d=0.07;
            float w=0.25;

            float v1=v;
            if(uv.x>0.5+d && uv.x<0.5+d+w && uv.y>0.25-d && uv.y<0.25-d+w) v1=1.-v1;
            if(uv.x>0.25-d && uv.x<0.25-d+w && uv.y>0.5+d && uv.y<0.5+d+w) v1=1.-v1;

            float v2=v;
            if(uv.x>0.5+d && uv.x<0.5+d+w && uv.y>0.5+d && uv.y<0.5+d+w) v2=1.-v2;
            if(uv.x>0.25-d && uv.x<0.25-d+w && uv.y>0.25-d && uv.y<0.25-d+w) v2=1.-v2;

            float blend=0.5+0.5*cos(pi*t+squarepos.y*0.2);

            v=mix(v,mix(v2,v1,blend),isactive);
            //v=isactive;
            //v=v2;


    /*if(abs(round(uv.y,0.5)-uv.y)<0.01) v=0.5;*/
            tv+=v;
        }
    }

    tv=tv/float(ss*ss);
    fragColor=vec4(tv,tv,tv,1.0);
}

/* Modified by Avi Levy (c) 2015
 *
 * This is the famous Sierpinski
 * tetrahedron, to 16 iterations.
 *
 * Instructions:
 *      Click and drag the fractal
 *          - Upwards zooms in
 *          - Left/right rotates
 *
 * 
 * Original by inigo quilez - iq/2013
 */

const vec3 va = vec3(  0.0,  0.57735,  0.0 );
const vec3 vb = vec3(  0.0, -1.0,  1.15470 );
const vec3 vc = vec3(  1.0, -1.0, -0.57735 );
const vec3 vd = vec3( -1.0, -1.0, -0.57735 );

// return distance and address
vec2 map(vec3 p) {
    float a = 0.0;
    float s = 1.0;
    float r = 1.0;
    float dm;
    vec3 v;
    for(int i=0; i<16; i++) {
        float d, t;
        d = dot(p-va,p-va);              v=va; dm=d; t=0.0;
        d = dot(p-vb,p-vb); if( d<dm ) { v=vb; dm=d; t=1.0; }
        d = dot(p-vc,p-vc); if( d<dm ) { v=vc; dm=d; t=2.0; }
        d = dot(p-vd,p-vd); if( d<dm ) { v=vd; dm=d; t=3.0; }
        p = v + 2.0*(p - v); r*= 2.0;
        a = t + 4.0*a; s*= 4.0;
    }

    return vec2( (sqrt(dm)-1.0)/r, a/s );
}

const float precis = 0.000001;

vec3 intersect(in vec3 ro, in vec3 rd) {
    vec3 res = vec3(1e20, 0.0, 0.0);

    float maxd = 5.0;

    // sierpinski
    float h = 1.0;
    float t = 0.5;
    float m = 0.0;
    vec2 r;
    for(int i=0; i<100; i++) {
        r = map( ro+rd*t );
        if( r.x<precis || t>maxd ) break;
        m = r.y;
        t += r.x;
    }

    if(t < maxd && r.x < precis) {
        res = vec3(t, 2., m);
    }

    return res;
}

vec3 light = normalize(vec3(1., .7, .9));

vec4 render(in vec3 ro, in vec3 rd) {
    // raymarch
    vec3 tm = intersect(ro,rd);
    if(tm.y < 0.5) {
        return vec4(0., 0., 0., 1.);
    }
    // Position vector
    vec3 position = ro + tm.x * rd;

    // Normal vector calculation
    vec3 epsilon = vec3(precis, 0., 0.);
    vec3 normal = normalize(
        vec3(
            map(position + epsilon.xyy).x - map(position - epsilon.xyy).x,
            map(position + epsilon.yxy).x - map(position - epsilon.yxy).x,
            map(position + epsilon.yyx).x - map(position - epsilon.yyx).x
        )
    );

    // Occlusion calculation
    float ao = 0.;
    float sca = 1.;
    for(int i=0; i<8; i++) {
        float h = 0.001 + 0.5*pow(float(i)/7.0,1.5);
        float d = map(position + h * normal).x;
        ao += -(d-h)*sca;
        sca *= 0.95;
    }

    // surface-light interacion
    vec3 color = (
        .5 + .5 * cos(
            6.2831 * tm.z + vec3(0., 1., 2.)
        )
    )
    * (0.5 + 0.5 * normal.y) // ambient lighting
    * clamp(1.0 - 0.8*ao, 0.0, 1.0) // occlusion
    * 1.5 * vec3(1);

    return vec4(
        pow(clamp(color, 0., 1.), vec3(.45)) // gamma
    , 1.);
}

void mainImage(out vec4 color, in vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;
    vec2 m = vec2(0.5);
    if(iMouse.z>0.0) {
        m = iMouse.xy/iResolution.xy/10.;
        m.y = pow(2., 150. * m.y - 3.);
    }

    // camera
    float an = 3.2 + 0.5*iTime - 6.2831*(m.x-0.5);

    vec3 ro = vec3(2.5*sin(an),0.0,2.5*cos(an));
    vec3 ta = vec3(0.0,-0.5,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 rd = normalize( p.x*uu + p.y*vv + 5.0*ww*m.y );

    color = render(ro, rd);
}

#define PI 3.1415926536897932

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float dsin(float val)
{
    return sin(radians(val));
}

float clamps(float val)
{
    return clamp(val,0.,1.);
}

float randfade(vec2 co, float time){
    float a = fract(sin(dot(co.xy+1. ,vec2(12.9898,78.233))) * 43758.5453);
    float b = fract(sin(dot(co.xy+2. ,vec2(12.9898,78.233))) * 43758.5453); 
    float c = fract(sin(dot(co.xy+3. ,vec2(12.9898,78.233))) * 43758.5453); 
    return mix(a,b+clamps(mix(0.,c,sin(time/2.))),(sin(time)/2.)+0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime;
    //Make background
    float noisebg = 1.-(length(uv-0.5)-randfade(uv,time)*sin((uv.y*5.+uv.x)+(uv.x*3.+(uv.y*uv.y*6.))+time));
    vec3 bg = clamp(vec3(pow(noisebg,0.2),noisebg*1.5,noisebg),0.,1.);

    //Make raster bar1
    //vec2(shade,alpha)
    float ypos = sin(time)/1.;
    float size = 5.;
    vec2 uv1 = ((uv-0.5)*size)+0.5;
    vec2 rasterbar = vec2(dsin((uv1.y*180.)+(ypos*180.)),1.-clamps(float(uv1.y>(1.-ypos))+float(uv1.y<(0.-ypos))));
    //
    //Make raster bar2
    //vec2(shade,alpha)
    ypos = sin(time*3.)/1.1;
    vec2 rasterbar2 = vec2(dsin((uv1.y*180.)+(ypos*180.)),1.-clamps(float(uv1.y>(1.-ypos))+float(uv1.y<(0.-ypos))));
    //
    //Make raster bar3
    //vec2(shade,alpha)
    ypos = sin(time*2.)/0.7;
    vec2 rasterbar3 = vec2(dsin((uv1.y*180.)+(ypos*180.)),1.-clamps(float(uv1.y>(1.-ypos))+float(uv1.y<(0.-ypos))));
    //This is confusing
    vec3 colors = vec3(clamp(bg-rasterbar.g-rasterbar2.g-rasterbar3.g,0.,1.))+vec3(mix(0.,rasterbar.r,rasterbar.g),mix(0.,rasterbar2.r,rasterbar2.g)-rasterbar.g,((mix(0.,rasterbar3.r,rasterbar3.g))-rasterbar.g)-rasterbar2.g);
	fragColor = vec4(colors,1.);
}

