vec3 camPos = vec3(0.0), camFacing;
vec3 camLookat=vec3(0,0.0,0);

// polynomial smooth min (k = 0.1);
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 matMin(vec2 a, vec2 b)
{
	if (a.x < b.x) return a;
	else return b;
}

float spinTime;

const float thick = 0.85;
const float spacer = 0.01;
const float thickSpace = thick - spacer;
float Disc(vec3 p, float scale)
{
    float len = length(p.xz);
    // infinite cylinder
    float dist = len - scale;
    // cut out the inner disc
    dist = max(dist, -(len - thick*scale));
    //dist = max(dist, -(length(p.xz + vec2(thick*scale, 0.0)) - thick*scale*0.15));
    // cut off half the disc because that looks cool maybe.
    dist = max(dist, -p.z);
    // make it flat, but with nice rounded edges so reflections look good. (slow)
    dist = -smin(-dist, -(abs(p.y)-0.025), 0.015*scale);
    //dist = max(dist, abs(p.y)-0.025);
    return dist;
}

// Calculate the distance field that defines the object.
vec2 DistanceToObject(vec3 p)
{
    float dist = 1000000.0;
    float currentThick = 8.0;
    float harmonicTime = spinTime*0.125*3.14159*4.0;
    // make 15 discs inside each other
    for (int i = 0; i < 15; i++)
    {
        dist = min(dist, Disc(p, currentThick));
        p = RotateX(p, harmonicTime);
        p = RotateZ(p, harmonicTime);
        // scale down a level
        currentThick *= thickSpace;
    }
    vec2 distMat = vec2(dist, 0.0);
    // ball in center
    distMat = matMin(distMat, vec2(length(p) - 0.45, 6.0));
    return distMat;
}

// dirVec MUST BE NORMALIZED FIRST!!!!
float SphereIntersect(vec3 pos, vec3 dirVecPLZNormalizeMeFirst, vec3 spherePos, float rad)
{
    vec3 radialVec = pos - spherePos;
    float b = dot(radialVec, dirVecPLZNormalizeMeFirst);
    float c = dot(radialVec, radialVec) - rad * rad;
    float h = b * b - c;
    if (h < 0.0) return -1.0;
    return -b - sqrt(h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    localTime = iTime - 0.0;
	// ---------------- First, set up the camera rays for ray marching ----------------
	vec2 uv = fragCoord.xy/iResolution.xy * 2.0 - 1.0;
    float zoom = 1.7;
    uv /= zoom;

	// Camera up vector.
	vec3 camUp=vec3(0,1,0);

	// Camera lookat.
	camLookat=vec3(0,0.0,0);

    // debugging camera
    float mx=iMouse.x/iResolution.x*PI*2.0-0.7 + localTime*3.1415 * 0.0625*0.666;
	float my=-iMouse.y/iResolution.y*10.0 - sin(localTime * 0.31)*0.5;//*PI/2.01;
	camPos += vec3(cos(my)*cos(mx),sin(my),cos(my)*sin(mx))*(12.2);

	// Camera setup.
	vec3 camVec=normalize(camLookat - camPos);
	vec3 sideNorm=normalize(cross(camUp, camVec));
	vec3 upNorm=cross(camVec, sideNorm);
	vec3 worldFacing=(camPos + camVec);
	vec3 worldPix = worldFacing + uv.x * sideNorm * (iResolution.x/iResolution.y) + uv.y * upNorm;
	vec3 rayVec = normalize(worldPix - camPos);

	// ----------------------------------- Animate ------------------------------------
    localTime = iTime*0.125;
    // This is a wave function like a triangle wave, but with flat tops and bottoms.
    // period is 1.0
    float rampStep = min(3.0,max(1.0, abs((fract(localTime)-0.5)*1.0)*8.0))*0.5-0.5;
    rampStep = smoothstep(0.0, 1.0, rampStep);
    // lopsided triangle wave - goes up for 3 time units, down for 1.
    float step31 = (max(0.0, (fract(localTime+0.125)-0.25)) - min(0.0,(fract(localTime+0.125)-0.25))*3.0)*0.333;

    spinTime = step31 + localTime - 0.125;
	// --------------------------------------------------------------------------------
	vec2 distAndMat = vec2(0.5, 0.0);
	float t = 0.0;
	//float inc = 0.02;
	float maxDepth = 21.0;
	vec3 pos = vec3(0,0,0);
    marchCount = 0.0;
    // intersect with sphere first as optimization so we don't ray march more than is needed.
    float hit = SphereIntersect(camPos, rayVec, vec3(0.0), 8.0);
    if (hit >= 0.0)
    {
        t = hit;
        // ray marching time
        for (int i = 0; i < 120; i++)	// This is the count of the max times the ray actually marches.
        {
            pos = camPos + rayVec * t;
            // *******************************************************
            // This is _the_ function that defines the "distance field".
            // It's really what makes the scene geometry.
            // *******************************************************
            distAndMat = DistanceToObject(pos);
            // move along the ray
            t += distAndMat.x;
            if ((t > maxDepth) || (abs(distAndMat.x) < 0.0025)) break;
            //marchCount+= 1.0;
        }
    }
    else
    {
        t = maxDepth + 1.0;
        distAndMat.x = 1000000.0;
    }
    // --------------------------------------------------------------------------------
	// Now that we have done our ray marching, let's put some color on this geometry.

	vec3 sunDir = normalize(vec3(3.93, 10.82, -1.5));
	vec3 finalColor = vec3(0.0);

	// If a ray actually hit the object, let's light it.
	//if (abs(distAndMat.x) < 0.75)
    if (t <= maxDepth)
	{
        // calculate the normal from the distance field. The distance field is a volume, so if you
        // sample the current point and neighboring points, you can use the difference to get
        // the normal.
        vec3 smallVec = vec3(0.005, 0, 0);
        vec3 normalU = vec3(distAndMat.x - DistanceToObject(pos - smallVec.xyy).x,
                           distAndMat.x - DistanceToObject(pos - smallVec.yxy).x,
                           distAndMat.x - DistanceToObject(pos - smallVec.yyx).x);

        vec3 normal = normalize(normalU);

        // calculate 2 ambient occlusion values. One for global stuff and one
        // for local stuff
        float ambientS = 1.0;
        ambientS *= saturate(DistanceToObject(pos + normal * 0.1).x*10.0);
        ambientS *= saturate(DistanceToObject(pos + normal * 0.2).x*5.0);
        ambientS *= saturate(DistanceToObject(pos + normal * 0.4).x*2.5);
        ambientS *= saturate(DistanceToObject(pos + normal * 0.8).x*1.25);
        float ambient = ambientS * saturate(DistanceToObject(pos + normal * 1.6).x*1.25*0.5);
        ambient *= saturate(DistanceToObject(pos + normal * 3.2).x*1.25*0.25);
        ambient *= saturate(DistanceToObject(pos + normal * 6.4).x*1.25*0.125);
        ambient = max(0.035, pow(ambient, 0.3));	// tone down ambient with a pow and min clamp it.
        ambient = saturate(ambient);

        // calculate the reflection vector for highlights
        vec3 ref = reflect(rayVec, normal);
        ref = normalize(ref);

        // Trace a ray for the reflection
        float sunShadow = 1.0;
        float iter = 0.1;
        vec3 nudgePos = pos + ref*0.3;// normal*0.02;	// don't start tracing too close or inside the object
		for (int i = 0; i < 40; i++)
        {
            float tempDist = DistanceToObject(nudgePos + ref * iter).x;
	        sunShadow *= saturate(tempDist*50.0);
            if (tempDist <= 0.0) break;
            //iter *= 1.5;	// constant is more reliable than distance-based
            iter += max(0.00, tempDist)*1.0;
            if (iter > 9.0) break;
        }
        sunShadow = saturate(sunShadow);

        // ------ Calculate texture color ------
        vec3 texColor;
        texColor = vec3(1.0);// vec3(0.65, 0.5, 0.4)*0.1;
        texColor = vec3(0.85, 0.945 - distAndMat.y * 0.15, 0.93 + distAndMat.y * 0.35)*0.951;
        if (distAndMat.y == 6.0) texColor = vec3(0.91, 0.1, 0.41)*10.5;
        //texColor *= mix(vec3(0.3), vec3(1.0), tex3d(pos*0.5, normal).xxx);
        texColor = max(texColor, vec3(0.0));
        texColor *= 0.25;
        //texColor += vec3(1.0, 0.0, 0.0) * 8.0/length(pos);

        // ------ Calculate lighting color ------
        // Start with sun color, standard lighting equation, and shadow
        vec3 lightColor = vec3(0.0);// sunCol * saturate(dot(sunDir, normal)) * sunShadow*14.0;
        // sky color, hemisphere light equation approximation, ambient occlusion
        lightColor += vec3(0.1,0.35,0.95) * (normal.y * 0.5 + 0.5) * ambient * 0.2;
        // ground color - another hemisphere light
        lightColor += vec3(1.0) * ((-normal.y) * 0.5 + 0.5) * ambient * 0.2;


        // finally, apply the light to the texture.
        finalColor = texColor * lightColor;
       // if (distAndMat.y == 6.0) finalColor += vec3(0.91, 0.032, 0.01)*0.2;
        //if (distAndMat.y == ceil(mod(localTime, 4.0))) finalColor += vec3(0.0, 0.41, 0.72)*0.925;

        // reflection environment map - this is most of the light
        vec3 refColor = GetEnvColor2(ref, sunDir)*sunShadow;// * (length(normalU)/smallVec.x-0.875)*8.0;
        finalColor += refColor * 0.35 * ambient;// * sunCol * sunShadow * 9.0 * texColor.g;

        // fog
		finalColor = mix(vec3(1.0, 0.41, 0.41) + vec3(1.0), finalColor, exp(-t*0.0007));
        // visualize length of gradient of distance field to check distance field correctness
        //finalColor = vec3(0.5) * (length(normalU) / smallVec.x);
	}
    else
    {
	    finalColor = GetEnvColor2(rayVec, sunDir);// + vec3(0.1, 0.1, 0.1);
    }
    //finalColor += marchCount * vec3(1.0, 0.3, 0.91) * 0.001;

    // vignette?
    //finalColor *= vec3(1.0) * saturate(1.0 - length(uv/2.5));
    //finalColor *= 1.2;

	// output the final color with sqrt for "gamma correction"
	fragColor = vec4(sqrt(clamp(finalColor, 0.0, 1.0)),1.0);
}

#define PI 3.14
#define TAU 6.28318530718

vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float noise(in vec2 uv) {
	return sin(1.5*uv.x)*sin(1.5*uv.y);
}

const mat2 m = mat2( 0.80,  0.60, -0.60,  0.80 );

float fbm(vec2 uv) {
    float f = 0.0;
    f += 0.5000*noise(uv); uv = m*uv*2.02;
    f += 0.2500*noise(uv); uv = m*uv*2.03;
    f += 0.1250*noise(uv); uv = m*uv*2.01;
    f += 0.0625*noise(uv);
    return f/0.9375;
}

float fbm2(in vec2 uv) {
   vec2 p = vec2(fbm(uv + vec2(0.0,0.0)),
                 fbm(uv + vec2(5.2,1.3)));

   return fbm(uv + 4.0*p);
}

float rand(in vec2 uv) {
    return fract(sin(dot(uv.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 add(in vec2 a, in vec2 b) {

    float mat;
    if(a.x < b.x) {
      mat = a.y;
    } else {
      mat = b.y;
    }

    return vec2(min(a.x,b.x), mat);
}

vec2 sub(in vec2 a, in vec2 b) {

    float mat;
    if(a.x < b.x) {
      mat = b.y;
    } else {
      mat = a.y;
    }

    return vec2(max(a.x, b.x), mat);
}

vec3 rep(in vec3 p, in vec3 c) {
    vec3 q = mod(p,c)-0.5*c;
    return q;
}

vec2 rotate(in vec2 p, in float ang) {
    float c = cos(ang), s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

vec2 torus(in vec3 p, in vec2 t, in float mat) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return vec2(length(q)-t.y, mat);
}

vec2 sphere(in vec3 p, in float r, in float mat) {
    return vec2(length(p) - r, mat);
}

vec2 plane(in vec3 p, in vec4 n, in float mat) {
  return vec2(dot(p,n.xyz) + n.w, mat);
}

vec2 cylinder(in vec3 p, in vec3 c, in float mat) {
  return vec2(length(p.xz-c.xy)-c.z, mat);
}

vec2 box(in vec3 p, in vec3 b, in float mat) {
  return vec2(length(max(abs(p)-b,0.0)), mat);
}

// WIP
float trans(in float x, in vec2 p) {
   return mod(x, 0.001*TAU) - 0.001*TAU / 2.0;
}


vec2 knot(in vec3 p, in float k, in float mat) {
    float r = length(p.xy);
    float oa, a = atan(p.y, p.x); 
    oa = k*a;
    a = trans(a, p.xy);

    p.xy = r*vec2(cos(a), sin(a)); p.x -= 6.0;
    p.xz = cos(oa)*p.xz + sin(oa)*vec2(-p.z, p.x);
    p.x = abs(p.x) - 1.35; 
    return vec2(length(p) - 1.0, mat);
}

float map(in vec3 p, inout float mat) {

   p.z = cos(p.z);

   vec2 scene = vec2(999.0, 0.0);

    // TODO : see how to interpolate between rate N and N+1
   float rate = 1.0; // mod(iTime, 3.0);

   float r1 = floor(rate);
   float r2  = floor(rate) + 1.0;

   vec2 k = knot(p, r1, length(p+fbm2(p.xy))*.1);

   vec2 k2 = knot(p, r2, length(p+fbm2(p.xy))*.1);

   float y = 0.5 + cos(iTime) * 0.5;

   scene = add(scene,mix(k,k2,y));

   mat = scene.y;
   return scene.x;
}

mat3 setLookAt(in vec3 ro, in vec3 ta,in float cr) {
	vec3  cw = normalize(ta-ro);
	vec3  cp = vec3(sin(cr), cos(cr),0.0);
	vec3  cu = normalize( cross(cw,cp) );
	vec3  cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

float softshadow(in vec3 ro, in vec3 rd, in float mint, in float tmax) {
	float res = 1.0;
    float t = mint;

    float mat = 0.0;

    for( int i = 0; i < 16; i++ ) {
		float h = map(ro + rd*t, mat);
        res = min( res, 4.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x / iResolution.y;

    // camera	
    vec3 ro = vec3(
        0.0,
        0.0,
        16.0+iTime*5.0
   	);
    vec3 ta = vec3(0.0,0.0,0.0);
    float roll = iTime*.1;

    // camera tx
    mat3 ca = setLookAt( ro, ta, roll );
    vec3 rd = normalize( ca * vec3(p.xy,1.75) );

    float t = 0.001;      // Near
    float tmax = 120.0; // Far

    float h = 0.001;
    float hmax = 0.001;

    float mat = 0.0;

    vec3 c = vec3(0.0);
    vec3 ao = vec3(0.0);

    const int steps = 100;
    for(int i = 0 ; i < steps ; i++) {

        if(h < hmax || t > tmax ) {
        	ao = vec3(1.0) - float(i)/float(steps);
            break;
        }

        h = map(ro + t *rd, mat);
        t += h;
    }

    if(t < tmax) {
        vec3 pos = ro+rd*t;

        vec2 r = vec2(0.001,0.0);
        vec3 nor = normalize(vec3(map(pos+r.xyy, mat)-map(pos-r.xyy, mat),
                                  map(pos+r.yxy, mat)-map(pos-r.yxy, mat),
                                  map(pos+r.yyx, mat)-map(pos-r.yyx, mat)));
      	vec3 ref = reflect(rd,nor);

        c = vec3(1.0,1.2,1.1);;

        if(mat == 0.0) {
            c = vec3(0.0);
        } else {
            c = vec3(.5,.1,.3);

            c = rgb2hsv(c);

            c.x += mat+.1 + pos.z*.1;
            c.y = .85;
            c.z = 1.4;

            c = hsv2rgb(c);
        }

        float power = 8.0;
        ao = vec3(pow(ao.x,power),pow(ao.y,power),pow(ao.z,power));

        c *= 1.0 - exp(1.0 / log(ao));
    } else {
        c = vec3(0.);
    }

	fragColor = vec4(c,1.0);
}

#define PI 3.14159
#define inf 999999.0

// This sets switching to the second model on the transition.
const bool switchModel = true;

mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }
mat3 rotation;

vec3 light = vec3(0.0, 0.0, -1.0);
vec4 ambient = vec4(0.15);
const float boxs = 1.0;

// distance functions: 
// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float sdBox(in vec3 p,in vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}


float sdCross( in vec3 p )
{
  float da = sdBox(p.xyz, vec3(inf, boxs, boxs));
  float db = sdBox(p.yzx, vec3(boxs, inf, boxs));
  float dc = sdBox(p.zxy, vec3(boxs, boxs, inf));
  return min(da,min(db,dc));
}


// "scene"
float colswitch = 0.0;
const float switchFreg = 4.0;
bool model1 = true;
int hititer = -1;
vec4 basecolor = vec4(0.0, 0.5, 1.0, 0.0);
vec4 secondarycol = vec4(0.1, 0.2, 0.2, 0.0);


float map(in vec3 pos)
{
    pos *= rotation;

    float s = 1.0;
    const int iter = 3;

    float m = udRoundBox(pos, vec3(boxs * 0.5), 0.4);

    for (int i = 0; i < iter; ++i)
    {
        vec3 a = mod(pos * s, 2.0) - 1.0;

        s *= 3.0;
        float cr = 1.5;
        if(model1 && switchModel)
        {
            cr = 2.0;
        }
        vec3 r = cr - 3.0 * abs(a);
        float c = sdCross(r) / s;

        if(c > 0.0 && m < 0.0)
        {
        	hititer = i;
        }


        m = max(m, c);
    }
    return m;
}

vec3 grad(in vec3 p)
{
    vec2 offset = vec2(0.008, 0.0);
    vec3 g = vec3(map(p + offset.xyy) - map(p - offset.xyy),
				  map(p + offset.yxy) - map(p - offset.yxy),
				  map(p + offset.yyx) - map(p - offset.yyx));
    return normalize(g);

}


float ao (in vec3 pos, in vec3 norm)
{
    float dist = 0.01;
    pos += norm * dist;
    float m = map(pos);
    float ao = clamp(m, 0.0, dist) / dist;
    return ao;
}


bool trace(in vec3 o, in vec3 dir, inout vec4 color)
{
    color = vec4(0.0);
    vec3 rp = o;
    float m = 0.0;
    float closest = 9999.0;
    float t = iTime * 0.4 + 9.0;
    colswitch = mod(t, switchFreg * 2.0);
    bool firstpass = colswitch < switchFreg;
	vec4 bcol = vec4(0.0);
    float mm = mod(t, switchFreg);

	for (int i = 0; i < 100; ++i)
    {

        // initial state setup
        bcol = firstpass ? basecolor : secondarycol;
        model1 = firstpass;

        float m = o.z + mm;
        float dif = rp.z - m;

		// switch on sweep
        if (dif < 0.0)
        {
            bcol = firstpass ? secondarycol : basecolor;
            model1 = !firstpass;
        }                

        m = map(rp);
        closest = min(closest, m);
        color = bcol;
        if (m < 0.0)
        {
            // coloring
            if(hititer == 1)
            {
            	color = vec4(0.2, 0.4, 0.8, 0.0);
            }

            if(hititer == 2)
            {
            	color = vec4(1., 1.0, 1.0, 0.0);
            }

            vec3 norm = grad(rp);
            // diffuse
            float d = dot(light, norm);
            d = clamp(d, 0.1, 1.0);
            color *= d;

            // blinn-phong specular
            vec3 H = normalize(-dir + light);
            float spc = clamp(dot(H, norm), 0.0, 1.0);

            spc = pow(spc, 35.0) * 0.4;
            color += spc * vec4(1.0, 1.0, 1.0, 0.0);

			// ambient + ao            
            color += ambient;
            color *= mix(ao(rp, norm), 1.0, 0.5);

            // switch effect 
            float e = abs(dif);
            float efsize = 0.05;
            e = 1.0 - clamp(e, 0.0, efsize) / efsize;
            color += e * texture(iChannel0, vec2(rp.z, rp.x) * 4.0).r;
            return true;
        }


        rp += dir * max(m, 0.0001);

        if(rp.z > 1.0)
        {
    	   // outer glow	
           float maxd = 0.03;
           float glow = clamp( (maxd - closest), 0.0, closest) / closest;
           color = vec4(0.0, 0.5, 1.0, 0.0) * (glow) * 0.5; 
           return glow >= 0.8;
        }
    }
    return false;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    light = normalize(light);
    fragColor = vec4(0.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5);
    uv.y /= iResolution.x / iResolution.y;

    rotation = mat3(1.0);
    rotation *= roty(iTime * 0.2);
    rotation *= rotx(iTime * 0.1);

    rotation *= roty(iMouse.x * 0.025);
    rotation *= rotx(-iMouse.y * 0.025);

    vec3 o = vec3(0.0, 0.0, -3.0);
    vec3 dir = normalize(vec3(uv.x, uv.y, 0.6));

    if(!trace(o, dir, fragColor))
    {
        fragColor += vec4(0.9, 0.7, 0.5, 0.0) * (1.8 - pow(length(uv), 0.45));
    }
}

