//	hash22() from Dave_Hoskins -- https://www.shadertoy.com/view/ltfGDs
vec2 hash22( vec2 p ) {
	p		= fract( p * vec2( 5.3983, 5.4427 ) );
    p		+= dot( p.yx, p.xy + vec2( 21.5351, 14.3137 ) );
	return fract( vec2( p.x * p.y * 95.4337, p.x * p.y * 97.597 ) );
}
float sum( vec2 p ) { return p.x + p.y; }
float stars( vec2 p, float density, float brightness ) {
	return 1. / ( 1. + brightness * exp( pow( 2., density ) * sum( hash22( p ) ) ) );
}
float noise( vec2 p ) {
	return .5 + ( sin( p.x ) + sin( p.y  ) ) / 4.;
}
mat2 m2 = mat2( 0.80,  0.60, -0.60,  0.80 ) * 2.;
float fbm( in vec2 p ) {
	float z		=2.;
	float rz	= -0.05;
	p			*= 0.25;
	for (int i= 0 ; i < 4 ; i++) {
		rz		+= abs( ( noise( p * vec2( .741, .0217 ) ) - 0.5 ) * 2. ) / z;
		z		= z * 2.;
		p		= p * 2. * m2;
	}
	return rz;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float t		= -iTime * .2; 
	vec2 p		= fragCoord;
	vec2 uv		= p / iResolution.xy - 0.5;
	uv.x		*= iResolution.x / iResolution.y;
	uv			*= 15. * 0.1;

	float r		= length( uv );
	float x		= dot( normalize( uv ), vec2( .5, 0. ) ) + t;
	float y		= dot( normalize( uv ), vec2( 0., .5 ) ) + t;

    float val;
    val			= fbm( vec2( r + y * 4.5, r + x * 4.5 ) );
	val			= smoothstep( 5. * .02 - .1, 10. + ( 5. * 0.02 - .1 ) + .001, val );
	val			= sqrt( val );

	vec3 col	= val / vec3( 4., 1., .3 );
	col			= 1. - col;
	col			= mix( col, vec3( 1. ), 5. - 266.667 * r );
	float a		= col.x;
    a			= clamp( a, 0., 1. );	//	comment out for smoky effect
	col			= clamp( col, 0., 1. );

	//fragColor.rgb = mix( vec3( stars( p, 5., .25 ) ), col, a );
    fragColor.rgb = mix( vec3( clamp( pow( 1.3 * stars( p, 5., .25 ), 3. ), 0., 1. ) ), col, a );
}

#define ss 4
#define pi 3.1415926535897
#define rotation 1.

float roundf(float v, float d){
    return ceil(v/d-0.5)*d;
}


float checkerboard(vec2 uv){
    vec2 p=mod(uv-vec2(0.5),1.0);
    return mod(step(p.x,0.5)+step(p.y,0.5),2.0);
}

vec2 rot(vec2 uv, float r){
    float cr=cos(r),sr=sin(r);
    return vec2(cr*uv.x-sr*uv.y,sr*uv.x+cr*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tv=0.0;
    float t=iTime*0.67;

    for(int xp=0;xp<ss;xp++){
        for(int yp=0;yp<ss;yp++){
            vec2 uv = 2.0*(fragCoord.xy-iResolution.xy*0.5+vec2(xp,yp)/float(ss))/iResolution.x;
            uv*=4.0;

            //Enable to counteract the Cafe Wall effect
            //uv=rot(uv,0.01*rotation*sin(pi*t));


            uv.x=uv.x-roundf(uv.y-0.25,0.5)*t;
            float v=checkerboard(uv);

            if(abs(roundf(uv.y,0.5)-uv.y)<0.01) v=0.5;
                    tv+=v;
        }
    }
    tv=tv/float(ss*ss);
    fragColor=vec4(tv,tv,tv,1.0);
}

