mat3 MakeRPY(float roll, float pitch, float yaw)
{
    float cp = cos(pitch);
    float sp = sin(pitch);
    float sr = sin(roll);
    float cr = cos(roll);
    float sy = sin(yaw);
    float cy = cos(yaw);

    return mat3(cp * cy, (sr * sp * cy) - (cr * sy), (cr * sp * cy) + (sr * sy),
                cp * sy, (sr * sp * sy) + (cr * cy), (cr * sp * sy) - (sr * cy),
                -sp, sr * cp, cr * cp);
}

float de(vec3 p) {
    vec3 rotAxis = vec3(0,1,0);
//	mat3 rot = rotationMatrix3(rotAxis, iTime*5.);
	p*=vec3(0.4,0.4,0.4);
	vec3 offset = vec3(1,1,1);
	float scale = 3. + 0.3 * sin(iTime * 0.084);
    float rotx = 5.0 * sin(iTime * 0.01);
    float roty = 5.0 * sin(iTime * 0.0057);
    float rotz = 5.0 * sin(iTime * 0.0266);
    mat3 rot = MakeRPY(rotx,roty,rotz);

	for (int i=0; i<Iterations; i++) {
        p*=rot;
		float tmp;

		p = abs(p);
		if (p.x-p.y<0.) {tmp=p.y;p.y=p.x;p.x=tmp;}
		if (p.x-p.z<0.) {tmp=p.z;p.z=p.x;p.x=tmp;}
		if (p.y-p.z<0.) {tmp=p.z;p.z=p.y;p.y=tmp;}

		p.z -= 0.5*offset.z*(scale-1.)/scale;
		p.z = -abs(-p.z);
		p.z += 0.5*offset.z*(scale-1.)/scale;

		p.xy = scale*p.xy - offset.xy*(scale-1.);
		p.z = scale*p.z;
	}
    vec3 d = abs(p) - vec3(1.,1.,1.);
    float distance = min(max(d.x, max(d.y, d.z)),0.) + length(vec3(max(d.x,0.),max(d.y,0.), max(d.z,0.)));
    distance *= pow(scale, -float(Iterations));
    return distance;
}

vec4 lerp(vec4 col1, vec4 col2, float amt)
{
    amt = clamp(amt,0.,1.);
    return col1*amt + col2*(1.-amt);
}

vec4 getColour(vec3 p) {
	p*=vec3(0.4,0.4,0.4);
	vec3 offset = vec3(1,1,1);
	float scale = 3. + 0.3 * sin(iTime * 0.084);
    float rotx = 5.0 * sin(iTime * 0.01);
    float roty = 5.0 * sin(iTime * 0.0057);
    float rotz = 5.0 * sin(iTime * 0.0266);
    mat3 rot = MakeRPY(rotx,roty,rotz);

    float colour=1000000.;

	for (int i=0; i<Iterations; i++) {
        p*=rot;
		float tmp;

		p = abs(p);
		if (p.x-p.y<0.) {tmp=p.y;p.y=p.x;p.x=tmp;}
		if (p.x-p.z<0.) {tmp=p.z;p.z=p.x;p.x=tmp;}
		if (p.y-p.z<0.) {tmp=p.z;p.z=p.y;p.y=tmp;}

		p.z -= 0.5*offset.z*(scale-1.)/scale;
		p.z = -abs(-p.z);
		p.z += 0.5*offset.z*(scale-1.)/scale;

		p.xy = scale*p.xy - offset.xy*(scale-1.);
		p.z = scale*p.z;

        colour = min(colour, length(p)-0.7);
    }

    return lerp(vec4(0.21,0.35,0.66,1.), vec4(0.76,0.65,0.21,1.), colour);
}

#define PI 3.14159265359

float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(12.9898,78.233,128.852))) * 43758.5453)*2.0-1.0;
}

float simplex3D(vec3 p)
{

	float f3 = 1.0/3.0;
	float s = (p.x+p.y+p.z)*f3;
	int i = int(floor(p.x+s));
	int j = int(floor(p.y+s));
	int k = int(floor(p.z+s));

	float g3 = 1.0/6.0;
	float t = float((i+j+k))*g3;
	float x0 = float(i)-t;
	float y0 = float(j)-t;
	float z0 = float(k)-t;
	x0 = p.x-x0;
	y0 = p.y-y0;
	z0 = p.z-z0;

	int i1,j1,k1;
	int i2,j2,k2;

	if(x0>=y0)
	{
		if(y0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=1; k2=0; } // X Y Z order
		else if(x0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=0; k2=1; } // X Z Y order
		else { i1=0; j1=0; k1=1; i2=1; j2=0; k2=1; }  // Z X Z order
	}
	else 
	{ 
		if(y0<z0) { i1=0; j1=0; k1=1; i2=0; j2=1; k2=1; } // Z Y X order
		else if(x0<z0) { i1=0; j1=1; k1=0; i2=0; j2=1; k2=1; } // Y Z X order
		else { i1=0; j1=1; k1=0; i2=1; j2=1; k2=0; } // Y X Z order
	}

	float x1 = x0 - float(i1) + g3; 
	float y1 = y0 - float(j1) + g3;
	float z1 = z0 - float(k1) + g3;
	float x2 = x0 - float(i2) + 2.0*g3; 
	float y2 = y0 - float(j2) + 2.0*g3;
	float z2 = z0 - float(k2) + 2.0*g3;
	float x3 = x0 - 1.0 + 3.0*g3; 
	float y3 = y0 - 1.0 + 3.0*g3;
	float z3 = z0 - 1.0 + 3.0*g3;	

	vec3 ijk0 = vec3(i,j,k);
	vec3 ijk1 = vec3(i+i1,j+j1,k+k1);	
	vec3 ijk2 = vec3(i+i2,j+j2,k+k2);
	vec3 ijk3 = vec3(i+1,j+1,k+1);	

	vec3 gr0 = normalize(vec3(noise3D(ijk0),noise3D(ijk0*2.01),noise3D(ijk0*2.02)));
	vec3 gr1 = normalize(vec3(noise3D(ijk1),noise3D(ijk1*2.01),noise3D(ijk1*2.02)));
	vec3 gr2 = normalize(vec3(noise3D(ijk2),noise3D(ijk2*2.01),noise3D(ijk2*2.02)));
	vec3 gr3 = normalize(vec3(noise3D(ijk3),noise3D(ijk3*2.01),noise3D(ijk3*2.02)));

	float n0 = 0.0;
	float n1 = 0.0;
	float n2 = 0.0;
	float n3 = 0.0;

	float t0 = 0.5 - x0*x0 - y0*y0 - z0*z0;
	if(t0>=0.0)
	{
		t0*=t0;
		n0 = t0 * t0 * dot(gr0, vec3(x0, y0, z0));
	}
	float t1 = 0.5 - x1*x1 - y1*y1 - z1*z1;
	if(t1>=0.0)
	{
		t1*=t1;
		n1 = t1 * t1 * dot(gr1, vec3(x1, y1, z1));
	}
	float t2 = 0.5 - x2*x2 - y2*y2 - z2*z2;
	if(t2>=0.0)
	{
		t2 *= t2;
		n2 = t2 * t2 * dot(gr2, vec3(x2, y2, z2));
	}
	float t3 = 0.5 - x3*x3 - y3*y3 - z3*z3;
	if(t3>=0.0)
	{
		t3 *= t3;
		n3 = t3 * t3 * dot(gr3, vec3(x3, y3, z3));
	}
	return 96.0*(n0+n1+n2+n3);

}

float fbm(vec3 p)
{
	float f;
    f  = 0.50000*simplex3D( p ); p = p*2.01;
    f += 0.25000*simplex3D( p ); p = p*2.02; //from iq
    f += 0.12500*simplex3D( p ); p = p*2.03;
    f += 0.06250*simplex3D( p ); p = p*2.04;
    f += 0.03125*simplex3D( p );
	return f;
}

mat3 rot(vec3 ang)
{
	mat3 x = mat3(1.0,0.0,0.0,0.0,cos(ang.x),-sin(ang.x),0.0,sin(ang.x),cos(ang.x));
	mat3 y = mat3(cos(ang.y),0.0,sin(ang.y),0.0,1.0,0.0,-sin(ang.y),0.0,cos(ang.y));
	mat3 z = mat3(cos(ang.z),-sin(ang.z),0.0,sin(ang.z),cos(ang.z),0.0,0.0,0.0,1.0);
	return x*y*z;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy/iResolution.xy;
	vec2 p = uv*2.0-1.0;
	p.x*=1.78;
    p.x*=2.0;
    p.x*=uv.y*uv.y+2.0;
    p.x*=pow(uv.y,10.0*uv.y)+0.8;
	p.y-=1.0;
	uv.y-=0.2;
	float time = iTime*0.25;
	p.x+=fbm(vec3(time*0.1,uv)*0.5)*0.5;
	vec3 ro = vec3(0.0,-time*4.0,-2.0);
	vec3 rd = normalize(vec3(vec2(p.x,p.y-time*4.0),0.0)-ro);
	vec3 lp = vec3(0.0,-time*4.0-1.5,0.0);
	vec3 col = vec3(0.02, 0.005,0.001);
	if(length(p)<2.5) // don't waste resources on pixels that are behind the mask
	{
		for(int i = 0; i<5; i++)
		{	
			vec3 rp = ro+rd*float(i);
			float c = distance(rp,lp);
			float n = fbm(rp)*0.5+0.5;
			col = col*(10.0*n/c)*(fbm(vec3(time*0.1))*0.5+1.0);

		}
	}		//some color masks with fbm
	col*=smoothstep(1.0,-1.0,uv.y-smoothstep(0.0,1.0,fbm(vec3(vec2(p.x,uv.y*1.0-time*2.0),time)*1.0)*2.0));
	col*=smoothstep(1.0,-1.0,uv.y-smoothstep(0.0,1.0,fbm(vec3(vec2(p.x+100.0,uv.y*1.0-time*1.5),time)*2.0)*2.0));
	col*=smoothstep(1.0,-1.0,uv.y-smoothstep(0.0,4.0,fbm(vec3(vec2(p.x+200.0,uv.y*1.0-time*1.0),time)*2.0)*2.0));
	col*=smoothstep(2.5,0.0,length(p)); //mask the edges
	col*=vec3(1.0,1.0,100.0*smoothstep(0.0,-1.0,uv.y-p.x*p.x*0.1)); //blue hue bottom
	col = clamp(col, 0.0,1.0);

    col*=1.0+smoothstep(0.5,0.2,length((uv*2.0-1.0)*vec2(.75,1.0)+vec2(0.0,1.2)))*2.0; //stronger base for the flame

	fragColor = vec4(col,1.0);
}

//map screen space to world space
vec2 screen_map(vec2 uv, vec2 center, vec2 zoom) {
    return ((uv - vec2(0.5,0.5)) * zoom) - center;
}

//raise a complex number to an arbitrary power then add a constant
vec2 power_add(vec2 z, float power, vec2 c) {
    float r = length(z);
    float t = atan(z.y, z.x);
    return vec2(pow(r,power)*cos(power*t) + c.x, pow(r,power)*sin(power*t) + c.y);
}

//square a complex number just using components then add a constant
vec2 square_add(vec2 z, vec2 c) {
 	return vec2((z.x*z.x - z.y*z.y) + c.x, (2.0 * z.y * z.x) + c.y);
}

//calculate if the given point is in the mandelbrot set
float mandelbrot(vec2 c) {

 	const float power = 2.0;// + (0.1 * iTime);
   	const float limit = 2.0;// choose a limit of 2 becuase the point is guranteed to diverge once |z| > 2
    vec2 z = c;
    float escape = 0.0;

    for(int i = 0; i < 600; i++)	{

        z = square_add(z, c);    
        //z = power_add(z, power, c);

        if(length(z) > limit) {
            //add a fractional part to the escape time to make the colours smoother
            //based on the observation that the recursion formula is asymptopically just squaring
            escape =  float(i) - log(log(length(z))/ log(limit)) / log(power) ;//float(i);
            break;
        }
    }  
    return escape;
}

//average the pixel value over an area
float average_mandelbrot(vec2 c, float zoom) {
  vec2 p = zoom / (iResolution.xy * 1.0);  
 float val = 3.0*mandelbrot(c) + mandelbrot(c + p * vec2(1.0, 1.0)) + mandelbrot(c + p * vec2(1.0, -1.0))+ mandelbrot(c + p * vec2(-1.0, 1.0)) + mandelbrot(c + p * vec2(-1.0, -1.0));
 return val / 10.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //uv is a 2d vector represnting which pixel you're currently computing
	vec2 uv = fragCoord.xy / iResolution.xy;

    float zoom = exp(-iTime / 2.0);

    vec2 center = mix(vec2(0.6, 0.0), vec2(0.236835, 0.812488), clamp(1.0 - exp(-iTime), 0.0, 1.0));
    vec2 z = screen_map(uv, center, vec2(3.0,2.0) * zoom * 1.1);

    float escape = average_mandelbrot(z, zoom);
    //float escape = mandelbrot(z);

    float Pi = 3.14159;
    float x = escape / 50.0;
    vec4 orange = vec4(0.8, 0.2,0.1, 1.0);
    vec4 black = vec4(0.0, 0.0,0.0, 1.0);

    fragColor = mix(black, orange, x*1.0);

}

float n(float g) { return g * .5 + .5; }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
  vec2 p  = vec2(iResolution.x/iResolution.y, 1) * uv;
  float a = atan(p.y, p.x);
  float l = length(p);
  vec3  c = vec3(0);

  p = vec2(sin(a), cos(a))/l;

  for (int i = 0; i < 3; i++) {
    float mag = 0.0;
    float t = iTime;// + float(i) * 0.05;

    p.y += 0.425;

    mag += n(cos(p.y * 1.5 + t * 5.));
    mag += n(sin(p.x + t * 3.));
    mag += n(cos(p.x * p.y));
    mag *= 0.333;

    c[i] = mag;
  }

  fragColor = vec4(1.0 - pow(c, vec3(0.4545)), 1.0);
}

#define PI 3.141592653589793


// Radial Spectrum
void radialSpectrum( out vec4 fragColor, in vec2 fragCoord )
{
  float u_aspect = iResolution.x / iResolution.y;

  vec2 v_texcoord = fragCoord.xy / iResolution.xy;

  vec2 pos = vec2((v_texcoord.x * 2. - 1.) * u_aspect, v_texcoord.y * 2. - 1.);

  float theta = abs(atan(pos.x, pos.y)) / PI * .75;
  float amp = texture(iChannel0, vec2(theta, 0.25)).x;
  float dist = distance(pos, vec2(0., 0.));
  dist *= dist;

  vec3 c1 = vec3(0., 0., 0.);
  vec3 c2 = vec3(0., .5, .8);

  float fade = float(amp > dist) * (dist / amp);

  fragColor = vec4(mix(c1, c2, fade), 1.);
}


// Radial Spectrum Variation 2
void radialSpectrumVariationTwo( out vec4 fragColor, in vec2 fragCoord )
{
  float u_aspect = iResolution.x / iResolution.y;
  vec2 v_texcoord = fragCoord.xy / iResolution.xy;

  vec2 pos = v_texcoord * 2. - 1.;
  pos *= vec2(u_aspect, 1.);
  float theta = (atan(pos.y, pos.x) + PI) / (PI * 2.);
  float dist = length(pos);
  float amp = texture(iChannel0, vec2(dist, 0.25)).x;
  amp = sqrt(amp);

  float fade = float(amp > theta) * (theta / amp);

  fragColor = vec4(mix(
    vec3(0., .1, .1),
    vec3(0., 1., .8),
    fade
  ), 1.);
}


// Circluar Audio Spectrum
void circluarAudioSpectrum( out vec4 fragColor, in vec2 fragCoord )
{
  float u_aspect = iResolution.x / iResolution.y;
  vec2 v_texcoord = fragCoord.xy / iResolution.xy;
  vec2 u_mouse = iMouse.xy / iResolution.xy;

  vec2 center = vec2(.5, .5); 

  vec2 pos = vec2(v_texcoord.x*u_aspect, v_texcoord.y);
  pos.x-=1.0;

  float freq = texture(iChannel0, v_texcoord).x;

  vec3 color = vec3(freq > v_texcoord.y) * (v_texcoord.y / freq);

  float d = distance(pos, center); 
  float radiusSize = texture(iChannel0, vec2(.1,0)).x; 
  if(d > radiusSize && d < (radiusSize+.1))
  {
    fragColor = vec4(d, 1.0-u_mouse.x, 1.0-u_mouse.y, 1.0);
  }
  else
  {
    d=d*d*d;
    fragColor = vec4((1.0-freq)*d, (1.0-u_mouse.x)*d, (1.0-u_mouse.y)*d, 1.0); 
  }
}


// Circluar Strip Reactor
void circluarStripReactor( out vec4 fragColor, in vec2 fragCoord )
{
  float u_aspect = iResolution.x / iResolution.y;
  vec2 v_texcoord = fragCoord.xy / iResolution.xy;
  vec2 u_mouse = iMouse.xy / iResolution.xy;
  float u_time = iTime;

  vec2 center = vec2(.5, .5); 

  vec2 pos = vec2(v_texcoord.x*u_aspect, v_texcoord.y);
  //  pos.x-=1.0;
  vec2 scaledMouse = vec2(u_mouse.x*u_aspect, u_mouse.y);

  float freq = texture(iChannel0, v_texcoord).x;

  vec3 color = vec3(freq > v_texcoord.y) * (v_texcoord.y / freq);

  float d = distance(pos, scaledMouse); 
  float radiusSize = texture(iChannel0, vec2(.1,0)).x; 
  if(d > radiusSize && d < (radiusSize+abs(sin(u_time))))
  {
     fragColor = vec4(d, 1.0-u_mouse.x, 1.0-u_mouse.y, 1.0);
  }
  else
  {
    d=d*d*d;
    float id = .5*exp(1.-d);
    d = 0.41; 
    fragColor = vec4(id*d, id*d, id*d, 1.0); 
  }
}

// Reza Glowies
void rezaGlowies( out vec4 fragColor, in vec2 fragCoord )
{
  float u_aspect = iResolution.x / iResolution.y;
  vec2 v_texcoord = fragCoord.xy / iResolution.xy;
  vec2 u_mouse = iMouse.xy / iResolution.xy;
  float u_time = iTime;

  vec2 center = vec2(.5, .5); 
  vec2 pos = vec2(v_texcoord.x*u_aspect, v_texcoord.y);
  pos.x+=-1.0;
  vec2 scaledMouse = vec2(u_mouse.x*u_aspect, u_mouse.y);

