  vec2 u_mouse = iMouse.xy / iResolution.xy;
  float u_time = iTime;

  vec2 pos = v_texcoord * 2. - 1.;
  pos *= vec2(u_aspect, 1.);

  float theta = abs(atan(pos.x, pos.y) / PI);

  float dist = length(pos);
  dist -= distance(pos, vec2(-.5, .5));
  dist -= distance(pos, vec2(.5, .5));
  dist += distance(pos, vec2(-.5, -.5));
  dist += distance(pos, vec2(.5, -.5));
  dist = sqrt(dist) / 2.;

  float amp = texture(iChannel0, vec2(dist, 0.)).x;
  amp = sqrt(amp);

  float fade = float(amp > theta && amp < theta * 1.4) * (theta / amp);

  fragColor = vec4(mix(
    vec3(0., .1, .1),
    vec3(0., 1., .8),
    fade
  ), 1.);
}


