float fbm(vec2 p)
{
    p /= 50.;
    mat2 m = mat2(.8, -.6, .6, .8); 
    float amp = .5;

    float f=0.;
    for (int i = 0; i < 6; i++)
    {
        f += amp * texture(iChannel0, p).r;
        p *= m * 2.;
        amp *= .5;
    }

    return f;
}


float udBox( vec3 p, vec3 b )
{
  mat3 rot;

  p = p - vec3(0,2.,0.);

  float speed = 8.;
  float st = sin(time*speed);
  float ct = cos(time*speed);

  rot[0] = vec3(st,ct,0.);
  rot[2] = vec3(0.,0.,1.);
  rot[1] = vec3(ct,-st,0.);

  p = rot*p;

  return length(max(abs(p)-b,0.0));
}

