float sdCappedCone( in vec3 p, in vec3 c )
{
    vec2 q = vec2( length(p.xz), p.y );
    vec2 v = vec2( c.z*c.y/c.x, -c.z );

    vec2 w = v - q;

    vec2 vv = vec2( dot(v,v), v.x*v.x );
    vec2 qv = vec2( dot(v,w), v.x*w.x );

    vec2 d = max(qv,0.0)*qv/vv;

    return sqrt( dot(w,w) - max(d.x,d.y) )* sign(max(q.y*v.x-q.x*v.y,w.y));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sphere(vec3 z, vec3 loc, float radius)
{
    return length(z-loc)-radius;
}

float de(vec3 z)
{

    z.x = mod(z.x,10.)-5.;
    z.z = mod(z.z,10.)-5.;

    float sphere1 = sphere(z, vec3(0.,sin(time*20.),0.), .5);
    float sphere2 = sphere(z, vec3(-1.,-1.+.5*cos(time*20.),-1.), .7);
    float flr = z.y+2.;

    mat_hit = int(step(0.,sphere1-sphere2));

    float dist = min(sphere1,sphere2);
    mat_hit += int(step(0.,dist-flr));
    dist = min(dist,udBox(z,vec3(1.45)));
    dist = min(dist,flr);

    return dist;
}

vec3 norm(vec3 p)
{
    float d = de(p);

    float x = d - de(vec3(p.x+EPS,p.y,p.z));
    float y = d - de(vec3(p.x,p.y+EPS,p.z));
    float z = d - de(vec3(p.x,p.y,p.z+EPS));

    return normalize(vec3(x,y,z));
}

float ao(vec3 p, vec3 n)
{
    n*=-0.2;
    float f = .8+de(p+n);
    return clamp(f,0.,1.);
}

float march(vec3 ro, vec3 rd, out bool hit, out vec3 pt, inout float d)
{
    hit = false;
    for (int i = 0; i < MAX_ITER; i++)
    {
        pt = ro+rd*d;
        float s = de(pt);
        d += STEP_FACTOR*s;

        if (s < EPS)
        {
            hit = true;
            return d;
        }
    }

    return 100.;
}

vec3 fogclr=vec3(.5,.6,.7);

vec3 light(vec3 col, vec3 normal)
{
    vec3 lpos = vec3(2.);
    vec3 ldir = normalize(vec3(-1.));

    return col*(clamp(dot(normal,ldir),0.,1.));

}

void fog(inout vec3 col, float d, float f)
{
    col = mix(col,fogclr, clamp(1.-exp(-d*d*.0105*2.5*f),.0,1.));
}

bool trace(vec3 ro, vec3 rd, inout vec3 col)
{
    vec3 orig_ro = ro;
    float d = 0.;
    int max_bounces = MAX_BOUNCES;

    float factor = 1.;
    factor = .95;

    bool any_hits = false;
    for (int bounce = 0; bounce < MAX_BOUNCES; bounce++)
    {
        factor *= factor;
        bool hit = false;
        vec3 pt;

        d = march(ro,rd,hit,pt,d);

        if (hit)
        {
            vec3 mat_color = normalize(vec3(float(mat_hit),float(mat_hit)+1.,float((mat_hit+1)*(mat_hit+1))));
            vec3 n = norm(pt);
            col += clamp(factor,0.,1.)*light(mat_color, n);
//            col *= ao(pt,n);

            col += clamp(1.-abs(dot(n,pt-orig_ro)),0.,1.);

            float f = fbm(orig_ro.xy);
            fog(col,d, f);
            //col = mix(col,fogclr,f);

            ro = pt;
            d = 0.01;
            rd = reflect(rd,n);
            any_hits = true;
        }
        else
        {
            break;
        }
    }

    return any_hits;
}



