// returns (dx, dy, distance)
vec3 worley(vec2 n,float s)
{
    vec3 ret = vec3(s * 10.);
    // look in 9 cells (n, plus 8 surrounding)
    for(int x = -1;x<2;x++)
    {
        for(int y = -1;y<2;y++)
        {
            vec2 xy = vec2(x,y);// xy can be thought of as both # of cells distance to n, and 
            vec2 cellIndex = floor(n/s) + xy;
            vec2 worleyPoint = rand(cellIndex);// random point in this cell (0-1)
            worleyPoint += xy - fract(n/s);// turn it into distance to n. ;
            float d = length(worleyPoint) * s;
            if(d < ret.z)
                ret = vec3(worleyPoint, d);
        }
    }
    return ret;
}


vec4 applyLighting(vec4 inpColor, vec2 uv, vec3 normal, vec3 LightPos, vec4 LightColor, vec4 AmbientColor)
{
    if(distance(uv.xy, LightPos.xy) < 0.01) return vec4(1.,0.,0.,1.);
    vec3 LightDir = vec3(LightPos.xy - uv, LightPos.z);
    float D = length(LightDir);// distance for attenuation
    vec3 N = normalize(normal);
    vec3 L = normalize(LightDir);
    vec3 Diffuse = (LightColor.rgb * LightColor.a) * max(dot(N, L), 0.0);
    vec3 Ambient = AmbientColor.rgb * AmbientColor.a;
    vec3 Intensity = Ambient + Diffuse;
    vec3 FinalColor = inpColor.rgb * Intensity;
    return vec4(FinalColor, inpColor.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scrollOffset = vec2(iTime * iResolution.x / 17., 0.0);
    vec2 uv = fragCoord.xy + scrollOffset;

    vec2 lightPosXY = vec2(nsin(iTime * 1.66), nsin(iTime * 1.99));
    lightPosXY = (lightPosXY * 0.75) + 0.125;// not so big movement
    lightPosXY *= iResolution.xy;
    lightPosXY += scrollOffset;
    if(iMouse.z > 0.)
	    lightPosXY = iMouse.xy + scrollOffset;

    fragColor = vec4(vec3(0.5), 1.0);// background

    // compute the normal for this fragment by subdividing.
    float wsize = iResolution.x;
    vec2 normal = vec2(0.);
    float influenceFactor = 1.0;

    float colorMix = 1.0;

    for(int i = 0; i < iterationCount; ++ i)
    {
        vec3 w = worley(uv, wsize);
		normal.xy += influenceFactor * w.xy;
        wsize *= iterationSizeFactor;
        influenceFactor *= iterationNormalInfluenceFactor;

        float tintAmt = w.z / wsize;

        // i don't really understand why this looks so much better at low size factors.
        if(iterationSizeFactor < 0.5)
	        tintAmt = tintAmt * iterationSizeFactor;

#if COLOR_MODE == 1
        fragColor = vec4(mix(fragColor.rgb, getPalette(i), tintAmt * colorMix), 1.0);
#endif
#if COLOR_MODE == 2
        fragColor = vec4(vec3(tintAmt), 1.0);
#endif
        colorMix *= iterationColorInfluenceFactor;
    }

    // lighting
#if COLOR_MODE != 2
    vec3 lightPos = vec3(lightPosXY, iResolution.x / 2.);
    vec4 lightColor = vec4(0.95,0.9,0.9,1.);
    vec4 ambientColor = vec4(0.5,0.5,0.9,1.);
	fragColor = applyLighting(fragColor, uv, vec3(normal, 2.5), lightPos, lightColor, ambientColor);

    // show light position.
    if(distance(uv, lightPosXY) < iResolution.x / 75.)
        fragColor = vec4(1.0,1.0,0.,1.0);
#endif
}

float k;
float centerBlack;
float depth;
float rotation;

vec2 p;
vec2 uv;
vec3 textColor;

const float pi = 3.1416;
const float pi2 = 3.1416 * 2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    k = iTime / 1.8;
	uv = fragCoord.xy / iResolution.xy;
    p = uv - .5;

    float dfc = distance(vec2(.5), uv);

    depth = 1.0 / dfc ;
    rotation = atan(p.y, p.x) / pi + sin(k / 4.0) + depth * cos(k * 0.3) * 0.2;

    centerBlack = (1.00 - cos(dfc * pi2)) * 0.1;

    vec2 pos = vec2(
            mod(depth + k * 8.0, pi2), 
        	rotation 
    );

    float x = pos.x;
    float y = pos.y;

    float r = (25.0 + (sin(x - 0.5) + sin(y * pi) * 18.0) - sin(x * pi) * 19.0) * 0.3;
	float g = 1.0 + (sin(x + 0.6) * sin(y * pi ) );
	float b = 5.0 + (sin(x * 0.7) + sin(y * pi) * 5.0);

    textColor = vec3(r, b / 3.0, b) * vec3(b, r, g) / 5.0 ;

    fragColor = vec4(textColor, 1.0) * centerBlack;
}

//(if mousex+y>0...) 
#define time iTime*.5
#define resolution iResolution.xy

vec3 ldir;
float ot;

float tree(vec2 p) {
	p=p*.72+vec2(0.,1.32);
	ot=1000.;
	for (int i=0; i<28; i++) {
		float l=dot(p,p);
		ot=min(ot,abs(l-.6));
		p.x=abs(p.x);
		p=p/l*2.-vec2(.38,.2);

	}
	return dot(p,p)*.02;
}

float light(vec2 p) {
	vec2 d=vec2(0.,.003);
	float d1=tree(p-d.xy)-tree(p+d.xy);
	float d2=tree(p-d.yx)-tree(p+d.yx);	
  	vec3 n1=vec3(0.,d.y,d1);
  	vec3 n2=vec3(d.y,0.,d2);
  	vec3 n=normalize(cross(n1,n2));
  	float diff=max(0.,dot(ldir,n))*.6;
	vec3 r=reflect(vec3(0.,0.,1.),ldir);
	float spec=pow(max(0.,dot(r,n)),25.)*.4;
  	return (diff+spec+.15)*max(0.4,1.-tree(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = gl_FragCoord.xy/resolution.xy-.5;
	vec2 aspect=vec2(resolution.x/resolution.y,1.);
    p*=aspect;
  	if (iMouse.z>0.) p+=3.*(iMouse.xy/iResolution.xy-.5);
    p*=1.+sin(time)*.2;
    float a=2.+cos(time*.3)*.5;
    mat2 rot=mat2(sin(a),cos(a),-cos(a),sin(a));
    p*=rot;
    p+=vec2(sin(time),cos(time))*.2;
	vec3 lightpos=vec3(sin(time*3.)*.8,cos(time)*.9,-1.);
	lightpos.xy*=aspect*.5;
	ldir=normalize(vec3(p,-tree(p))+lightpos);
  	float l=light(p);
  	ot=max(1.-ot*.7,0.);
	vec3 col=l*vec3(ot*ot*1.45,ot*.9,ot*ot*.55);
	col+=pow(max(0.,.2-length(p+lightpos.xy))/.2,3.)*vec3(1.2,1.1,1.);
	col*=pow(max(0.,1.-length(p+lightpos.xy)*.3),2.5);
	fragColor = vec4(col+.03, 1.0 );
}

float rand( vec2 p )
{
	return fract(sin(dot(p.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
float noise(vec2 _v, vec2 _freq)
{
	float fl1 = rand(floor(_v * _freq));
	float fl2 = rand(floor(_v * _freq) + vec2(1.0, 0.0));
	float fl3 = rand(floor(_v * _freq) + vec2(0.0, 1.0));
	float fl4 = rand(floor(_v * _freq) + vec2(1.0, 1.0));
	vec2 fr = fract(_v * _freq);

	// linear interpolate
	float r1 = mix(fl1, fl2, fr.x);
	float r2 = mix(fl3, fl4, fr.x);
	return mix(r1, r2, fr.y);
}

float perlin_noise(vec2 _pos, float _freq_start, float _amp_start, float _amp_ratio)
{
	float freq = _freq_start;
	float amp = _amp_start;
	float pn = noise(_pos, vec2(freq, freq)) * amp;
	for(int i=0; i<4; i++)
	{
		freq *= 2.0;
		amp *= _amp_ratio;
		pn += (noise(_pos, vec2(freq, freq)) * 2.0 - 1.0) * amp;
	}
	return pn;
}


