float rmf(vec3 p)
{
    float signal = 0.0;
    float value  = 0.0;
    float weight = 1.0;
    float h = 1.0;
    float f = 1.0;

    for (int curOctave=0; curOctave < 11; curOctave++) 
    {
        signal = noise(p)*2.0-0.4;
        signal = pow(1.0 - abs(signal), 2.0) * weight;
        weight = clamp(0.0, 1.0, signal * 16.0);
        value += (signal * pow(f, -1.0));
        f *= 2.0;
        p *= 2.0;
    }

    return (value * 1.25) - 1.0;
}

const int Iterations=15;

const float Scale=2.1;

float de(vec3 pos) 
{
	vec3 p=pos;
    p.y +=4.76-iTime*0.005;
	p.xz=abs(.5-mod(pos.xz,1.))+.01;
	float DEfactor=1.;
	float ot=1000.;
	for (int i=0; i<Iterations; i++) 
    {
		p = abs(p)-vec3(0.,2.,0.);  
		float r2 = dot(p, p);
		ot = min(ot,abs(length(p)));
		float sc=Scale/clamp(r2,0.4,1.);
		p*=sc; 
		DEfactor*=sc;
		p = p - vec3(0.5,1.,0.5);
	}
    float rr=length(pos+vec3(0.,-3.03,1.85))-.017;
    float d=length(p)/DEfactor-.0005;

    return d;
}

float sdf(vec3 p)
{
	float sd = p.y;


	sd = smin(sd, de(p), 60.0);
    //sd = min(sd, length(p)-1.0-rmf(p+time*0.01, 8.0, 1.8, coneR)*0.06);
    sd -= rmf(p*8.0+2.0)*0.015;       // ceiling

    return sd;
}

vec4 getColorAndRoughness(vec3 p, vec3 N, float ambo)
{
    return vec4(1.0);
}

vec3 grad(vec3 p, float coneR)
{
    coneR*=3.0;
    vec3 f = vec3(sdf(p));
    vec3 g = vec3(sdf(p+vec3(coneR, 0.0, 0.0)),
                  sdf(p+vec3(0.0, coneR, 0.0)),
                  sdf(p+vec3(0.0, 0.0, coneR)));
    return (g-f) / coneR;
}

vec3 setupRayDirection(float camFov)
{
	vec2 coord = vec2(gl_FragCoord.xy);
    vec2 v = vec2(coord / iResolution.xy) * 2.0 - 1.0;
    float camAspect = iResolution.x/iResolution.y;
    float fov_y_scale = tan(camFov/2.0);
    vec3 raydir = vec3(v.x*fov_y_scale*camAspect, v.y*fov_y_scale, -1.0);
    return normalize(raydir);
}

float ambientOcclusion( in vec3 pos, in vec3 nor, float coneR )
{
  float occ = 0.0;
  float sca = 1.1;
  for ( int i=0; i<5; i++ )
  {
    float hr = 0.02 + 0.11*float(i) / 4.0;
    vec3 aopos =  nor * hr + pos;
    float dd = sdf(aopos);
    occ += -(dd-hr)*sca;
    sca *= 0.95;
  }
  return clamp( 1.0 - 1.5*occ, 0.0, 1.0 );
}


vec3 shade(vec3 ro, vec3 rd, float t, float coneR, vec3 ld)
{
    vec3 g = grad(ro+rd*t, coneR);
    vec3 N = normalize(g);
    float ambo = ambientOcclusion(ro+rd*t, N, coneR);
    float ndl = clamp(dot(N, ld), 0.0, 1.0);
    vec3 color = mix(vec3(0.782, 0.569, 0.45), vec3(0.8, 0.678, 0.569), clamp(1.5-length(g), 0.0, 1.0));
    return vec3(mix(ambo*color, ambo*color*ndl, 0.5));
}

float fogmap(in vec3 p, in float d)
{
    vec3 q = p;
    q.z-=0.0;
    p.x -= iTime*0.05;
    vec3 turb = vec3(noise(80.0*p.xyz+iTime*0.51)*0.5, noise(160.0*p.xzy+iTime*0.2)*0.3, noise(60.0*p.zyx+iTime*0.1)*0.2);
    p += turb;
    float fog = (max(noise(p*64.0+0.1)-0.1, 0.0)*noise(p*16.0))*0.03;

    return fog;
}


#define iterations 14
#define formuparam 0.530

#define volsteps 3
#define stepsize 0.2

#define zoom   1.20
#define tile   0.850
#define speed  0.1

#define brightness 0.0015
#define darkmatter 0.400
#define distfading 0.160
#define saturation 0.400

vec3 space(vec2 uv)
{
    vec3 v=vec3(0.4);
    vec3 dir=vec3(uv*zoom,1.);

	//float a2=2.0;
	float a1=4.0;//+time*0.001;
	mat2 rot1=mat2(cos(a1),sin(a1),-sin(a1),cos(a1));
	mat2 rot2=rot1;//mat2(cos(a2),sin(a2),-sin(a2),cos(a2));
	dir.xz*=rot1;
	dir.xy*=rot2;

	//from.x-=time;
	//mouse movement
	vec3 from=vec3(170.0, 170.2,0.01);
	from+=vec3(0.0,0.0,-2.);

	//from.x-=iMouse.x;
	//from.y-=iMouse.y;

	from.xz*=rot1;
	from.xy*=rot2;

	//volumetric rendering
	float s=.4,fade=.2;


    for (int r=0; r<volsteps; r++) 
    {
        vec3 q = vec3(0.3, 0.5, -3.0)+s*dir;

		vec3 p=from+s*dir*.5;
		p = abs(vec3(tile)-mod(p,vec3(tile*2.))); // tiling fold
		float pa,a=pa=0.;
		for (int i=0; i<iterations; i++) 
        { 
			p=abs(p)/dot(p,p)-formuparam; // the magic formula
			a+=abs(length(p)-pa); // absolute sum of average change
			pa=length(p);
		}

		float dm=max(0.,darkmatter-a*a*.001); //dark matter
		a*=a*a*2.; // add contrast
		if (r>3) fade*=1.-dm; // dark matter, don't render near
		//v+=vec3(dm,dm*.5,0.);
		v+=fade;
		v+=vec3(s,s*s,s*s*s*s)*a*brightness*fade; // coloring based on distance
		fade*=distfading; // distance fading
		s+=stepsize;

	}
	v=mix(vec3(length(v)),v,saturation); //color adjust
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;

    vec3 lightDir = normalize(vec3(0.0, 0.4, -0.3));
    //float v = mouse.y;
	vec3 rayOrigin = vec3(0.0, 0.01, 0.7);
    mat3 rm = rotationMatrix(vec3(0.0, 0.8, -0.15), -2.1);
    vec3 rayDir = rm*setupRayDirection(radians(60.0));


    float travel = 0.0;

    vec3 sp = vec3(0.0);
    float coneR;
    vec3 col = space(p)*0.01;
    float fog = 0.0;

    for (int i=0; i<256; i++)
    {

        coneR = travel * tan(0.5*radians(60.0)/iResolution.y);
        float sd = sdf(rayOrigin+rayDir*travel);

        if (sd < coneR)
        {

            col = shade(rayOrigin, rayDir, travel, coneR, lightDir);
            col = mix(col, vec3(0.4, 0.4, 0.5), smoothstep(3.0, 4.0, travel));
            break;
        }

        fog += fogmap(rayOrigin+rayDir*travel, travel);
        if (travel > 4.0 )
        {
            col = mix(col, vec3(0.4, 0.4, 0.5), smoothstep(1.0, 0.0, p.y));
            break;
        }

        travel += min(sd + travel * .001, 8.0);


    }

    fog = min(fog, 1.0);



    col = mix(col, vec3(0.97, .95, .9), fog);
    col = pow(col, vec3(1.2));

    vec2 q = fragCoord.xy/iResolution.xy;
    col *= pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.1);

    fragColor = vec4(col, 1.0);



}

float box( vec3 p, vec3 s )
{
  return length(max(abs(p)-s,0.0))-.1;
}
float noise(vec3 p) //Thx to Las^Mercury
{
	vec3 i = floor(p);
	vec4 a = dot(i, vec3(1., 57., 21.)) + vec4(0., 57., 21., 78.);
	vec3 f = cos((p-i)*acos(-1.))*(-.5)+.5;
	a = mix(sin(cos(a)*a),sin(cos(1.+a)*(1.+a)), f.x);
	a.xy = mix(a.xz, a.yw, f.y);
	return mix(a.x, a.y, f.z);
}

float sphere(vec3 p, vec4 spr)
{
	return length(spr.xyz-p) - spr.w;
}

float flame(vec3 p)
{
	float d = sphere(p*vec3(1.,.5,1.), vec4(3.,2.,.0,1.));
	return d + (noise(p+vec3(.0,-iTime*4.,.0)) + noise(p*3.)*.5)*.15*(p.y-2.) ;
}

float map( in vec3 p )
{
	p.x -= 17./2.;
	float d = 999999.;
	vec3 pp = p-vec3(17./2.,0.,0.);
	pp.xy = mod(p.xy,vec2(1.))-.5;
	d = min(d, box(pp,vec3(.3,.3,1.)) );
	d = max(d, p.x);
	d = max(d, -p.x-17.);
	d = max(d, p.y-4.);
	d = max(d, -p.y);
	d = max(d, -box(p+vec3(16.5,-3.5,0.), vec3(.4,.4,2.)) );
	d = max(d, -box(p+vec3(14.5,-3.5,0.), vec3(.4,.4,2.)) );
	d = max(d, -box(p+vec3(11.5,-3.5,0.), vec3(1.4,.4,2.)) );
	d = max(d, -box(p+vec3(5.5,-3.5,0.), vec3(2.4,.4,2.)) );
	d = max(d, -box(p+vec3(0.5,-3.5,0.), vec3(.4,.4,2.)) );
	d = max(d, -box(p+vec3(15.5,-1.5,0.), vec3(.4,1.4,2.)) );
	d = max(d, -box(p+vec3(13.5,-1.5,0.), vec3(.4,1.4,2.)) );
	d = max(d, -box(p+vec3(11.5,-1.5,0.), vec3(.4,1.4,2.)) );
	d = max(d, -box(p+vec3(6.5,-1.5,0.), vec3(.4,1.4,2.)) );
	d = max(d, -box(p+vec3(4.5,-1.5,0.), vec3(.4,1.4,2.)) );
	d = max(d, -box(p+vec3(9.,-.5,0.), vec3(.9,.4,2.)) );
	d = max(d, -box(p+vec3(9.,-2.5,0.), vec3(.9,.4,2.)) );
	d = max(d, -box(p+vec3(2.,-1.5,0.), vec3(.9,1.4,3.)) );
	d = max(d, -box(p+vec3(5.,-2.5,0.), vec3(.9,.4,3.)) );
	d = min(d, (flame(p+vec3(17./2.,0.,0.))));
	return d;
}

vec3 raymarch(in vec3 org, in vec3 dir)
{
	float d = 0.0, glow = 0.0, eps = 0.02;
	vec3  p = org;

	for(int i=0; i<64; i++)
	{
		d = map(p) + eps;
		p += d * dir;
		if( d<eps )
			break;
	}
	return p;
}
vec3 normal(in vec3 p)
{
    vec3 eps = vec3(0.01,0.0,0.0);
    return normalize(vec3(
        map(p+eps.xyy)-map(p-eps.xyy),
        map(p+eps.yxy)-map(p-eps.yxy),
        map(p+eps.yyx)-map(p-eps.yyx)
    ));
}
float ambiantOcclusion( in vec3 p, in vec3 n, in float d)
{
    float dlt = 0.1;
    float oc = 1.0;

    for(int i=1; i<=6; i++)
    {
		float dist = abs(map(p+n*dlt));
		dlt += dist;
		oc += map(p+n*dlt)+dist;
    }
    oc /= 6.;

    return 1. - exp(-oc*d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 v = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
	v.x *= iResolution.x/iResolution.y;
	vec3 col = vec3(0.);
	vec3 org = vec3(0.,2.5,8.);
	vec3 dir = normalize( vec3( v.xy, -1.5+length(v)*.25 ) );


	vec3 p = raymarch(org,dir);
	vec3 n = normal(p);
	col = vec3(0.) ;
	float f = flame(p);
	if(f<.1)
	{
		col = vec3(1.,.5,.1);
	}
	else if(map(p)<.1)
	{
		col += ambiantOcclusion(p,-dir,1.5);
		col *= ambiantOcclusion(p,n,1.5);
		col += vec3(1.,.5,.1) / (.5+pow(f,2.));
	}
	fragColor = vec4(col*min(iTime*.25,1.), 1.);
}

