	return fract( sin( q ) * 43758.5453 );
}

float noise( vec2 point )
{
	vec2 p = floor( point );
	vec2 f = fract( point );
	return mix(
		mix( random( p + vec2( 0.0, 0.0 ) ), random( p + vec2( 1.0, 0.0 ) ), f.x ),
		mix( random( p + vec2( 0.0, 1.0 ) ), random( p + vec2( 1.0, 1.0 ) ), f.x ),
		f.y
	);
}

float fractal( vec2 point )
{
    float sum = 0.0;
    float scale = 0.5;
    for ( int i = 0; i < 5; i++ )
	{
		sum += noise( point ) * scale;
		point *= 2.0;
        scale /= 2.0;
	}

	return sum;
}

