}

vec2 infiniteSpheres(vec3 p, vec3 c){
    vec3 q;
	q.x = mod(p.x,c.x)-0.5*c.x;
    q.y = mod(p.y,c.y)-0.5*c.y;
    q.z = mod(p.z,c.z)-0.5*c.z;
	return dSphere(q);
}

vec2 dist(vec3 p){
