}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 vPos = -1.0+2.0*q;

    vec3 camUp = vec3(0,
                      2.0*cos(iTime*0.1),
                      -2.0*sin(iTime*0.1)); 

    vec3 camPos = vec3(iTime*15.0+8.0*cos(iTime),
                       3.5,
                       3.5);

  	vec3 camLook = vec3(camPos.x-sin(iTime*0.5),
						camPos.y+0.2*cos(iTime*0.5),
                        camPos.z+1.0);

    vec3 vpn = normalize(camLook-camPos);
  	vec3 u = normalize(cross(camUp,vpn));
  	vec3 v = cross(vpn,u);
	vec3 vcv = camPos+vpn;
  	vec3 scrCoord = vcv+vPos.x*u*iResolution.x/iResolution.y+vPos.y*v;
  	vec3 scp = normalize(scrCoord-camPos);

  	const vec3 e = vec3(0.0001,0,0);
  	const float maxDepth = 128.0;
  	vec2 d = vec2(0.02,0.0);
  	vec3 p;

  	float f = 1.0;
  	for(int i=0; i<128; i++){
    	if ((abs(d.x) < .001) || (f > maxDepth)) 
      		break;

    	f += d.x;
    	p = camPos+scp*f;
    	d = dist(p);
