#define NOISE
#define NOISE_AMOUNT .014

const vec4 yin = vec4(1.);
const vec4 yang = vec4(0.,0.,0.,1.);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ctime = cos(iTime);

	vec3 rd = vec3(fragCoord.xy/iResolution.xy-vec2(.5), .5);
    rd.y *= iResolution.y/iResolution.x;
    rd = normalize(rd);

    vec3 rp = vec3(0.,0.,-5.);


    float f1,f2;
    vec4 cb = vec4(0.);
    vec4 c = vec4(0.);
    float d,db;
    for (int i = 0; i < 32; i++) {
        d = length(rp);
        db = max(3.-d,0.);

        f1 = fract((rp.x*rp.z+rp.y/(4.4+ctime*4.))*(.4+ctime*.3));
        f2 = 1.-f1;

        cb = (yin*f1 + yang*f2)*db;
        c = mix(c,cb,cb.w);

		rp += rd*d*.41;
        #ifdef NOISE
        rp += mod(vec3(f1,f2,f1*f2)*34783.8237324,vec3(NOISE_AMOUNT));
    	#endif
    }

    fragColor = mix(vec4(pow(rd.x,2.)),c,c.w);
}

