#define WOW 0.0

#define PI 3.14159265

float hash( vec2 _v ){
    return fract(sin(dot(_v,vec2(89.44,19.36)))*22189.22);
}

float iHash( vec2 _v, vec2 _r ){
    float h00 = hash( vec2( floor( _v*_r + vec2( 0.0, 0.0 ) ) / _r ) );
    float h10 = hash( vec2( floor( _v*_r + vec2( 1.0, 0.0 ) ) / _r ) );
    float h01 = hash( vec2( floor( _v*_r + vec2( 0.0, 1.0 ) ) / _r ) );
    float h11 = hash( vec2( floor( _v*_r + vec2( 1.0, 1.0 ) ) / _r ) );
    vec2 ip = vec2( smoothstep( vec2( 0.0, 0.0 ), vec2( 1.0, 1.0 ), mod( _v*_r, 1.0 ) ) );
    return ( h00 * ( 1.0 - ip.x ) + h10 * ip.x ) * ( 1.0 - ip.y ) + ( h01 * ( 1.0 - ip.x ) + h11 * ip.x ) * ip.y;
}

float noise( vec2 _v ){
    float sum = 0.;
    for( int i = 1; i < 9; i ++ )
    {
        sum += iHash( _v + vec2( i ), vec2( 2.0 * pow( 2.0, float( i ) ) ) ) / pow( 2.0, float( i ) );
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = vec2( fragCoord.xy * 2.0 - iResolution.xy ) / iResolution.x;

	float sum = 0.0;
	vec3 nor = vec3( 0.0 );
	for( float i = 0.0; i < PI * 1.9; i += PI * 0.25 ){
		vec2 gap = p - vec2( cos( i ), sin( i ) ) * 0.4 * ( 1.0 + WOW * sin( iTime * 4.0 ) * 0.5 );
		float strength = exp( -length( gap ) * 16.0 ) * 10.0;
		sum += strength;
		nor += vec3( gap, 0.1 ) * strength;
	}

	float meta = pow( clamp( sum, 0.0, 1.0 ), 100.0 );
	nor = normalize( nor );
	vec3 dif = ( clamp( dot( normalize( vec3( 1.0, 1.0, 1.0 ) ), nor ), 0.0, 1.0 ) + 0.1 ) * vec3( 0.9, 0.6, 0.3 );
	float sugar = clamp( abs( noise( p * 7.0 + 20.0 ) - 0.5 ) * 2.0, 0.0, 1.0 ) * 0.12 * sum;
	vec3 spe = pow( clamp( dot( normalize( vec3( 0.8, 0.9, 1.0 ) + vec3( 0.0, 0.0, 1.0 ) ), nor ) + sugar * 0.1, 0.0, 1.0 ), 30.0 ) * vec3( 0.2 );

	vec3 bg = ( vec3( 0.9, 0.0, 0.4 ) + clamp( sin( p.x * 50.0 + p.y * 40.0 + iTime * 8.0 ) * 10.5 - 4.0, 0.0, 1.0 ) * vec3( 0.0, 1.2, -0.3 ) * exp( -length( p*0.8 ) ) );

	fragColor = vec4( meta * ( dif + spe + sugar ) + ( 1.0 - meta ) * bg, 1.0 );
}

