struct Ray
{
    vec2 origin;
    vec2 end;
};

struct Circle
{
    vec2 position;
    float radius;
    vec3 color;
};

bool drawLine(vec2 position, Ray line, int thickness)
{
	return false;
}

float intersectCircle(Ray ray, Circle circle)
{
    float dist = distance(ray.origin, ray.end);

    vec2 d = ray.origin - ray.end;

    float a = dot(d, d);
    float b = 2.0 * dot(d, ray.origin - circle.position);
    float c = dot(circle.position, circle.position) + dot(ray.origin, ray.origin) - 2.0 * dot(circle.position, ray.origin) - circle.radius*circle.radius;

    float test = b*b - 4.0*a*c;

    if (test >= 0.0) 
    {
        dist = (-b - sqrt(test)) / (2.0 * a);
    }

    return dist;
}

bool intersectCircleShadow(Ray ray, Circle circle)
{
    bool inShadow = false;

    float intersection = intersectCircle(ray, circle);
    if(!(intersection > 0.0 || intersection < -1.0))
    {
        inShadow = true;
    }

    return inShadow;
}

float drawCircle(vec2 position, Circle circle)
{
    float xd = circle.position.x-position.x;
	float yd = circle.position.y-position.y;

    if((xd*xd + yd*yd) <= circle.radius * circle.radius)
    {
        return 1.0;
    }

    return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 rgb = vec3(0.1, 0.1, 0.1);
	vec2 origin = fragCoord.xy;

    float lightIntensity = 1.0;
    vec3 lightColor = vec3(0.0, 0.9, 0.6);

    Circle circle1, circle2, circle3;
    circle1.position = vec2(iResolution.x/4.0, iResolution.y/2.0);
    circle1.radius = 50.0;
    circle1.color = vec3(0.2, 0.2, 0.2);

    circle2.position = vec2(circle1.position.x / 2.0 + ((sin(iTime) +1.0) * 1.5) * circle1.position.x, circle1.position.y / 2.0 + ((sin(iTime * 2.0) * 0.5) + 0.5) * circle1.position.y);
    //circle2.position = vec2(iMouse.x, iMouse.y);
    circle2.radius = 10.0;
    circle2.color = vec3(1.0, 1.0, 1.0);

    circle3.position = vec2(iResolution.x - iResolution.x/4.0, iResolution.y/2.0);
    circle3.radius = 50.0;
    circle3.color = vec3(0.2, 0.2, 0.2);

    rgb += drawCircle(origin, circle1) * circle1.color; // First object
    rgb += drawCircle(origin, circle2) * circle2.color; // First light
    rgb += drawCircle(origin, circle3) * circle3.color; // Second light

    const float samples = 20.0; // TODO: include in circle
    Ray ray;
    ray.origin = origin;
    ray.end = circle2.position;

    // Light 
    for(float i = 1.0; i <= samples; ++i)
    {
        bool inShadow = false;

        // Resample
        // Perendicular to the surface of the light
        vec2 vec = normalize(ray.end - ray.origin);
        vec2 sampleVec = vec2(vec.y, -vec.x);
        vec2 resample = sampleVec * (i - samples/2.0);
        ray.end = circle2.position + resample;

        // FOR EACH OBJECT
        {
            inShadow = intersectCircleShadow(ray, circle1) || intersectCircleShadow(ray, circle3);
        }

        if(!inShadow)
        {
            float dist = distance(ray.origin, ray.end);
            rgb += lightColor / dist * lightIntensity;
        }
    }

	fragColor = vec4(rgb, 1.0);
}

/* Victor Franco (resonance) */

#define FAR 50.0
#define EPS 0.001
#define STEPS 256

#define PI    3.14159265359
#define TWOPI 6.28318530718

struct Inter {
	int id;
	float dist;
};

Inter iCilindroZ(int id, vec3 p, float s) {
 	return Inter (id, length(p.xy) - s);   
}

Inter iSphere(int id, vec3 p, float s) {
 	return Inter (id, length(p) - s);
}

Inter iCube(int id, vec3 p, float s) {
 	return Inter(id, length(max(abs(p) - s, 0.0)));   
}

Inter opU(in Inter a, in Inter b) {
 	return a.dist > b.dist ? b : a;   
}

Inter opS(in Inter a, in Inter b) {
	b.dist = -b.dist;
    return a.dist > b.dist ? a : b;
}

Inter map(vec3 p) {
    vec3 pCubos  = p;
    vec3 pCubos2 = p;

    float t  = iTime * 0.075 + 0.5 * TWOPI * pCubos.z/FAR;    
    float t2 = iTime * 0.075 + 0.5 * TWOPI * pCubos.x/FAR;

    pCubos.xy += 18.0 * vec2(cos(t), sin(t));    
    pCubos.xy = mod(pCubos.xy, 10.0) - 5.0;
    pCubos.z  = mod(pCubos.z,  1.25) - 0.625;

    pCubos2.xy += 18.0 * vec2(cos(t), sin(t));    
    pCubos2.zy = mod(pCubos2.zy, 10.0) - 5.0;
    pCubos2.x  = mod(pCubos2.x,  1.25) - 0.625;

    vec3 pEsfera = p;
    pEsfera.z += -5.2;

 	return opU(
        iSphere(1, pEsfera, 1.0),
        opS(opU(iCube(2, pCubos,  1.0),
                iCube(2, pCubos2, 1.0)),
        	iSphere(3, pEsfera, 1.8)
        	));
}

vec3 colorize(Inter inter) {
    if (inter.id == 1) return vec3(0.3, 0.4, 0.6);
    if (inter.id == 2) return vec3(0.4);
    if (inter.id == 3) return vec3(0.0);
    return vec3(1.0);
}

vec3 normals(vec3 p) {
 	vec2 e = vec2(0.01, 0.0);
    return normalize(vec3(
        map(p + e.xyy).dist - map(p - e.xyy).dist,
        map(p + e.yxy).dist - map(p - e.yxy).dist,
        map(p + e.yyx).dist - map(p - e.yyx).dist));
}

bool raymarch(out Inter inter, in vec3 ro, in vec3 rd) 
{
    Inter deltaInter;  // InterseÃ§Ã£o temporÃ¡ria do ponto de
                       // marching atÃ© o objeto

    float dist;        // DistÃ¢ncia real do 'ro' atÃ© o objeto

    // Intersecta
    for (int i = 0; i < STEPS; i++) {
        deltaInter = map(ro + dist * rd);

        // Caso passemos da distÃ¢ncia mÃ¡xima, para o loop
        if (dist > FAR)
            break;

        // Caso ocorra colisÃ£o, move as informaÃ§Ãµes da interseÃ§Ã£o
        // temporÃ¡ria para a interseÃ§Ã£o final e retorna verdadeiro
        if (deltaInter.dist < EPS)
        {
            inter.id   = deltaInter.id;
            inter.dist = dist;
            return true;
        }

        dist += deltaInter.dist;
    }

    // Retorna que nÃ£o foi possÃ­vel encontrar uma interseÃ§Ã£o
	return false;
}

vec3 renderPixel(in vec2 coord)
{
    // PosiÃ§Ã£o da colisÃ£o e cor da colisÃ£o
	Inter inter;
    vec3 col = vec3(0.0);

    // Coordenadas do plano
    vec2 uv = 1.0 - 2.0 * (coord.xy / iResolution.xy);
         uv.x *= iResolution.x/iResolution.y;

    vec3 rd = normalize(vec3(uv, 1.0));
    vec3 ro = vec3(0.0);

    // Se acontecer raymarching
    float realDist;

    for (int i = 0; i < 3; i++) {
        // Realiza o raymarching
        if (!raymarch(inter, ro, rd))
        	break;

        if (i == 0)
            realDist = inter.dist;

        // PosisÃ£o de colisÃ£o e normal
        vec3 pos = ro + rd * (inter.dist - EPS);
        vec3 normal = normals(pos);

        // Pinta
        if (i == 0)
            col = colorize(inter);
        else
        	col += 0.2 * colorize(inter);

        //if (inter.id == 1)
        //    break;

        // Muda o referencial para o ponto de colisao

        ro = pos;
        rd = reflect(rd, normal);
    }

    col = mix(col, vec3(1.0), realDist/FAR);
    return realDist == 0.0 ? vec3(1.0) : col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 e = vec2(0.5, 0.0);

    // 2x multisampling;
  	vec3 col = renderPixel(fragCoord + e.xx)
             + renderPixel(fragCoord + e.xy);

	fragColor = vec4(col/2.0, 1.0);
}

