const float pi = 3.14159265;
const float tau = pi * 2.0;
const float halfpi = pi * 0.5;
const float oneoverpi  = 1.0 / pi;
const float oneovertau = 1.0 / tau;

const float ZMAX = 99999.0;
const float EPSILON = 0.001;

