// code from : http://www.rorydriscoll.com/2009/01/07/better-sampling/

vec3 cosineWeightedDirection( vec2 uv, vec3 normal, float roughness ) 
{
    float u = uv.x; float v = uv.y;
    float r = sqrt(u);
    float angle = 6.283185307179586 * v;
    // compute basis from normal
    vec3 sdir, tdir;
    if (abs(normal.x)<.5) 
    {
        sdir = cross(normal, vec3(1,0,0));
    } 
    else 
    {
        sdir = cross(normal, vec3(0,1,0));
    }
    tdir = cross(normal, sdir);

    return ( r*cos(angle)*sdir + r*sin(angle)*tdir + sqrt(1.-u)*normal ) * roughness;
}

vec3 TangentToWorld( vec3 Vec, vec3 TangentZ )
{
	vec3 UpVector = abs( TangentZ.z ) < 0.999 ? vec3( 0.0, 0.0, 1.0 ) : vec3( 1.0, 0.0, 0.0 );
	vec3 TangentX = normalize( cross( UpVector, TangentZ ) );
	vec3 TangentY = cross( TangentZ, TangentX );
	return TangentX * Vec.x + TangentY * Vec.y + TangentZ * Vec.z;
}

vec3 ImportanceSampleGGX( vec2 E, float Roughness )
{
	float m = Roughness * Roughness;
	float m2 = m * m;

	float Phi = 2.0 * pi * E.x;
	float CosTheta = sqrt( (1.0 - E.y) / ( 1.0 + (m2 - 1.0) * E.y ) );
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 H;
	H.x = SinTheta * cos( Phi );
	H.y = SinTheta * sin( Phi );
	H.z = CosTheta;

	//float d = ( CosTheta * m2 - CosTheta ) * CosTheta + 1.0;
	//float D = m2 / ( pi*d*d );
	//float PDF = D * CosTheta;

	return H;
}

vec3 UniformSampleCone( vec2 E, float CosThetaMax )
{
	float Phi = 2.0 * pi * E.x;
	float CosTheta = mix( CosThetaMax, 1.0, E.y );
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 L;
	L.x = SinTheta * cos( Phi );
	L.y = SinTheta * sin( Phi );
	L.z = CosTheta;

	//float PDF = 1.0 / ( 2 * PI * (1 - CosThetaMax) );

	return L;
}

vec3 UniformSampleHemisphere( vec2 E )
{
	float Phi = 2.0 * pi * E.x;
	float CosTheta = E.y;
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 H;
	H.x = SinTheta * cos( Phi );
	H.y = SinTheta * sin( Phi );
	H.z = CosTheta;

	//float PDF = 1.0 / (2 * PI);

	return H;
}

