float Square( float s )
{
    return s*s;
}

float F_Fresnel( float SpecularColor, float VoH )
{
	float SpecularColorSqrt = sqrt( clamp( 0.0, 0.99, SpecularColor ) );
	float n = ( 1.0 + SpecularColorSqrt ) / ( 1.0 - SpecularColorSqrt );
	float g = sqrt( n*n + VoH*VoH - 1.0 );
	return 0.5 * Square( (g - VoH) / (g + VoH) ) * ( 1.0 + Square( ((g+VoH)*VoH - 1.0) / ((g-VoH)*VoH + 1.0) ) );
}

float F_Schlick( in float f90, in float u )
{
    float f = pow( 1.0 - u, 5.0 );
	return f + ( 1.0 - f ) * f90;
}

float disneyDiffuse( float NdotV, float NdotL, float LdotH, float linearRoughness )
{
	float energyBias = mix( 0.0, 0.5, linearRoughness );
   	float energyFactor = mix( 1.0, 1.0 / 1.51, linearRoughness );

	float fd90 = energyBias + 2.0 * LdotH * LdotH * linearRoughness;

	float lightScatter = F_Schlick( fd90, NdotL );
	float viewScatter  = F_Schlick( fd90, NdotV );
	return lightScatter * viewScatter * energyFactor;
}

float V_SmithGGXCorrelatedOriginal( float NdotL, float NdotV, float alphaG )
{
	// This is the optimize version
	float alphaG2 = alphaG * alphaG;
    float NdotL2 = NdotL * NdotL;
    float NdotV2 = NdotV * NdotV;

	float lambda_v = (-1.0 + sqrt( alphaG2 * ( 1.0 - NdotL2 ) / NdotL2 + 1.0 ) ) * 0.5;
	float lambda_l = (-1.0 + sqrt( alphaG2 * ( 1.0 - NdotV2 ) / NdotV2 + 1.0 ) ) * 0.5;
	float G_SmithGGXCorrelated = 1.0 / ( 1.0 + lambda_v + lambda_l );
	float V_SmithGGXCorrelated = G_SmithGGXCorrelated / ( 4.0 * NdotL * NdotV );
    return V_SmithGGXCorrelated;
}

float V_SmithGGXCorrelated( float NdotL, float NdotV, float alphaG )
{
	// This is the optimize version
	float alphaG2 = alphaG * alphaG;
	// Caution : the " NdotL *" and " NdotV *" are explicitely inversed , this is not a mistake .
	float Lambda_GGXV = NdotL * sqrt((-NdotV * alphaG2 + NdotV ) * NdotV + alphaG2 );
	float Lambda_GGXL = NdotV * sqrt((-NdotL * alphaG2 + NdotL ) * NdotL + alphaG2 );

	return 0.5 / ( Lambda_GGXV + Lambda_GGXL );
}

const float PI = 3.14159265359;
const float oneoverPI = 1.0 / PI;
float D_Beckmann( float NoH, float m )
{
	float m2 = m * m;
	float NoH2 = NoH * NoH;
	return exp( (NoH2 - 1.0) / (m2 * NoH2) ) / ( PI * m2 * NoH2 * NoH2 );
}

float D_GGX( float NdotH, float m )
{
	float m2 = m * m;
	float f = ( NdotH * m2 - NdotH ) * NdotH + 1.0;
	return m2 / (f * f);
}

float cot ( float x) { return cos (x) / sin (x); }
float acot ( float x) { return atan (1.0 / x); }

float illuminanceSphereOrDisk( float cosTheta , float sinSigmaSqr )
{
	float sinTheta = sqrt( 1.0 - cosTheta * cosTheta );

	float illuminance = 0.0;
	if ( cosTheta * cosTheta > sinSigmaSqr )
	{
		illuminance = PI * sinSigmaSqr * saturate( cosTheta );
	}
	else
	{
        float x = sqrt( 1.0 / sinSigmaSqr - 1.0 ); // For a disk this simplify to x = d / r
        float y = -x * ( cosTheta / sinTheta );
        float sinThetaSqrtY = sinTheta * sqrt( 1.0 - y * y );
        illuminance = ( cosTheta * acos( y ) - x * sinThetaSqrtY ) 
            * sinSigmaSqr + atan( sinThetaSqrtY / x );
	}
	return max( illuminance , 0.0 );
}

float getSphereIlluminance( vec3 lightDirection, vec3 worldNormal, 
                            float lightRadius, float sqrDist )
{
	/*float Beta = acos( dot( worldNormal, lightDirection ) );
	float H = sqrt( sqrDist );
	float h = H / lightRadius;
	float x = sqrt( h * h - 1.0 );
	float y = -x * ( 1.0 / tan( Beta ) );

	float illuminance = 0.0;
	if( h * cos ( Beta ) > 1.0 )
		illuminance = cos ( Beta ) / ( h * h );
	else
    {
		illuminance = ( 1.0 / ( PI * h * h ) ) *
			( cos( Beta ) * acos ( y ) - x * sin( Beta ) * sqrt( 1.0 - y * y ) ) +
			( 1.0 / PI ) * atan( sin ( Beta ) * sqrt( 1.0 - y * y ) / x );
	}

	illuminance *= PI;*/

	float cosTheta = clamp( dot( worldNormal , lightDirection ), -0.999, 0.999 );
	float sqrLightRadius = lightRadius * lightRadius;
	float sinSigmaSqr = min( sqrLightRadius / sqrDist, 0.9999 );
    float illuminance = illuminanceSphereOrDisk( cosTheta, sinSigmaSqr );
    return illuminance;
}

float smoothDistanceAtt( float squaredDistance , float invSqrAttRadius )
{
	float factor = squaredDistance * invSqrAttRadius;
	float smoothFactor = saturate( 1.0 - factor * factor );
	return smoothFactor * smoothFactor;
}

float getDistanceAtt( vec3 unormalizedLightVector, float invSqrAttRadius )
{
	float sqrDist = dot ( unormalizedLightVector, unormalizedLightVector );
	float attenuation = 1.0 / ( max( sqrDist, 0.01 * 0.01 ) );
    attenuation *= smoothDistanceAtt( sqrDist, invSqrAttRadius );
    return attenuation;
}

vec3 DoLight( vec3 origin, vec3 lightPosition, vec3 normal, vec3 viewDirection )
{
    float lightRadius = 0.125;

    vec3 lightVector = ( lightPosition - origin );

    //

    vec3 R = reflect( -viewDirection, normal );
    vec3 ClosestPointOnRay = dot( lightVector, R ) * R;
    vec3 CenterToRay = ClosestPointOnRay - lightVector;
    vec3 ClosestPointOnSphere = lightVector + CenterToRay 
       * saturate( lightRadius * inversesqrt( dot( CenterToRay, CenterToRay ) ) );
    lightVector = ClosestPointOnSphere;

    //

	vec3 light = normalize( lightVector );

    vec3 halfVector = normalize( viewDirection + light );
    float n_dot_v = abs( dot( viewDirection, normal ) + 1e-5 );
    float n_dot_l = saturate( dot( light, normal ) );
    float l_dot_h = saturate( dot( light, halfVector ) );
    float n_dot_h = saturate( dot( normal, halfVector ) );

    float roughness = 0.4;//saturate( sin( iTime ) );
    float roughnessSquared = roughness * roughness;

    float fresnel = F_Schlick( 0.04, n_dot_v );
    float visibility = V_SmithGGXCorrelated( n_dot_l, n_dot_v, roughnessSquared );
    float distribution = D_GGX( n_dot_h, roughnessSquared );
    float specular = distribution * fresnel * saturate( visibility * oneoverPI );

    float attenuation = getDistanceAtt( lightVector, 0.00007 );
    float diffuse = oneoverPI;// * disneyDiffuse( n_dot_v, n_dot_l, l_dot_h, roughness );

    float illuminance = getSphereIlluminance( light, normal, 
                                              lightRadius, dot( lightVector, lightVector ) );

    //

    Ray shadowray;
    shadowray.origin = origin;
    shadowray.direction = light;
    Intersection shadowintersect = projectRay( shadowray );
    float shadow = shadowintersect.distance < length( lightVector ) ? 0.0 : 1.0; 

    float final = illuminance * ( diffuse + specular );
    return vec3( final );
}

vec3 getBRDF( Light light, Intersection intersect, vec3 incidentReflection, vec3 viewDirection, vec3 enviroment )
{
    return DoLight( intersect.position, light.lightPosition, intersect.normal, viewDirection );
}

// filmic without sRGB conversion

// shoulder strength
const float A = 0.22;
// linear strength
const float B = 0.3;
// linear angle
const float C = 0.1;
// toe strength
const float D = 0.20;
// toe numerator
const float E = 0.01;
// toe denominator
const float F = 0.30;
// linear white point
const float W = 11.2;
float filmic_curve(float x) {
	return ((x*(A*x+C*B)+D*E)/(x*(A*x+B)+D*F))-E/F;
}
float inverse_filmic_curve(float x) {
    float q = B*(F*(C-x) - E);
    float d = A*(F*(x - 1.0) + E);
    return (q -sqrt(q*q - 4.0*D*F*F*x*d)) / (2.0*d);
}
vec3 filmic(vec3 x) {
    float w = filmic_curve(W);
    return vec3(
        filmic_curve(x.r),
        filmic_curve(x.g),
        filmic_curve(x.b)) / w;
}
vec3 inverse_filmic(vec3 x) {
    x *= filmic_curve(W);
    return vec3(
        inverse_filmic_curve(x.r),
        inverse_filmic_curve(x.g),
        inverse_filmic_curve(x.b));
}

