#define ANTI_ALIAS

#define NUM_FACES 4
#define IN_RADIUS 0.25
#define OUT_RADIUS 0.70
#define SCROLL_SPEED -0.9

#define COLOR_1 0.50, 0.90, 0.95
#define COLOR_2 0.95, 0.60, 0.10

float tau = atan(1.0) * 8.0;
float pi = atan(1.0) * 4.0;
float aaSize = 0.0;

vec4 slice(float x0, float x1, vec2 uv)
{
    float u = (uv.x - x0)/(x1 - x0);
    float w = (x1 - x0);
    vec3 col = vec3(0);

    //Gradient
    col = mix(vec3(COLOR_1), vec3(COLOR_2), u);

    //Lighting 
    col *= w / sqrt(2.0 * IN_RADIUS*IN_RADIUS * (1.0 - cos(tau / float(NUM_FACES))));

    //Edges
    col *= smoothstep(0.05, 0.10, u) * smoothstep(0.95, 0.90, u) + 0.5;

    //Checker board
    uv.y += iTime * SCROLL_SPEED; //Scrolling

    #ifdef ANTI_ALIAS
    	col *= (-1.0 + 2.0 * smoothstep(-0.03, 0.03, sin(u*pi*4.0) * cos(uv.y*16.0))) * (1.0/16.0) + 0.7;
    #else
    	col *= sign(sin(u * pi * 4.0) * cos(uv.y * 16.0)) * (1.0/16.0) + 0.7;
    #endif

    float clip = 0.0;

    #ifdef ANTI_ALIAS
    	clip = (1.0-smoothstep(0.5 - aaSize/w, 0.5 + aaSize/w, abs(u - 0.5))) * step(x0, x1);
    #else
    	clip = float((u >= 0.0 && u <= 1.0) && (x0 < x1));
    #endif

    return vec4(col, clip);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    aaSize = 2.0 / iResolution.y; //Scale anti aliasing with resolution
    vec2 res = iResolution.xy / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.y;
    uv -= res / 2.0;
    uv *= 2.0;

    //Polar coordinates
    vec2 uvr = vec2(length(uv), atan(uv.y, uv.x) + pi);
    uvr.x -= OUT_RADIUS;

    vec3 col = vec3(0.05);

    //Twisting angle
    float angle = uvr.y + 2.0*iTime + sin(uvr.y) * sin(iTime) * pi;

    for(int i = 0;i < NUM_FACES;i++)
    {
        float x0 = IN_RADIUS * sin(angle + tau * (float(i) / float(NUM_FACES)));
        float x1 = IN_RADIUS * sin(angle + tau * (float(i + 1) / float(NUM_FACES)));

        vec4 face = slice(x0, x1, uvr);

        col = mix(col, face.rgb, face.a); 
    }

    //col = (abs(uv.x) > 1.0) ? vec3(1) : col;

	fragColor = vec4(col, 1.0);
}

vec2 dSphere(vec3 p){
  	float d = length(p)-4.2;
  	return vec2(d,1);
}

vec2 dFloor(vec3 p){
  return vec2(p.y+0.2,0);
}

vec2 oSub(vec2 a, vec2 b){
  	float d = max(a.x, -b.x);
  	return vec2(d,1);
}

vec2 oAdd(vec2 a, vec2 b){
  	float d = min(a.x, b.x);
    float c;
    if(d==a.x)
        c = a.y;
    else
        c = b.y;
  	return vec2(d,c);
}

float udRoundBox(vec3 p, vec3 b, float r){
  return length(max(abs(p)-b,0.0))-r;
}

vec2 infiniteIs(vec3 p, vec3 c){
    vec3 q;
	q.x = mod(p.x,c.x)-0.5*c.x;
    q.y = p.y;
    //q.y = mod(p.y,c.y)-0.5*c.y;

    q.z = mod(p.z,c.z)-0.5*c.z;
	return oAdd(oAdd(oAdd(vec2(udRoundBox(q,vec3(0.2,0.1,0.5),0.1),1.0),
                vec2(udRoundBox(q+vec3(0.0,-1.4+0.18*cos(iTime*6.0)*(1.0-0.1*sin(p.z*5.5)),0.0),vec3(0.2,0.1,0.5),0.1),1.0)),
                vec2(udRoundBox(q+vec3(0.0,-0.7,0.0),vec3(0.2,0.69+-0.09*cos(iTime*6.0),0.1),0.1),1.0)),
                dFloor(p));

}

vec2 dist(vec3 p){
  	return infiniteIs(p,vec3(4.0,4.0,4.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 q = fragCoord.xy/iResolution.xy;
    vec2 vPos = -1.0+2.0*q;



    vec3 camPos = vec3(1.0*mod(iTime*10.0,600.0),
                       3.7+3.0*sin(iTime*0.8),
                       0.0);

  	vec3 camLook = vec3(camPos.x+30.0,
						1.7,
                        camPos.z+0.0);

    vec3 camUp = vec3(0.0,
                      1.0+0.0*cos(iTime),
                      0.0*sin(iTime)); 

    vec3 vpn = normalize(camLook-camPos);
  	vec3 u = normalize(cross(camUp,vpn));
  	vec3 v = cross(vpn,u);
	vec3 vcv = camPos+vpn;
  	vec3 scrCoord = vcv+vPos.x*u*iResolution.x/iResolution.y+vPos.y*v;
  	vec3 scp = normalize(scrCoord-camPos);

  	const vec3 e = vec3(0.01,0,0);
  	const float maxDepth = 256.0;
  	vec2 d = vec2(0.002,0.0);
  	vec3 p;

  	float f = 1.0;
  	for(int i=0; i<64; i++){
    	if ((abs(d.x) < .001) || (f > maxDepth)) 
      		break;

    	f += d.x;
    	p = camPos+scp*f;
    	d = dist(p);
  	}

  	if (f < maxDepth){
        vec3 color;
        if(d.y==1.0){
    		color = vec3(0.863, //Orange
                         0.447,
                         0.118);
        }else{
            color = vec3(0.454,	//Blue
                         0.522,
                         0.745);
        }

  		vec3 n = vec3(d.x-dist(p-e.xyy).x,
					  d.x-dist(p-e.yxy).x,
                  	  d.x-dist(p-e.yyx).x);

    	vec3 N = normalize(n);
    	fragColor=vec4(color*dot(N,normalize(camPos-p))-f/128.0,1.0);
    }else
    	fragColor=vec4(0.0,0.0,0.0,1.0);

}

